            .state-circle:hover { filter: drop-shadow(0 2px 8px rgba(255,255,255,0.8)); stroke-width: 3; }
            .state-label { font-size: 11px; font-weight: 700; text-anchor: middle; pointer-events: none; fill: white; text-shadow: 0 1px 2px rgba(0,0,0,0.3); }
          `}</style>
        </defs>

        <rect width="900" height="750" fill="#0284c7" />

        {brazilStates.map(state => {
          const orders = stateData[state.code] || 0;
          const color = getColorForValue(orders, maxOrders);
          const radius = Math.max(12, Math.min(36, 12 + (orders / Math.max(maxOrders, 1)) * 24));

          return (
            <g key={state.code} onMouseEnter={() => setHoveredState(state.code)} onMouseLeave={() => setHoveredState(null)}>
              <circle cx={state.x} cy={state.y} r={radius} fill={color} className="state-circle" stroke="white" strokeWidth="2" />
              <text x={state.x} y={state.y + 5} className="state-label" style={{ fontSize: radius > 20 ? '12px' : '10px', fontWeight: 'bold' }}>
                {state.code}
              </text>
              {radius > 18 && (
                <text x={state.x} y={state.y + 18} className="state-label" style={{ fontSize: '9px', opacity: 0.8 }}>
                  {orders}
                </text>
              )}
              {hoveredState === state.code && (
                <g>
                  <rect x={state.x - 55} y={state.y - 70} width="110" height="55" fill="#0f172a" rx="6" stroke="#cbd5e1" strokeWidth="2" />
                  <text x={state.x} y={state.y - 48} style={{ fontSize: '14px', fontWeight: 'bold', textAnchor: 'middle', fill: '#22d3ee' }}>
                    {state.code}
                  </text>
                  <text x={state.x} y={state.y - 32} style={{ fontSize: '12px', textAnchor: 'middle', fill: '#10b981', fontWeight: 'bold' }}>
                    {orders} orders
                  </text>
                </g>
              )}
            </g>
          );
        })}
      </svg>
    </div>
  );
};

