const formatCurrency = (value) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  if (num >= 1000000) return `$${(num / 1000000).toFixed(1)}M`;
  if (num >= 1000) return `$${(num / 1000).toFixed(1)}K`;
  return `$${num.toFixed(2)}`;
};

const formatCurrencyWithCommas = (value) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  return `$${num.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
};

const CustomTooltip = ({ active, payload, isCurrency = false }) => {
  if (active && payload && payload.length) {
    const value = payload[0].value;
    const formattedValue = isCurrency ? formatCurrencyWithCommas(value) : value.toFixed(2);
    return (
      <div style={styles.tooltipStyle}>
        <p style={{ margin: 0 }}>{`${payload[0].name}: ${formattedValue}`}</p>
      </div>
    );
  }
  return null;
};

