                  <input type="range" min="1" max="5" step="0.5" value={graph2ReviewRange[0]} onChange={(e) => setGraph2ReviewRange([parseFloat(e.target.value), graph2ReviewRange[1]])} style={{width: '100%', marginBottom: '4px', cursor: 'pointer'}} />
                  <input type="range" min="1" max="5" step="0.5" value={graph2ReviewRange[1]} onChange={(e) => setGraph2ReviewRange([graph2ReviewRange[0], parseFloat(e.target.value)])} style={{width: '100%', cursor: 'pointer'}} />
