                <select multiple style={{width: '100%', padding: '8px 12px', border: '1px solid #a855f7', borderRadius: '6px', fontSize: '12px', backgroundColor: '#1e293b', color: '#e2e8f0', cursor: 'pointer', height: '80px'}} value={graph4Categories} onChange={(e) => setGraph4Categories(Array.from(e.target.selectedOptions, opt => opt.value))}>
