            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', gridColumn: 'span 3' }}>
              <div style={styles.graphCard}>
                <div style={styles.graphCardHeader}>
                  <h3 style={styles.graphCardTitle}>â­ Review Distribution</h3>
                  <button style={{...styles.filterButton}} onClick={() => setShowGraph3Filters(!showGraph3Filters)}>âš™ Filter</button>
                </div>
                <div style={{display: showGraph3Filters ? 'block' : 'none', backgroundColor: '#0f172a', border: '1px solid #f97316', borderRadius: '8px', padding: '16px', marginBottom: '12px', boxShadow: '0 0 20px rgba(249, 115, 22, 0.1)'}}>
                  <div style={{marginBottom: '12px'}}>
