                    <input type="range" min="0" max={aggregatedData.maxSpend || 10000} step="100" value={graph3SpendMax} onChange={(e) => setGraph3SpendMax(parseFloat(e.target.value))} style={{width: '100%', cursor: 'pointer'}} />
                  </div>
                </div>
                <ResponsiveContainer width="100%" height={300}>
