import React, { useState, useRef, useEffect } from 'react';
import { TrendingUp, Users, DollarSign, Activity, ChevronRight, Send, Loader, BarChart3, PieChart as PieChartIcon, LineChart as LineChartIcon, MessageSquare, Home as HomeIcon, Filter, Menu, X } from 'lucide-react';
import APIClient from './api';

export default function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [menuOpen, setMenuOpen] = useState(false);

  // Analytics page state
  const [selectedMetric, setSelectedMetric] = useState('revenue');
  const [filterMonth, setFilterMonth] = useState('all');

  // Text analysis state
  const [chatHistory, setChatHistory] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [isTextLoading, setIsTextLoading] = useState(false);
  const textChatEndRef = useRef(null);

  // Infographics state
  const [selectedChart, setSelectedChart] = useState('revenue');
  const [graphInput, setGraphInput] = useState('');
  const [isGraphLoading, setIsGraphLoading] = useState(false);
  const [generatedCharts, setGeneratedCharts] = useState([]);

  // Scroll to bottom for chats
  useEffect(() => {
    textChatEndRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatHistory]);

  // Metrics data
  const metricsData = [
    {
      id: 'revenue',
      title: 'Total Revenue',
      value: '$124,589',
      change: '+12.5%',
      icon: DollarSign,
      color: 'bg-blue-100',
      accent: 'text-blue-600',
      bgGradient: 'from-blue-600 to-blue-700',
    },
    {
      id: 'users',
      title: 'Active Users',
      value: '8,234',
      change: '+5.2%',
      icon: Users,
      color: 'bg-indigo-100',
      accent: 'text-indigo-600',
      bgGradient: 'from-indigo-600 to-indigo-700',
    },
    {
      id: 'engagement',
      title: 'Engagement Rate',
      value: '64.8%',
      change: '+2.1%',
      icon: Activity,
      color: 'bg-cyan-100',
      accent: 'text-cyan-600',
      bgGradient: 'from-cyan-600 to-cyan-700',
    },
    {
      id: 'growth',
      title: 'Month-over-Month',
      value: '23.4%',
      change: '+8.3%',
      icon: TrendingUp,
      color: 'bg-blue-100',
      accent: 'text-blue-600',
      bgGradient: 'from-blue-600 to-blue-700',
    },
  ];

  const chartData = [
    { month: 'Jan', value: 4000 },
    { month: 'Feb', value: 3000 },
    { month: 'Mar', value: 2000 },
    { month: 'Apr', value: 2780 },
    { month: 'May', value: 1890 },
    { month: 'Jun', value: 2390 },
  ];

  const recentActivity = [
    { id: 1, user: 'Sarah Anderson', action: 'Created new project', time: '2 hours ago' },
    { id: 2, user: 'Mike Chen', action: 'Updated dashboard', time: '5 hours ago' },
    { id: 3, user: 'Emma Wilson', action: 'Shared report', time: '1 day ago' },
    { id: 4, user: 'James Rodriguez', action: 'Completed task', time: '2 days ago' },
  ];

  const chartTypes = [
    { id: 'revenue', title: 'Revenue Trend', icon: LineChartIcon },
    { id: 'users', title: 'User Growth', icon: BarChart3 },
    { id: 'distribution', title: 'Distribution', icon: PieChartIcon },
  ];

  // Text Analysis Handler
  const handleTextAnalysis = async () => {
    if (!textInput.trim()) return;

    const userMessage = textInput;
    setChatHistory(prev => [...prev, { sender: 'user', text: userMessage }]);
    setTextInput('');
    setIsTextLoading(true);

    try {
      const result = await APIClient.analyzeText(userMessage);
      if (result.success) {
        setChatHistory(prev => [...prev, { 
          sender: 'bot', 
          text: result.summary,
          questions: result.questions 
        }]);
      } else {
        setChatHistory(prev => [...prev, { 
          sender: 'bot', 
          text: `Error: ${result.error}`,
          error: true 
        }]);
      }
    } catch (error) {
      setChatHistory(prev => [...prev, { 
        sender: 'bot', 
        text: `Connection error: ${error.message}`,
        error: true 
      }]);
    }

    setIsTextLoading(false);
  };

  // Graph Generation Handler
  const handleGraphGeneration = async () => {
    if (!graphInput.trim()) return;

    const userMessage = graphInput;
    setGraphInput('');
    setIsGraphLoading(true);

    try {
      const result = await APIClient.generateGraphs(userMessage);
      if (result.success) {
        setGeneratedCharts(result.charts || []);
      } else {
        alert(`Error: ${result.error}`);
      }
    } catch (error) {
      alert(`Connection error: ${error.message}`);
    }

    setIsGraphLoading(false);
  };

  // ============================================================================
  // NAVIGATION HEADER
  // ============================================================================
  const NavigationHeader = () => (
    <div className="bg-gradient-to-r from-blue-900 to-blue-800 border-b border-blue-700 sticky top-0 z-50 shadow-lg">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-gradient-to-br from-cyan-400 to-blue-500 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-lg">E</span>
          </div>
          <h2 className="text-2xl font-bold text-white">ExorVia</h2>
        </div>

        {/* Desktop Navigation */}
        <div className="hidden md:flex gap-2">
          {[
            { page: 'home', label: 'Home', icon: HomeIcon },
            { page: 'analytics', label: 'Analytics', icon: BarChart3 },
            { page: 'text', label: 'Text Analysis', icon: MessageSquare },
            { page: 'graphs', label: 'Infographics', icon: LineChartIcon },
          ].map((nav) => {
            const Icon = nav.icon;
            return (
              <button
                key={nav.page}
                onClick={() => { setCurrentPage(nav.page); setMenuOpen(false); }}
                className={`flex gap-2 items-center px-4 py-2 rounded-lg transition-colors ${
                  currentPage === nav.page
                    ? 'bg-cyan-500 text-white shadow-lg'
                    : 'text-blue-100 hover:bg-blue-700'
                }`}
              >
                <Icon size={18} />
                <span>{nav.label}</span>
              </button>
            );
          })}
        </div>

        {/* Mobile Menu Button */}
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          className="md:hidden text-blue-100 hover:text-white"
        >
          {menuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Navigation */}
      {menuOpen && (
        <div className="md:hidden bg-blue-800 border-t border-blue-700 px-6 py-4 space-y-2">
          {[
            { page: 'home', label: 'Home', icon: HomeIcon },
            { page: 'analytics', label: 'Analytics', icon: BarChart3 },
            { page: 'text', label: 'Text Analysis', icon: MessageSquare },
            { page: 'graphs', label: 'Infographics', icon: LineChartIcon },
          ].map((nav) => {
            const Icon = nav.icon;
            return (
              <button
                key={nav.page}
                onClick={() => { setCurrentPage(nav.page); setMenuOpen(false); }}
                className={`w-full flex gap-2 items-center px-4 py-2 rounded-lg transition-colors ${
                  currentPage === nav.page
                    ? 'bg-cyan-500 text-white'
                    : 'text-blue-100 hover:bg-blue-700'
                }`}
              >
                <Icon size={18} />
                <span>{nav.label}</span>
              </button>
            );
          })}
        </div>
      )}
    </div>
  );

  // ============================================================================
  // HOME PAGE
  // ============================================================================
  const HomePage = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-950 via-blue-900 to-blue-800">
      <div className="p-6 md:p-8 max-w-7xl mx-auto">
        <div className="mb-12">
          <h1 className="text-5xl font-bold text-white mb-2">ExorVia Dashboard</h1>
          <p className="text-blue-200 text-lg">Welcome to your comprehensive data analysis platform</p>
        </div>

        {/* Feature Cards */}
        <div className="grid md:grid-cols-3 gap-8 mb-12">
          {[
            { 
              icon: 'ðŸ“Š', 
              title: 'Analytics Dashboard', 
              desc: 'View comprehensive metrics and KPIs',
              page: 'analytics'
            },
            { 
              icon: 'âœï¸', 
              title: 'Text Analysis', 
              desc: 'Ask AI questions about your data',
              page: 'text'
            },
            { 
              icon: 'ðŸ“ˆ', 
              title: 'Infographics', 
              desc: 'Generate custom visualizations',
              page: 'graphs'
            },
          ].map((card, idx) => (
            <div
              key={idx}
              onClick={() => setCurrentPage(card.page)}
              className="group bg-blue-800 hover:bg-blue-700 rounded-xl p-8 shadow-lg transition-all duration-300 cursor-pointer hover:shadow-2xl hover:scale-105 border border-blue-700 hover:border-cyan-500"
            >
              <div className="text-5xl mb-4 group-hover:scale-110 transition-transform">{card.icon}</div>
              <h3 className="text-xl font-bold text-white mb-2">{card.title}</h3>
              <p className="text-blue-200">{card.desc}</p>
            </div>
          ))}
        </div>

        {/* Quick Stats */}
        <div className="grid md:grid-cols-4 gap-6 mb-8">
          {metricsData.map((metric) => {
            const IconComponent = metric.icon;
            return (
              <div
                key={metric.id}
                onClick={() => setSelectedMetric(metric.id)}
                className={`p-6 rounded-xl cursor-pointer transition-all duration-300 ${
                  selectedMetric === metric.id
                    ? 'bg-white shadow-xl scale-105'
                    : 'bg-blue-800 hover:bg-blue-700 shadow-lg border border-blue-700'
                }`}
              >
                <div className="flex items-center justify-between mb-4">
                  <div className={`p-3 rounded-lg ${metric.color} ${selectedMetric === metric.id ? metric.accent : 'text-blue-400'}`}>
                    <IconComponent size={24} />
                  </div>
                  <span className="text-cyan-400 text-sm font-semibold">{metric.change}</span>
                </div>
                <h3 className={`text-sm font-medium mb-2 ${selectedMetric === metric.id ? 'text-blue-900' : 'text-blue-300'}`}>
                  {metric.title}
                </h3>
                <p className={`text-2xl font-bold ${selectedMetric === metric.id ? 'text-blue-900' : 'text-white'}`}>
                  {metric.value}
                </p>
              </div>
            );
          })}
        </div>

        {/* Chart Preview */}
        <div className="bg-blue-800 rounded-xl p-6 shadow-lg border border-blue-700">
          <h2 className="text-xl font-bold text-white mb-4">Revenue Trend</h2>
          <div className="flex items-end gap-3 h-48">
            {chartData.map((item, index) => {
              const maxValue = Math.max(...chartData.map((d) => d.value));
              const percentage = (item.value / maxValue) * 100;
              return (
                <div key={index} className="flex-1 flex flex-col items-center gap-2">
                  <div
                    className="w-full bg-gradient-to-t from-cyan-500 to-cyan-400 rounded-t transition-all hover:opacity-80"
                    style={{ height: `${percentage}%` }}
                  />
                  <span className="text-xs text-blue-300">{item.month}</span>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );

  // ============================================================================
  // ANALYTICS PAGE
  // ============================================================================
  const AnalyticsPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-950 via-blue-900 to-blue-800 p-6 md:p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">Analytics Dashboard</h1>
          <p className="text-blue-200">Real-time performance metrics and insights</p>
        </div>

        {/* KPI Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {metricsData.map((metric) => {
            const IconComponent = metric.icon;
            const isSelected = selectedMetric === metric.id;
            return (
              <div
                key={metric.id}
                onClick={() => setSelectedMetric(metric.id)}
                className={`p-6 rounded-xl cursor-pointer transition-all duration-300 ${
                  isSelected
                    ? 'bg-white shadow-xl scale-105'
                    : 'bg-blue-800 hover:bg-blue-700 shadow-lg border border-blue-700'
                }`}
              >
                <div className="flex items-center justify-between mb-4">
                  <div
                    className={`p-3 rounded-lg ${metric.color} ${isSelected ? metric.accent : 'text-blue-400'}`}
                  >
                    <IconComponent size={24} />
                  </div>
                  <span className="text-cyan-400 text-sm font-semibold">{metric.change}</span>
                </div>
                <h3 className={`text-sm font-medium mb-2 ${isSelected ? 'text-blue-900' : 'text-blue-300'}`}>
                  {metric.title}
                </h3>
                <p className={`text-2xl font-bold ${isSelected ? 'text-blue-900' : 'text-white'}`}>
                  {metric.value}
                </p>
              </div>
            );
          })}
        </div>

        {/* Main Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Chart */}
          <div className="lg:col-span-2 bg-blue-800 rounded-xl p-6 shadow-lg border border-blue-700">
            <div className="mb-6">
              <h2 className="text-xl font-bold text-white mb-2">Revenue Trend</h2>
              <p className="text-blue-300 text-sm">Last 6 months</p>
            </div>
            <div className="flex items-end gap-3 h-48">
              {chartData.map((item, index) => {
                const maxValue = Math.max(...chartData.map((d) => d.value));
                const percentage = (item.value / maxValue) * 100;
                return (
                  <div key={index} className="flex-1 flex flex-col items-center gap-2">
                    <div
                      className="w-full bg-gradient-to-t from-cyan-500 to-cyan-400 rounded-t transition-all hover:opacity-80"
                      style={{ height: `${percentage}%` }}
                    />
                    <span className="text-xs text-blue-300">{item.month}</span>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Recent Activity */}
          <div className="bg-blue-800 rounded-xl p-6 shadow-lg border border-blue-700">
            <h2 className="text-xl font-bold text-white mb-4">Recent Activity</h2>
            <div className="space-y-4">
              {recentActivity.map((activity) => (
                <div
                  key={activity.id}
                  className="flex items-start gap-3 p-3 rounded-lg hover:bg-blue-700 transition-colors cursor-pointer group"
                >
                  <div className="w-10 h-10 rounded-full bg-blue-700 flex items-center justify-center flex-shrink-0">
                    <span className="text-white font-semibold text-sm">{activity.user.charAt(0)}</span>
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-white text-sm font-medium truncate">{activity.user}</p>
                    <p className="text-blue-300 text-xs truncate">{activity.action}</p>
                    <p className="text-blue-400 text-xs mt-1">{activity.time}</p>
                  </div>
                  <ChevronRight size={16} className="text-blue-500 group-hover:text-cyan-400 flex-shrink-0 mt-1" />
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Filter Section */}
        <div className="mt-8 bg-blue-800 rounded-xl p-6 shadow-lg border border-blue-700">
          <h2 className="text-xl font-bold text-white mb-4">Filter Data</h2>
          <div className="flex gap-2 flex-wrap">
            {['all', 'january', 'february', 'march'].map((month) => (
              <button
                key={month}
                onClick={() => setFilterMonth(month)}
                className={`px-4 py-2 rounded-lg font-medium transition-all ${
                  filterMonth === month
                    ? 'bg-cyan-500 text-white shadow-lg'
                    : 'bg-blue-700 text-blue-100 hover:bg-blue-600'
                }`}
              >
                {month.charAt(0).toUpperCase() + month.slice(1)}
              </button>
            ))}
          </div>
          <p className="text-blue-300 text-sm mt-4">
            Currently filtering by: <span className="text-white font-semibold">{filterMonth}</span>
          </p>
        </div>
      </div>
    </div>
  );

  // ============================================================================
  // TEXT ANALYSIS PAGE
  // ============================================================================
  const TextAnalysisPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-950 via-blue-900 to-blue-800 p-6 md:p-8">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">Text Analysis</h1>
          <p className="text-blue-200">Ask questions about your data and get AI-powered insights</p>
        </div>

        {/* Chat Container */}
        <div className="bg-blue-800 rounded-xl shadow-lg overflow-hidden flex flex-col h-[600px] border border-blue-700">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-6 space-y-4">
            {chatHistory.length === 0 ? (
              <div className="flex items-center justify-center h-full">
                <div className="text-center">
                  <MessageSquare size={48} className="text-blue-600 mx-auto mb-4" />
                  <p className="text-blue-300 text-lg">Start the conversation...</p>
                  <p className="text-blue-400 text-sm mt-2">Ask about your data and get AI insights</p>
                </div>
              </div>
            ) : (
              <>
                {chatHistory.map((msg, idx) => (
                  <div key={idx} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                    <div
                      className={`max-w-xs px-4 py-3 rounded-lg ${
                        msg.sender === 'user'
                          ? 'bg-gradient-to-r from-cyan-500 to-cyan-600 text-white rounded-br-none shadow-lg'
                          : msg.error
                          ? 'bg-red-900 text-red-100 rounded-bl-none border border-red-700'
                          : 'bg-blue-700 text-blue-50 rounded-bl-none border border-blue-600'
                      }`}
                    >
                      <p className="text-sm leading-relaxed">{msg.text}</p>
                      {msg.questions && msg.questions.length > 0 && (
                        <div className="mt-3 pt-3 border-t border-blue-600">
                          <p className="text-xs font-semibold mb-2 opacity-90">Generated Questions:</p>
                          <ul className="text-xs space-y-1 opacity-90">
                            {msg.questions.slice(0, 3).map((q, i) => (
                              <li key={i}>â€¢ {q.substring(0, 50)}...</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
                {isTextLoading && (
                  <div className="flex justify-start">
                    <div className="bg-blue-700 px-4 py-3 rounded-lg rounded-bl-none flex gap-2 items-center border border-blue-600">
                      <Loader size={18} className="animate-spin text-cyan-400" />
                      <p className="text-sm text-blue-50">Analyzing your data...</p>
                    </div>
                  </div>
                )}
                <div ref={textChatEndRef} />
              </>
            )}
          </div>

          {/* Input */}
          <div className="border-t border-blue-700 p-4 bg-blue-900">
            <div className="flex gap-3">
              <input
                type="text"
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleTextAnalysis()}
                placeholder="Ask a question about your data..."
                className="flex-1 px-4 py-3 bg-blue-700 text-white placeholder-blue-400 rounded-lg focus:outline-none focus:ring-2 focus:ring-cyan-500 text-sm"
                disabled={isTextLoading}
              />
              <button
                onClick={handleTextAnalysis}
                disabled={isTextLoading || !textInput.trim()}
                className="px-6 py-3 bg-gradient-to-r from-cyan-500 to-cyan-600 hover:from-cyan-600 hover:to-cyan-700 disabled:from-blue-700 disabled:to-blue-600 text-white rounded-lg font-medium transition-all flex gap-2 items-center shadow-lg disabled:shadow-none"
              >
                <Send size={18} />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // ============================================================================
  // INFOGRAPHICS PAGE
  // ============================================================================
  const InfographicsPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-blue-950 via-blue-900 to-blue-800 p-6 md:p-8">
      <div className="max-w-6xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">Infographic Generator</h1>
          <p className="text-blue-200">Create custom visualizations from your data</p>
        </div>

        {/* Input Section */}
        <div className="bg-blue-800 rounded-xl p-6 shadow-lg border border-blue-700 mb-8">
          <div className="flex gap-3">
            <input
              type="text"
              value={graphInput}
              onChange={(e) => setGraphInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleGraphGeneration()}
              placeholder="Describe the visualization you want to create..."
              className="flex-1 px-4 py-3 bg-blue-700 text-white placeholder-blue-400 rounded-lg focus:outline-none focus:ring-2 focus:ring-cyan-500 text-sm"
              disabled={isGraphLoading}
            />
            <button
              onClick={handleGraphGeneration}
              disabled={isGraphLoading || !graphInput.trim()}
              className="px-6 py-3 bg-gradient-to-r from-cyan-500 to-cyan-600 hover:from-cyan-600 hover:to-cyan-700 disabled:from-blue-700 disabled:to-blue-600 text-white rounded-lg font-medium transition-all flex gap-2 items-center shadow-lg disabled:shadow-none"
            >
              <Send size={18} />
            </button>
          </div>
        </div>

        {/* Loading State */}
        {isGraphLoading && (
          <div className="flex items-center justify-center py-12">
            <div className="bg-blue-800 px-6 py-4 rounded-lg border border-blue-700 flex gap-3 items-center">
              <Loader size={24} className="animate-spin text-cyan-400" />
              <p className="text-white text-lg font-medium">Generating visualizations...</p>
            </div>
          </div>
        )}

        {/* Charts Grid */}
        {generatedCharts.length > 0 && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {generatedCharts.map((chart, idx) => (
              <div key={idx} className="bg-blue-800 rounded-xl p-6 shadow-lg border border-blue-700">
                <h3 className="text-lg font-bold text-white mb-4">{chart.title}</h3>

                {/* Simple Chart Visualization */}
                {chart.chart_type === 'line' || chart.chart_type === 'bar' ? (
                  <div className="flex items-end gap-3 h-64">
                    {chart.data && chart.data.slice(0, 6).map((item, i) => {
                      const values = chart.data.map(d => d[chart.yKey] || d.value || 0);
                      const maxValue = Math.max(...values);
                      const percentage = (values[i] / maxValue) * 100;
                      return (
                        <div key={i} className="flex-1 flex flex-col items-center gap-2">
                          <div
                            className="w-full bg-gradient-to-t from-cyan-500 to-cyan-400 rounded-t hover:opacity-80 transition-opacity"
                            style={{ height: `${percentage}%` }}
                          />
                          <span className="text-xs text-blue-300">{item[chart.xKey] || 'N/A'}</span>
                        </div>
                      );
                    })}
                  </div>
                ) : chart.chart_type === 'pie' ? (
                  <div className="flex items-center justify-around h-64 flex-wrap">
                    {chart.data && chart.data.map((item, i) => (
                      <div key={i} className="text-center">
                        <div
                          className="w-24 h-24 rounded-full flex items-center justify-center text-white font-bold mb-3 shadow-lg"
                          style={{
                            background: `linear-gradient(135deg, hsl(${190 + i * 30}, 70%, 50%) 0%, hsl(${190 + i * 30}, 70%, 40%) 100%)`
                          }}
                        >
                          {Math.round((item[chart.yKey] || item.value || 0))}%
                        </div>
                        <p className="text-sm text-blue-200">{item[chart.xKey] || 'N/A'}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <p className="text-blue-300">Chart type: {chart.chart_type}</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {/* Empty State */}
        {!isGraphLoading && generatedCharts.length === 0 && (
          <div className="text-center py-12">
            <BarChart3 size={48} className="text-blue-600 mx-auto mb-4" />
            <p className="text-blue-300 text-lg">No visualizations yet</p>
            <p className="text-blue-400 text-sm mt-2">Describe what you want to visualize and click Send</p>
          </div>
        )}
      </div>
    </div>
  );

  // ============================================================================
  // MAIN RENDER
  // ============================================================================
  return (
    <div className="min-h-screen bg-blue-950">
      <NavigationHeader />
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'analytics' && <AnalyticsPage />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <InfographicsPage />}
    </div>
  );
}
