  const [reviewRange, setReviewRange] = useState([1, 5]);

  // Local graph filters
  const [graph1Filters, setGraph1Filters] = useState({ deliveryStatus: 'all', deliveryDays: [0, 30] });
  const [graph2Filters, setGraph2Filters] = useState({ states: [] });
