  // Filter data
  const filteredData = useMemo(() => {
    let filtered = [...mockData];

    if (selectedStates.length > 0) filtered = filtered.filter(d => selectedStates.includes(d.state));
    if (selectedCategories.length > 0) filtered = filtered.filter(d => selectedCategories.includes(d.category));
    if (selectedPaymentTypes.length > 0) filtered = filtered.filter(d => selectedPaymentTypes.includes(d.paymentType));
    if (selectedStatuses.length > 0) filtered = filtered.filter(d => selectedStatuses.includes(d.status));
    if (deliveryOnTime !== 'all') filtered = filtered.filter(d => d.onTime === deliveryOnTime);
    filtered = filtered.filter(d => d.reviewScore >= reviewRange[0] && d.reviewScore <= reviewRange[1]);

    return filtered;
  }, [selectedStates, selectedCategories, selectedPaymentTypes, selectedStatuses, deliveryOnTime, reviewRange]);

  // KPI Calculations
  const kpis = useMemo(() => {
    const totalOrders = filteredData.length || 1;
    const avgReview = (filteredData.reduce((sum, d) => sum + d.reviewScore, 0) / totalOrders).toFixed(2);
    const onTimeRate = ((filteredData.filter(d => d.onTime === 'Yes').length / totalOrders) * 100).toFixed(1);
    const totalRevenue = filteredData.reduce((sum, d) => sum + d.revenue, 0);
    const avgDeliveryDays = (filteredData.reduce((sum, d) => sum + d.deliveryDays, 0) / totalOrders).toFixed(1);
    const avgOrderValue = (totalRevenue / totalOrders).toFixed(2);

    return { totalOrders, avgReview, onTimeRate, totalRevenue, avgDeliveryDays, avgOrderValue };
  }, [filteredData]);

  const formatCurrency = (value) => {
    const num = typeof value === 'number' ? value : parseFloat(value) || 0;
    if (num >= 1000000) return `$${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `$${(num / 1000).toFixed(1)}K`;
    return `$${num.toFixed(2)}`;
