                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie data={paymentData} cx="50%" cy="50%" dataKey="value">
                      {paymentData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                    </Pie>
                    <Tooltip contentStyle={tooltipStyle} />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // TEXT ANALYSIS PAGE
  const TextAnalysisPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '56rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={styles.searchSection}>
          <div style={styles.searchContainer}>
            <input type="text" placeholder="Ask about your e-commerce data..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} style={styles.searchInput} />
            <button style={styles.searchButton}>Analyze</button>
          </div>
        </div>
        <div style={styles.resultsContainer}>
          {[
            { id: 1, question: 'What products generate the most revenue?', answer: 'Electronics and Fashion are top drivers, accounting for 62% of sales.' },
            { id: 2, question: 'How is delivery performance trending?', answer: 'On-time delivery improved 18% last quarter. S칚o Paulo and Rio lead at 92%.' },
            { id: 3, question: 'Which payment method is most popular?', answer: 'Credit Card dominates at 58%, followed by Boleto (22%) and Debit Card (15%).' }
          ].map(result => (
            <div key={result.id} style={styles.resultCard}>
              <h3 style={styles.resultQuestion}>{result.question}</h3>
              <p style={styles.resultAnswer}>{result.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // INFOGRAPHICS PAGE
  const InfographicsPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={styles.chartSelectionGrid}>
          {[
            { id: 'revenue', title: 'Revenue Trend', icon: '游늳' },
            { id: 'delivery', title: 'Delivery Performance', icon: '游뚴' },
            { id: 'categories', title: 'Category Sales', icon: '游늵' },
            { id: 'payment', title: 'Payment Methods', icon: '游눱' }
          ].map(chart => (
            <div key={chart.id} style={styles.chartOption(selectedMetric === chart.id)} onClick={() => setSelectedMetric(chart.id)}>
              <span style={styles.chartOptionIcon}>{chart.icon}</span>
              <h3 style={styles.chartOptionTitle}>{chart.title}</h3>
            </div>
          ))}
        </div>

        <div style={styles.vizContainer}>
          {selectedMetric === 'revenue' && (
            <ResponsiveContainer width="100%" height={400}>
              <AreaChart data={monthlyData}>
                <defs><linearGradient id="colorRev" x1="0" y1="0" x2="0" y2="1"><stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8}/><stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/></linearGradient></defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="month" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Area type="monotone" dataKey="revenue" stroke="#3b82f6" fillOpacity={1} fill="url(#colorRev)" />
              </AreaChart>
            </ResponsiveContainer>
          )}

          {selectedMetric === 'delivery' && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie data={onTimeData} cx="50%" cy="50%" innerRadius={80} outerRadius={140} dataKey="value">
                  {onTimeData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                </Pie>
                <Tooltip contentStyle={tooltipStyle} />
              </PieChart>
            </ResponsiveContainer>
          )}

          {selectedMetric === 'categories' && (
            <ResponsiveContainer width="100%" height={400}>
              <BarChart data={categoryData} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis type="number" stroke="#94a3b8" />
                <YAxis dataKey="name" type="category" stroke="#94a3b8" width={100} />
                <Tooltip contentStyle={tooltipStyle} />
                <Bar dataKey="value" fill="#3b82f6" />
              </BarChart>
            </ResponsiveContainer>
          )}

          {selectedMetric === 'payment' && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie data={paymentData} cx="50%" cy="50%" dataKey="value">
                  {paymentData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                </Pie>
                <Tooltip contentStyle={tooltipStyle} />
              </PieChart>
            </ResponsiveContainer>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <>
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'dashboard' && <DashboardPage />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <InfographicsPage />}
    </>
  );
}
