                <div style={{...styles.graphCard, backgroundColor: '#164e63', marginBottom: '1.5rem'}}>
                  <h3 style={{color: '#22d3ee', fontSize: '16px', fontWeight: '600'}}>ðŸ“Š Summary</h3>
                  <p style={{color: '#e0f2fe', lineHeight: '1.6'}}>{analysisResults.summary}</p>
                </div>
                {(analysisResults.results || []).map((result, idx) => (
                  <div key={idx} style={{...styles.graphCard, marginBottom: '1.5rem', opacity: result.status === 'error' ? 0.7 : 1}}>
                    <h3 style={{fontSize: '16px', fontWeight: '600', color: result.status === 'success' ? '#10b981' : '#ef4444', margin: '0 0 1rem 0'}}>
                      {result.status === 'success' ? 'âœ“' : 'âœ—'} {result.question}
                    </h3>
                    <pre style={{whiteSpace: 'pre-wrap', fontFamily: 'monospace', fontSize: '12px', color: '#cbd5e1', margin: 0}}>
                      {result.result}
                    </pre>
                  </div>
                ))}
              </>
            ) : (
              <div style={{...styles.graphCard, borderColor: '#ef4444', borderWidth: '2px'}}>
                <h3 style={{fontSize: '16px', fontWeight: '600', color: '#ef4444', margin: '0 0 1rem 0'}}>âŒ Error</h3>
                <p style={{color: '#cbd5e1'}}>{analysisResults.error}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );

  const GraphsPage = () => {
    const renderChart = (chart) => {
      if (chart.chart_type === 'error') {
        return (
          <div key={chart.title} style={{...styles.graphCard, borderColor: '#ef4444', marginBottom: '1.5rem'}}>
            <h3 style={{fontSize: '16px', fontWeight: '600', color: '#ef4444', margin: '0 0 1rem 0'}}>âŒ {chart.title}</h3>
            <p style={{color: '#cbd5e1'}}>{chart.error}</p>
          </div>
        );
      }

      if (!chart.data || chart.data.length === 0) return null;

      const colors = ['#3b82f6', '#f97316', '#10b981', '#ec4899', '#f59e0b'];

      try {
        let chartComponent = null;

        switch (chart.chart_type) {
          case 'line':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" label={{ value: chart.xKey, position: 'insideBottom', offset: -5, textAnchor: 'middle' }} />
                  <YAxis stroke="#94a3b8" label={{ value: chart.yKey, angle: -90, position: 'insideLeft', textAnchor: 'middle' }} />
                  <Tooltip content={<EnhancedTooltip />} />
                  <Legend />
                  <Line type="monotone" dataKey={chart.yKey} stroke={colors[0]} strokeWidth={2} dot={{ r: 4 }} />
                </LineChart>
              </ResponsiveContainer>
            );
            break;

          case 'bar':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data} margin={{left: 0, right: 0, top: 0, bottom: 0}}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" label={{ value: chart.xKey, position: 'insideBottom', offset: -5, textAnchor: 'middle' }} />
                  <YAxis stroke="#94a3b8" label={{ value: chart.yKey, angle: -90, position: 'insideLeft', textAnchor: 'middle' }} />
                  <Tooltip content={<EnhancedTooltip />} />
                  <Legend />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
            break;

          case 'pie':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={chart.data} dataKey={chart.yKey} nameKey={chart.xKey} cx="50%" cy="50%" outerRadius={100}>
                    {chart.data.map((_, i) => <Cell key={i} fill={colors[i % 5]} />)}
                  </Pie>
                  <Tooltip content={<EnhancedTooltip />} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            );
            break;

          default:
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" label={{ value: chart.xKey, position: 'insideBottom', offset: -5, textAnchor: 'middle' }} />
                  <YAxis stroke="#94a3b8" label={{ value: chart.yKey, angle: -90, position: 'insideLeft', textAnchor: 'middle' }} />
                  <Tooltip content={<EnhancedTooltip />} />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
        }

        return (
          <div key={chart.title} style={{...styles.graphCard, marginBottom: '1.5rem'}}>
            <h3 style={styles.graphCardTitle}>{chart.title}</h3>
            {chartComponent}
          </div>
        );
      } catch (e) {
        console.error('Chart error:', e);
        return null;
      }
    };

    return (
      <div style={styles.container}>
        <Navigation />
        <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
          <div style={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '2rem' }}>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <input 
                autoFocus
                type="text" 
                placeholder="Ask for visualizations (e.g., 'Show revenue trends by month')..." 
                value={graphQuery} 
                onChange={(e) => setGraphQuery(e.target.value)}
                onKeyDown={(e) => { if (e.key === 'Enter') handleGenerateGraphs(e); }}
                style={{ flex: 1, padding: '0.75rem', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '0.5rem', color: 'white', fontFamily: 'inherit' }} 
              />
              <button onClick={handleGenerateGraphs} disabled={graphLoading} style={{padding: '0.75rem 1.5rem', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600', opacity: graphLoading ? 0.6 : 1}}>
                {graphLoading ? 'ðŸ“Š Generating...' : 'Generate'}
              </button>
            </div>
          </div>

          {graphLoading && <div style={{ padding: '2rem', textAlign: 'center', color: '#cbd5e1' }}>Generating...</div>}

          {generatedCharts.length > 0 && !graphLoading && <div>{generatedCharts.map(chart => renderChart(chart))}</div>}

          {generatedCharts.length === 0 && !graphLoading && (
            <div>
              <h3 style={{color: '#e2e8f0', marginBottom: '1rem', fontSize: '1.125rem', fontWeight: 'bold', textAlign: 'center'}}>ðŸ“Š Suggested Visualizations:</h3>
              {[
                'Show total revenue by month (line chart)',
                'What are the top product categories by volume (bar chart)',
                'Breakdown of regions (pie chart)',
                'Revenue trends over time (line chart)',
                'Average AOV by product category (bar chart)'
              ].map((item, idx) => (
                <div 
                  key={idx} 
                  style={{backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '1rem', cursor: 'pointer'}}
                  onClick={() => setGraphQuery(item)}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#334155'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#1e293b'}
                >
                  <p style={{color: '#cbd5e1', margin: 0}}>â†’ {item}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <>
      <style>{`@keyframes spin { to { transform: rotate(360deg); } }`}</style>
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'dashboard' && <DashboardPage />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <GraphsPage />}
    </>
  );
}
