import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area, ComposedChart } from 'recharts';
import { Menu, X, ChevronDown } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// ============================================================================
// API CLIENT
// ============================================================================
class APIClient {
  static async fetchDashboardData(filters = {}) {
    try {
      const params = new URLSearchParams();
      if (filters.regions && filters.regions.length > 0) params.append('regions', filters.regions.join(','));
      if (filters.categories && filters.categories.length > 0) params.append('categories', filters.categories.join(','));
      if (filters.customerTenureMin !== undefined && filters.customerTenureMin > 0) params.append('customerTenureMin', filters.customerTenureMin);
      if (filters.customerTenureMax !== undefined && filters.customerTenureMax > 0) params.append('customerTenureMax', filters.customerTenureMax);
      if (filters.customerRecencyMin !== undefined && filters.customerRecencyMin > 0) params.append('customerRecencyMin', filters.customerRecencyMin);
      if (filters.customerRecencyMax !== undefined && filters.customerRecencyMax > 0) params.append('customerRecencyMax', filters.customerRecencyMax);
      if (filters.totalTransactionsMin !== undefined && filters.totalTransactionsMin > 0) params.append('totalTransactionsMin', filters.totalTransactionsMin);
      if (filters.totalTransactionsMax !== undefined && filters.totalTransactionsMax > 0) params.append('totalTransactionsMax', filters.totalTransactionsMax);
      if (filters.discountMin !== undefined && filters.discountMin > 0) params.append('discountMin', filters.discountMin);
      if (filters.discountMax !== undefined && filters.discountMax > 0) params.append('discountMax', filters.discountMax);
      if (filters.timeFilter && filters.timeFilter !== 'all') params.append('timeFilter', filters.timeFilter);

      const url = `${API_BASE_URL}/data${params.toString() ? '?' + params.toString() : ''}`;
      console.log('ðŸŒ Fetching from:', url);
      const response = await fetch(url);
      console.log('ðŸ“¡ Response status:', response.status, response.statusText);

      if (!response.ok) {
        throw new Error(`API error: ${response.status} ${response.statusText}`);
      }

      const rawData = await response.json();
      console.log('ðŸ“¦ Response received');

      if (!Array.isArray(rawData) || rawData.length === 0) {
        console.error('âŒ Response is not an array or empty:', typeof rawData);
        return null;
      }

      const aggregatedData = rawData[0];
      console.log('âœ… Aggregated data extracted');
      return aggregatedData;
    } catch (error) {
      console.error('âŒ Dashboard data fetch error:', error.message);
      return null;
    }
  }

  static async analyzeText(userMessage) {
    try {
      console.log('ðŸ¤– Sending to OpenAI:', userMessage);
      const response = await fetch(`${API_BASE_URL}/text/analyze`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_message: userMessage,
        }),
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      console.log('âœ… Analysis complete:', data);

      if (data.success) {
        return {
          success: true,
          summary: data.summary,
          questions: data.questions || [],
          results: data.results || [],
          timestamp: new Date().toISOString(),
        };
      } else {
        return {
          success: false,
          error: data.error || 'Unknown error occurred',
        };
      }
    } catch (error) {
      console.error('âŒ Text analysis error:', error);
      return {
        success: false,
        error: error.message || 'Failed to connect to API',
      };
    }
  }

  static async generateGraphs(userMessage) {
    try {
      console.log('ðŸ“Š Requesting graphs:', userMessage);
      const response = await fetch(`${API_BASE_URL}/graphs/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_message: userMessage,
        }),
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      console.log('âœ… Graphs generated:', data);

      if (data.success) {
        return {
          success: true,
          charts: data.charts || [],
          questions: data.questions || '',
          timestamp: new Date().toISOString(),
        };
      } else {
        return {
          success: false,
          error: data.error || 'Unknown error occurred',
        };
      }
    } catch (error) {
      console.error('âŒ Graph generation error:', error);
      return {
        success: false,
        error: error.message || 'Failed to connect to API',
      };
    }
  }
}

const formatCurrency = (value) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  if (num >= 1000000) return `$${(num / 1000000).toFixed(1)}M`;
  if (num >= 1000) return `$${(num / 1000).toFixed(1)}K`;
  return `$${num.toFixed(2)}`;
};

const formatCurrencyWithCommas = (value) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  return `$${num.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
};

const CustomTooltip = ({ active, payload, isCurrency = false }) => {
  if (active && payload && payload.length) {
    const value = payload[0].value;
    const formattedValue = isCurrency ? formatCurrencyWithCommas(value) : typeof value === 'number' ? value.toFixed(2) : value;
    return (
      <div style={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '8px', color: '#e2e8f0', padding: '8px' }}>
        <p style={{ margin: 0 }}>{`${payload[0].name}: ${formattedValue}`}</p>
      </div>
    );
  }
  return null;
};

const styles = {
  container: { minHeight: '100vh', background: 'linear-gradient(to bottom right, #0f172a, #1e293b)' },
  header: { background: 'rgba(15, 23, 42, 0.8)', backdropFilter: 'blur(10px)', borderBottom: '1px solid #475569', position: 'sticky', top: 0, zIndex: 50 },
  headerContent: { maxWidth: '100%', margin: '0 auto', padding: '1.5rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  logo: { fontSize: '1.875rem', fontWeight: 'bold', background: 'linear-gradient(to right, #60a5fa, #22d3ee)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', margin: 0 },
  nav: { display: 'flex', gap: '1rem' },
  navButton: (isActive) => ({ padding: '0.5rem 1rem', borderRadius: '0.5rem', fontWeight: '500', border: 'none', cursor: 'pointer', backgroundColor: isActive ? '#2563eb' : 'transparent', color: isActive ? 'white' : '#cbd5e1' }),
  mainContainer: { display: 'grid', gridTemplateColumns: '300px 1fr' },
  mainContainerCollapsed: { display: 'grid', gridTemplateColumns: '1fr' },
  sidebar: { background: '#1e293b', borderRight: '1px solid #475569', padding: '24px', overflowY: 'auto', position: 'sticky', top: '80px', height: 'calc(100vh - 80px)' },
  sidebarSection: { marginBottom: '20px', paddingBottom: '16px', borderBottom: '1px solid #475569' },
  sidebarLabel: { fontWeight: '600', color: '#e2e8f0', marginBottom: '8px', display: 'block', fontSize: '12px' },
  sidebarSelect: { width: '100%', padding: '8px 12px', border: '1px solid #475569', borderRadius: '8px', fontSize: '12px', fontFamily: 'inherit', backgroundColor: '#0f172a', color: '#e2e8f0', cursor: 'pointer' },
  sidebarInput: { width: '100%', padding: '8px 12px', border: '1px solid #475569', borderRadius: '8px', fontSize: '12px', fontFamily: 'inherit', backgroundColor: '#0f172a', color: '#e2e8f0' },
  content: { padding: '32px', overflowY: 'auto', height: 'calc(100vh - 80px)' },
  kpiContainer: { background: '#1e293b', borderRadius: '12px', padding: '28px', boxShadow: '0 4px 12px rgba(0,0,0,0.3)', marginBottom: '32px', border: '1px solid #475569' },
  kpiGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(160px, 1fr))', gap: '16px', marginBottom: '16px' },
  kpiCard: (color) => ({ background: color, borderRadius: '12px', padding: '24px', textAlign: 'center', color: 'white', border: '1px solid rgba(255,255,255,0.1)' }),
  kpiLabel: { fontSize: '10px', fontWeight: '700', textTransform: 'uppercase', color: 'rgba(255,255,255,0.75)', marginBottom: '10px' },
  kpiValue: { fontSize: '24px', fontWeight: '700', color: '#FFFFFF', margin: 0 },
  chartsGrid: { display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '20px' },
  graphCard: { backgroundColor: '#1e293b', borderRadius: '12px', padding: '20px', minHeight: '400px', display: 'flex', flexDirection: 'column', border: '1px solid #475569' },
  graphCardFull: { gridColumn: 'span 2' },
  graphCardTitle: { fontSize: '14px', fontWeight: '600', color: '#e2e8f0', margin: '0 0 12px 0' },
};

export default function EcommerceDashboard() {
  const [currentPage, setCurrentPage] = useState('home');
  const [aggregatedData, setAggregatedData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [analysisLoading, setAnalysisLoading] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [graphQuery, setGraphQuery] = useState('');
  const [generatedCharts, setGeneratedCharts] = useState([]);
  const [graphLoading, setGraphLoading] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [timeFilter, setTimeFilter] = useState('all');

  // Global filters
  const [selectedRegions, setSelectedRegions] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [customerTenureMin, setCustomerTenureMin] = useState(0);
  const [customerTenureMax, setCustomerTenureMax] = useState(100);
  const [customerRecencyMin, setCustomerRecencyMin] = useState(0);
  const [customerRecencyMax, setCustomerRecencyMax] = useState(100);
  const [totalTransactionsMin, setTotalTransactionsMin] = useState(0);
  const [totalTransactionsMax, setTotalTransactionsMax] = useState(50);
  const [discountMin, setDiscountMin] = useState(0);
  const [discountMax, setDiscountMax] = useState(100);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await APIClient.fetchDashboardData({
          regions: selectedRegions,
          categories: selectedCategories,
          customerTenureMin,
          customerTenureMax,
          customerRecencyMin,
          customerRecencyMax,
          totalTransactionsMin,
          totalTransactionsMax,
          discountMin,
          discountMax,
          timeFilter
        });
        if (!data) {
          setError('No data returned from API');
          setAggregatedData(null);
        } else {
          setAggregatedData(data);
          // Update max values for sliders based on data
          if (data.filterLists) {
            setCustomerTenureMax(data.filterLists.tenureMax);
            setCustomerRecencyMax(data.filterLists.recencyMax);
            setTotalTransactionsMax(data.filterLists.transactionsMax);
            setDiscountMax(data.filterLists.discountMax);
          }
          setError(null);
          console.log('âœ… Aggregated data loaded:', data);
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch data');
        setAggregatedData(null);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [selectedRegions, selectedCategories, customerTenureMin, customerTenureMax, customerRecencyMin, customerRecencyMax, totalTransactionsMin, totalTransactionsMax, discountMin, discountMax, timeFilter]);

  const handleTextAnalysis = async (e) => {
    e?.preventDefault();
    if (!searchQuery.trim()) return;

    setAnalysisLoading(true);
    setAnalysisResults(null);

    const result = await APIClient.analyzeText(searchQuery);
    setAnalysisResults(result);
    setAnalysisLoading(false);
  };

  const handleGenerateGraphs = async (e) => {
    e?.preventDefault();
    if (!graphQuery.trim()) return;

    setGraphLoading(true);
    setGeneratedCharts([]);

    const result = await APIClient.generateGraphs(graphQuery);

    if (result.success) {
      setGeneratedCharts(result.charts || []);
    } else {
      setGeneratedCharts([{
        title: 'Error',
        chart_type: 'error',
        error: result.error || 'Failed to generate graphs'
      }]);
    }

    setGraphLoading(false);
    setGraphQuery('');
  };

  if (loading) {
    return <div style={{...styles.container, display: 'flex', alignItems: 'center', justifyContent: 'center'}}><div style={{color: 'white', fontSize: '18px'}}>â³ Loading data...</div></div>;
  }

  if (error) {
    return <div style={{...styles.container, display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px'}}><div style={{color: '#ef4444', fontSize: '18px'}}>âŒ {error}</div></div>;
  }

  const Navigation = () => (
    <div style={styles.header}>
      <div style={styles.headerContent}>
        <h1 style={styles.logo}>ðŸ“Š Analytics Dashboard</h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {currentPage === 'dashboard' && (
            <button onClick={() => setSidebarOpen(!sidebarOpen)} style={{ background: 'none', border: 'none', color: '#cbd5e1', cursor: 'pointer', fontSize: '20px' }}>
              {sidebarOpen ? <X size={20} /> : <Menu size={20} />}
            </button>
          )}
          <nav style={styles.nav}>
            {['home', 'dashboard', 'text', 'graphs'].map(page => (
              <button key={page} onClick={() => setCurrentPage(page)} style={styles.navButton(currentPage === page)}>
                {page.charAt(0).toUpperCase() + page.slice(1)}
              </button>
            ))}
          </nav>
        </div>
      </div>
    </div>
  );

  const HomePage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '5rem 1.5rem', textAlign: 'center' }}>
        <h2 style={{ fontSize: '3rem', fontWeight: 'bold', color: 'white', marginBottom: '1rem' }}>Advanced Analytics Dashboard</h2>
        <p style={{ fontSize: '1.25rem', color: '#cbd5e1', marginBottom: '3rem' }}>Track revenue, customer metrics, and product performance</p>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem' }}>
          {[
            { page: 'dashboard', icon: 'ðŸ“Š', title: 'Dashboard', desc: 'Monitor KPIs and analytics' },
            { page: 'text', icon: 'âœï¸', title: 'Text Analysis', desc: 'Ask questions about your data' },
            { page: 'graphs', icon: 'ðŸ“ˆ', title: 'Reports', desc: 'Generate custom visualizations' }
          ].map(card => (
            <div key={card.page} style={{ backgroundColor: '#1e293b', borderRadius: '1rem', padding: '2rem', border: '1px solid #475569', cursor: 'pointer' }} onClick={() => setCurrentPage(card.page)}>
              <span style={{ fontSize: '3.75rem', marginBottom: '1rem', display: 'block' }}>{card.icon}</span>
              <h3 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: 'white', marginBottom: '0.75rem' }}>{card.title}</h3>
              <p style={{color: '#cbd5e1', marginBottom: '1.5rem'}}>{card.desc}</p>
              <button style={{ padding: '0.75rem 1.5rem', background: 'linear-gradient(to right, #2563eb, #1d4ed8)', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600' }}>Explore â†’</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const DashboardPage = () => !aggregatedData ? null : (
    <div style={styles.container}>
      <Navigation />
      <div style={sidebarOpen ? styles.mainContainer : styles.mainContainerCollapsed}>
        {sidebarOpen && (
          <div style={styles.sidebar}>
            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Region</label>
              <select multiple style={{...styles.sidebarSelect, height: '80px'}} value={selectedRegions} onChange={(e) => setSelectedRegions(Array.from(e.target.selectedOptions, opt => opt.value))}>
                {(aggregatedData.filterLists?.regions || []).map(r => <option key={r} value={r}>{r}</option>)}
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Product Category</label>
              <select multiple style={{...styles.sidebarSelect, height: '80px'}} value={selectedCategories} onChange={(e) => setSelectedCategories(Array.from(e.target.selectedOptions, opt => opt.value))}>
                {(aggregatedData.filterLists?.categories || []).map(c => <option key={c} value={c}>{c}</option>)}
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Customer Tenure: {customerTenureMin.toFixed(0)} - {customerTenureMax.toFixed(0)}</label>
              <input type="range" min="0" max={aggregatedData.filterLists?.tenureMax || 100} step="1" value={customerTenureMin} onChange={(e) => setCustomerTenureMin(parseFloat(e.target.value))} style={{width: '100%', marginBottom: '4px', cursor: 'pointer'}} />
              <input type="range" min="0" max={aggregatedData.filterLists?.tenureMax || 100} step="1" value={customerTenureMax} onChange={(e) => setCustomerTenureMax(parseFloat(e.target.value))} style={{width: '100%', cursor: 'pointer'}} />
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Customer Recency: {customerRecencyMin.toFixed(0)} - {customerRecencyMax.toFixed(0)}</label>
              <input type="range" min="0" max={aggregatedData.filterLists?.recencyMax || 100} step="1" value={customerRecencyMin} onChange={(e) => setCustomerRecencyMin(parseFloat(e.target.value))} style={{width: '100%', marginBottom: '4px', cursor: 'pointer'}} />
              <input type="range" min="0" max={aggregatedData.filterLists?.recencyMax || 100} step="1" value={customerRecencyMax} onChange={(e) => setCustomerRecencyMax(parseFloat(e.target.value))} style={{width: '100%', cursor: 'pointer'}} />
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Total Transactions: {totalTransactionsMin.toFixed(0)} - {totalTransactionsMax.toFixed(0)}</label>
              <input type="range" min="0" max={aggregatedData.filterLists?.transactionsMax || 50} step="1" value={totalTransactionsMin} onChange={(e) => setTotalTransactionsMin(parseFloat(e.target.value))} style={{width: '100%', marginBottom: '4px', cursor: 'pointer'}} />
              <input type="range" min="0" max={aggregatedData.filterLists?.transactionsMax || 50} step="1" value={totalTransactionsMax} onChange={(e) => setTotalTransactionsMax(parseFloat(e.target.value))} style={{width: '100%', cursor: 'pointer'}} />
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Discount Applied: {discountMin.toFixed(0)}% - {discountMax.toFixed(0)}%</label>
              <input type="range" min="0" max={aggregatedData.filterLists?.discountMax || 100} step="1" value={discountMin} onChange={(e) => setDiscountMin(parseFloat(e.target.value))} style={{width: '100%', marginBottom: '4px', cursor: 'pointer'}} />
              <input type="range" min="0" max={aggregatedData.filterLists?.discountMax || 100} step="1" value={discountMax} onChange={(e) => setDiscountMax(parseFloat(e.target.value))} style={{width: '100%', cursor: 'pointer'}} />
            </div>
          </div>
        )}

        <div style={styles.content}>
          <div style={styles.kpiContainer}>
            <div style={styles.kpiGrid}>
              {[
                { label: 'Total Revenue', value: formatCurrency(aggregatedData.kpis?.totalRevenue || 0), color: 'linear-gradient(135deg, #3b82f6, #06b6d4)' },
                { label: 'Avg AOV', value: formatCurrency(aggregatedData.kpis?.avgAOV || 0), color: 'linear-gradient(135deg, #10b981, #059669)' },
                { label: 'Avg LTV', value: formatCurrency(aggregatedData.kpis?.avgLTV || 0), color: 'linear-gradient(135deg, #a855f7, #7c3aed)' },
                { label: 'Avg CAC %', value: `${(aggregatedData.kpis?.avgCACPercent || 0).toFixed(2)}%`, color: 'linear-gradient(135deg, #f97316, #ea580c)' },
                { label: 'Avg ROAS', value: `${(aggregatedData.kpis?.avgROAS || 0).toFixed(2)}x`, color: 'linear-gradient(135deg, #ec4899, #db2777)' },
                { label: 'Avg Lifetime', value: `${(aggregatedData.kpis?.avgLifetime || 0).toFixed(0)} days`, color: 'linear-gradient(135deg, #64748b, #475569)' },
              ].map((kpi, idx) => (
                <div key={idx} style={styles.kpiCard(kpi.color)}>
                  <div style={styles.kpiLabel}>{kpi.label}</div>
                  <h2 style={styles.kpiValue}>{kpi.value}</h2>
                </div>
              ))}
            </div>
          </div>

          <div style={{marginBottom: '24px', display: 'flex', gap: '12px', flexWrap: 'wrap', width: '100%'}}>
            {['1m', '3m', '6m', '1y', 'all'].map(period => (
              <button key={period} onClick={() => setTimeFilter(period)} style={{padding: '10px 20px', flex: '1 1 auto', backgroundColor: timeFilter === period ? '#3b82f6' : '#334155', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontSize: '13px', fontWeight: '600', transition: 'all 0.2s', minWidth: '120px'}}>
                {period === '1m' ? 'Last Month' : period === '3m' ? '3 Months' : period === '6m' ? '6 Months' : period === '1y' ? '1 Year' : 'All Time'}
              </button>
            ))}
          </div>

          <div style={styles.chartsGrid}>
            <div style={{...styles.graphCard, ...styles.graphCardFull}}>
              <h3 style={styles.graphCardTitle}>ðŸ“ˆ Revenue, Lost Revenue & CAC (Monthly)</h3>
              <ResponsiveContainer width="100%" height={350}>
                <ComposedChart data={aggregatedData.monthlyData || []}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="month" stroke="#94a3b8" />
                  <YAxis yAxisId="left" stroke="#94a3b8" />
                  <YAxis yAxisId="right" orientation="right" stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip isCurrency={true} />} />
                  <Legend />
                  <Line yAxisId="left" type="monotone" dataKey="revenue" stroke="#3b82f6" strokeWidth={2} name="Revenue" />
                  <Line yAxisId="left" type="monotone" dataKey="lost_revenue" stroke="#ef4444" strokeWidth={2} name="Lost Revenue" />
                  <Line yAxisId="right" type="monotone" dataKey="cac" stroke="#f97316" strokeWidth={2} name="CAC" />
                </ComposedChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <h3 style={styles.graphCardTitle}>ðŸ—ºï¸ Orders by Region</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={aggregatedData.regionData || []} cx="50%" cy="50%" dataKey="value" nameKey="name">
                    {(aggregatedData.regionData || []).map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                  </Pie>
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <h3 style={styles.graphCardTitle}>ðŸ“¦ Product Category Order Volumes (Monthly)</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={aggregatedData.categoryMonthlyData || []}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="month" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar dataKey="volume" fill="#8b5cf6" radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <h3 style={styles.graphCardTitle}>ðŸ›ï¸ Customer Purchase Distribution</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={aggregatedData.histogramData || []}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="num_purchases" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar dataKey="customer_count" fill="#06b6d4" radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div style={{...styles.graphCard, ...styles.graphCardFull}}>
              <h3 style={styles.graphCardTitle}>ðŸ’° Customer AOV vs Days Since First Purchase</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={aggregatedData.aovDaysData || []}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="days_since_first" stroke="#94a3b8" label={{ value: 'Days Since First Purchase', position: 'insideBottomRight', offset: -5 }} />
                  <YAxis stroke="#94a3b8" label={{ value: 'Average AOV', angle: -90, position: 'insideLeft' }} />
                  <Tooltip content={<CustomTooltip isCurrency={true} />} />
                  <Legend />
                  <Line type="monotone" dataKey="avg_aov" stroke="#10b981" strokeWidth={2} name="Average AOV" />
                </LineChart>
              </ResponsiveContainer>
            </div>

            <div style={{...styles.graphCard, ...styles.graphCardFull}}>
              <h3 style={styles.graphCardTitle}>ðŸ“Š ROAS by Product Category</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={aggregatedData.roasCategoryData || []} layout="vertical" margin={{left: 100}}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis type="number" stroke="#94a3b8" />
                  <YAxis dataKey="category" type="category" stroke="#94a3b8" width={100} />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar dataKey="avg_roas" fill="#ec4899" radius={[0, 8, 8, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const TextAnalysisPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '56rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '2rem' }}>
          <div style={{ display: 'flex', gap: '0.5rem' }}>
            <input 
              autoFocus
              type="text" 
              placeholder="Ask about your data..." 
              value={searchQuery} 
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDown={(e) => { if (e.key === 'Enter') handleTextAnalysis(e); }}
              style={{ flex: 1, padding: '0.75rem', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '0.5rem', color: 'white', fontFamily: 'inherit' }} 
            />
            <button onClick={handleTextAnalysis} disabled={analysisLoading} style={{padding: '0.75rem 1.5rem', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600', opacity: analysisLoading ? 0.6 : 1}}>
              {analysisLoading ? 'ðŸ¤– Analyzing...' : 'Analyze'}
            </button>
          </div>
        </div>

        {analysisLoading && <div style={{ padding: '2rem', textAlign: 'center', color: '#cbd5e1' }}>Analyzing...</div>}

        {analysisResults && !analysisLoading && (
          <div>
            {analysisResults.success ? (
              <>
                <div style={{...styles.graphCard, backgroundColor: '#164e63', marginBottom: '1.5rem'}}>
                  <h3 style={{color: '#22d3ee', fontSize: '16px', fontWeight: '600'}}>ðŸ“Š Summary</h3>
                  <p style={{color: '#e0f2fe', lineHeight: '1.6'}}>{analysisResults.summary}</p>
                </div>
                {(analysisResults.results || []).map((result, idx) => (
                  <div key={idx} style={{...styles.graphCard, marginBottom: '1.5rem', opacity: result.status === 'error' ? 0.7 : 1}}>
                    <h3 style={{fontSize: '16px', fontWeight: '600', color: result.status === 'success' ? '#10b981' : '#ef4444', margin: '0 0 1rem 0'}}>
                      {result.status === 'success' ? 'âœ“' : 'âœ—'} {result.question}
                    </h3>
                    <pre style={{whiteSpace: 'pre-wrap', fontFamily: 'monospace', fontSize: '12px', color: '#cbd5e1', margin: 0}}>
                      {result.result}
                    </pre>
                  </div>
                ))}
              </>
            ) : (
              <div style={{...styles.graphCard, borderColor: '#ef4444', borderWidth: '2px'}}>
                <h3 style={{fontSize: '16px', fontWeight: '600', color: '#ef4444', margin: '0 0 1rem 0'}}>âŒ Error</h3>
                <p style={{color: '#cbd5e1'}}>{analysisResults.error}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );

  const GraphsPage = () => {
    const renderChart = (chart) => {
      if (chart.chart_type === 'error') {
        return (
          <div key={chart.title} style={{...styles.graphCard, borderColor: '#ef4444', marginBottom: '1.5rem'}}>
            <h3 style={{fontSize: '16px', fontWeight: '600', color: '#ef4444', margin: '0 0 1rem 0'}}>âŒ {chart.title}</h3>
            <p style={{color: '#cbd5e1'}}>{chart.error}</p>
          </div>
        );
      }

      if (!chart.data || chart.data.length === 0) return null;

      const colors = ['#3b82f6', '#f97316', '#10b981', '#ec4899', '#f59e0b'];

      try {
        let chartComponent = null;

        switch (chart.chart_type) {
          case 'line':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Line type="monotone" dataKey={chart.yKey} stroke={colors[0]} strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            );
            break;

          case 'bar':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
            break;

          case 'pie':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={chart.data} dataKey={chart.yKey} nameKey={chart.xKey} cx="50%" cy="50%" outerRadius={100}>
                    {chart.data.map((_, i) => <Cell key={i} fill={colors[i % 5]} />)}
                  </Pie>
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            );
            break;

          default:
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
        }

        return (
          <div key={chart.title} style={{...styles.graphCard, marginBottom: '1.5rem'}}>
            <h3 style={styles.graphCardTitle}>{chart.title}</h3>
            {chartComponent}
          </div>
        );
      } catch (e) {
        console.error('Chart error:', e);
        return null;
      }
    };

    return (
      <div style={styles.container}>
        <Navigation />
        <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
          <div style={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '2rem' }}>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <input 
                autoFocus
                type="text" 
                placeholder="Ask for visualizations (e.g., 'Show revenue trends by month')..." 
                value={graphQuery} 
                onChange={(e) => setGraphQuery(e.target.value)}
                onKeyDown={(e) => { if (e.key === 'Enter') handleGenerateGraphs(e); }}
                style={{ flex: 1, padding: '0.75rem', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '0.5rem', color: 'white', fontFamily: 'inherit' }} 
              />
              <button onClick={handleGenerateGraphs} disabled={graphLoading} style={{padding: '0.75rem 1.5rem', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600', opacity: graphLoading ? 0.6 : 1}}>
                {graphLoading ? 'ðŸ“Š Generating...' : 'Generate'}
              </button>
            </div>
          </div>

          {graphLoading && <div style={{ padding: '2rem', textAlign: 'center', color: '#cbd5e1' }}>Generating...</div>}

          {generatedCharts.length > 0 && !graphLoading && <div>{generatedCharts.map(chart => renderChart(chart))}</div>}

          {generatedCharts.length === 0 && !graphLoading && (
            <div>
              <h3 style={{color: '#e2e8f0', marginBottom: '1rem', fontSize: '1.125rem', fontWeight: 'bold'}}>ðŸ“Š Suggested Visualizations:</h3>
              {[
                'Show total revenue by month (line chart)',
                'What are the top product categories by volume (bar chart)',
                'Breakdown of regions (pie chart)',
                'Revenue trends over time (line chart)',
                'Average AOV by product category (bar chart)'
              ].map((item, idx) => (
                <div 
                  key={idx} 
                  style={{backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '1rem', cursor: 'pointer'}}
                  onClick={() => setGraphQuery(item)}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#334155'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#1e293b'}
                >
                  <p style={{color: '#cbd5e1', margin: 0}}>â†’ {item}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <>
      <style>{`@keyframes spin { to { transform: rotate(360deg); } }`}</style>
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'dashboard' && <DashboardPage />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <GraphsPage />}
    </>
  );
}
