import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Menu, X } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// ============================================================================
// API CLIENT - Updated to include text analysis and graph generation
// ============================================================================
class APIClient {
  static async fetchDashboardData() {
    try {
      console.log('ðŸŒ Fetching from:', `${API_BASE_URL}/data`);
      const response = await fetch(`${API_BASE_URL}/data`);
      console.log('ðŸ“¡ Response status:', response.status, response.statusText);

      if (!response.ok) {
        throw new Error(`API error: ${response.status} ${response.statusText}`);
      }

      const rawData = await response.json();
      console.log('ðŸ“¦ Response received');

      if (!Array.isArray(rawData)) {
        console.error('âŒ Response is not an array:', typeof rawData);
        return [];
      }

      console.log(`âœ… Successfully loaded ${rawData.length} records`);
      return rawData;
    } catch (error) {
      console.error('âŒ Dashboard data fetch error:', error.message);
      return [];
    }
  }

  static async analyzeText(userMessage) {
    try {
      console.log('ðŸ¤– Sending to OpenAI:', userMessage);
      const response = await fetch(`${API_BASE_URL}/text/analyze`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_message: userMessage,
        }),
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      console.log('âœ… Analysis complete:', data);

      if (data.success) {
        return {
          success: true,
          summary: data.summary,
          questions: data.questions || [],
          results: data.results || [],
          timestamp: new Date().toISOString(),
        };
      } else {
        return {
          success: false,
          error: data.error || 'Unknown error occurred',
        };
      }
    } catch (error) {
      console.error('âŒ Text analysis error:', error);
      return {
        success: false,
        error: error.message || 'Failed to connect to API',
      };
    }
  }

  static async generateGraphs(userMessage) {
    try {
      console.log('ðŸ“Š Requesting graphs:', userMessage);
      const response = await fetch(`${API_BASE_URL}/graphs/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_message: userMessage,
        }),
      });

      console.log('ðŸ“¡ Response status:', response.status);

      if (!response.ok) {
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      console.log('âœ… Graphs generated:', data);

      if (data.success) {
        return {
          success: true,
          charts: data.charts || [],
          questions: data.questions || '',
          timestamp: new Date().toISOString(),
        };
      } else {
        return {
          success: false,
          error: data.error || 'Unknown error occurred',
        };
      }
    } catch (error) {
      console.error('âŒ Graph generation error:', error);
      return {
        success: false,
        error: error.message || 'Failed to connect to API',
      };
    }
  }
}

const COLUMNS = {
  orderId: 'Order ID',
  purchaseTimestamp: 'Purchase Timestamp',
  reviewScore: 'Review Score',
  deliveredOnTime: 'Delivered On Time',
  paymentType: 'Payment Type',
  paymentValue: 'Payment Value',
  shippingCost: 'Shipping Cost',
  productCategory: 'Product Category',
  customerState: 'Customer State',
  customerId: 'Customer ID',
  orderStatus: 'Order Status',
  deliveryDurationDays: 'Time to Deliver to Customer Days'
};

// Color scale for heatmap
const getColorForValue = (value, maxValue) => {
  if (!value) return '#e5e7eb';
  const intensity = Math.min(value / maxValue, 1);

  if (intensity < 0.2) return '#bfdbfe';
  if (intensity < 0.4) return '#7dd3fc';
  if (intensity < 0.6) return '#38bdf8';
  if (intensity < 0.8) return '#0284c7';
  return '#0c4a6e';
};

// ============================================================================
// BRAZIL STATE MAP - Fixed to show actual Brazil map visualization
// ============================================================================
const BrazilStateMap = ({ stateData, maxOrders }) => {
  const [hoveredState, setHoveredState] = useState(null);
  const [debugMode, setDebugMode] = useState(false);

  // Proper Brazilian state coordinates (approximate map positions)
  const brazilStates = [
    // North Region
    { code: 'AM', name: 'Amazonas', x: 280, y: 200 },
    { code: 'RR', name: 'Roraima', x: 320, y: 120 },
    { code: 'AP', name: 'AmapÃ¡', x: 420, y: 100 },
    { code: 'PA', name: 'ParÃ¡', x: 380, y: 240 },
    { code: 'TO', name: 'Tocantins', x: 450, y: 320 },
    { code: 'AC', name: 'Acre', x: 200, y: 280 },
    { code: 'RO', name: 'RondÃ´nia', x: 260, y: 320 },

    // Northeast Region
    { code: 'MA', name: 'MaranhÃ£o', x: 540, y: 320 },
    { code: 'PI', name: 'PiauÃ­', x: 520, y: 360 },
    { code: 'CE', name: 'CearÃ¡', x: 580, y: 360 },
    { code: 'RN', name: 'Rio Grande do Norte', x: 620, y: 340 },
    { code: 'PB', name: 'ParaÃ­ba', x: 640, y: 360 },
    { code: 'PE', name: 'Pernambuco', x: 660, y: 380 },
    { code: 'AL', name: 'Alagoas', x: 680, y: 390 },
    { code: 'SE', name: 'Sergipe', x: 700, y: 400 },
    { code: 'BA', name: 'Bahia', x: 660, y: 440 },

    // Central-West Region
    { code: 'MT', name: 'Mato Grosso', x: 420, y: 380 },
    { code: 'MS', name: 'Mato Grosso do Sul', x: 420, y: 450 },
    { code: 'GO', name: 'GoiÃ¡s', x: 480, y: 420 },
    { code: 'DF', name: 'Distrito Federal', x: 500, y: 440 },

    // Southeast Region
    { code: 'MG', name: 'Minas Gerais', x: 580, y: 480 },
    { code: 'SP', name: 'SÃ£o Paulo', x: 540, y: 520 },
    { code: 'RJ', name: 'Rio de Janeiro', x: 600, y: 520 },
    { code: 'ES', name: 'EspÃ­rito Santo', x: 620, y: 500 },

    // South Region
    { code: 'PR', name: 'ParanÃ¡', x: 520, y: 560 },
    { code: 'SC', name: 'Santa Catarina', x: 520, y: 600 },
    { code: 'RS', name: 'Rio Grande do Sul', x: 500, y: 640 },
  ];

  return (
    <div style={{ position: 'relative', width: '100%', height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', backgroundColor: '#f8fafc', borderRadius: '8px', padding: '10px' }}>
      <svg viewBox="0 0 900 750" style={{ width: '100%', height: '100%', maxWidth: '100%', backgroundColor: '#0284c7', borderRadius: '6px' }}>
        <defs>
          <style>{`
            .state-circle { cursor: pointer; transition: all 0.2s; filter: drop-shadow(0 1px 2px rgba(0,0,0,0.1)); }
            .state-circle:hover { filter: drop-shadow(0 2px 8px rgba(255,255,255,0.8)); stroke-width: 3; }
            .state-label { font-size: 11px; font-weight: 700; text-anchor: middle; pointer-events: none; fill: white; text-shadow: 0 1px 2px rgba(0,0,0,0.3); }
            .state-border { fill: none; stroke: #cbd5e1; stroke-width: 1; }
          `}</style>
        </defs>

        {/* Brazil ocean background */}
        <rect width="900" height="750" fill="#0284c7" />

        {/* Simplified Brazil landmass outline */}
        <path d="M 200 100 L 400 80 L 450 100 L 500 90 L 600 120 L 700 150 L 750 200 L 700 300 L 720 400 L 700 500 L 720 580 L 700 650 L 550 720 L 450 700 L 350 720 L 250 700 L 150 650 L 100 500 L 80 400 L 100 300 L 150 200 Z" 
          fill="#38bdf8" stroke="#0f172a" strokeWidth="2" opacity="1" className="state-border" />

        {/* States as circles with data */}
        {brazilStates.map(state => {
          const orders = stateData[state.code] || 0;
          const color = getColorForValue(orders, maxOrders);
          const radius = Math.max(12, Math.min(36, 12 + (orders / Math.max(maxOrders, 1)) * 24));

          return (
            <g key={state.code} onMouseEnter={() => setHoveredState(state.code)} onMouseLeave={() => setHoveredState(null)}>
              {/* Glow effect for high values */}
              {orders > maxOrders * 0.5 && (
                <circle cx={state.x} cy={state.y} r={radius + 8} fill={color} opacity="0.2" />
              )}

              {/* State circle */}
              <circle 
                cx={state.x} 
                cy={state.y} 
                r={radius} 
                fill={color} 
                className="state-circle" 
                stroke="white" 
                strokeWidth="2"
              />

              {/* State code label */}
              <text 
                x={state.x} 
                y={state.y + 5} 
                className="state-label"
                style={{ fontSize: radius > 20 ? '12px' : '10px', fontWeight: 'bold' }}
              >
                {state.code}
              </text>

              {/* Order count inside circle if space allows */}
              {radius > 18 && (
                <text 
                  x={state.x} 
                  y={state.y + 18} 
                  className="state-label"
                  style={{ fontSize: '9px', opacity: 0.8 }}
                >
                  {orders}
                </text>
              )}

              {/* Hover tooltip */}
              {hoveredState === state.code && (
                <g>
                  <rect 
                    x={state.x - 55} 
                    y={state.y - 70} 
                    width="110" 
                    height="55" 
                    fill="#0f172a" 
                    rx="6" 
                    stroke="#cbd5e1" 
                    strokeWidth="2"
                  />
                  <text 
                    x={state.x} 
                    y={state.y - 48} 
                    style={{ fontSize: '14px', fontWeight: 'bold', textAnchor: 'middle', fill: '#22d3ee' }}
                  >
                    {state.code}
                  </text>
                  <text 
                    x={state.x} 
                    y={state.y - 32} 
                    style={{ fontSize: '12px', textAnchor: 'middle', fill: '#10b981', fontWeight: 'bold' }}
                  >
                    {orders} orders
                  </text>
                  <text 
                    x={state.x} 
                    y={state.y - 18} 
                    style={{ fontSize: '10px', textAnchor: 'middle', fill: '#cbd5e1' }}
                  >
                    {state.name}
                  </text>
                </g>
              )}
            </g>
          );
        })}
      </svg>

      {/* Color scale legend */}
      <div style={{ marginTop: '12px', display: 'flex', alignItems: 'center', gap: '10px', fontSize: '12px', color: '#475569', flexWrap: 'wrap', justifyContent: 'center' }}>
        <span style={{ fontWeight: '600' }}>Order Volume:</span>
        <div style={{ display: 'flex', gap: '6px', alignItems: 'center' }}>
          <div style={{ width: '14px', height: '14px', backgroundColor: '#e5e7eb', border: '1px solid #9ca3af', borderRadius: '50%' }} />
          <span style={{ fontSize: '11px' }}>None</span>
        </div>
        <div style={{ display: 'flex', gap: '6px', alignItems: 'center' }}>
          <div style={{ width: '16px', height: '16px', backgroundColor: '#bfdbfe', border: '1px solid #9ca3af', borderRadius: '50%' }} />
          <span style={{ fontSize: '11px' }}>Low</span>
        </div>
        <div style={{ display: 'flex', gap: '6px', alignItems: 'center' }}>
          <div style={{ width: '18px', height: '18px', backgroundColor: '#38bdf8', border: '1px solid #9ca3af', borderRadius: '50%' }} />
          <span style={{ fontSize: '11px' }}>Medium</span>
        </div>
        <div style={{ display: 'flex', gap: '6px', alignItems: 'center' }}>
          <div style={{ width: '20px', height: '20px', backgroundColor: '#0284c7', border: '1px solid #9ca3af', borderRadius: '50%' }} />
          <span style={{ fontSize: '11px' }}>High</span>
        </div>
        <div style={{ display: 'flex', gap: '6px', alignItems: 'center' }}>
          <div style={{ width: '22px', height: '22px', backgroundColor: '#0c4a6e', border: '1px solid #9ca3af', borderRadius: '50%' }} />
          <span style={{ fontSize: '11px' }}>Max</span>
        </div>
      </div>

      {/* Debug mode toggle */}
      <button
        onClick={() => setDebugMode(!debugMode)}
        style={{ marginTop: '8px', padding: '4px 8px', fontSize: '10px', background: '#e5e7eb', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
      >
        {debugMode ? 'Hide Debug' : 'Show Debug'}
      </button>

      {/* Debug info */}
      {debugMode && (
        <div style={{ marginTop: '8px', padding: '8px', backgroundColor: '#f3f4f6', border: '1px solid #d1d5db', borderRadius: '4px', fontSize: '10px', maxHeight: '80px', overflowY: 'auto', maxWidth: '100%' }}>
          <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>States with data:</div>
          {Object.keys(stateData)
            .filter(code => stateData[code] > 0)
            .sort()
            .map(code => (
              <div key={code}>{code}: {stateData[code]} orders</div>
            ))}
          {Object.keys(stateData).filter(code => stateData[code] > 0).length === 0 && (
            <div style={{ color: '#ef4444' }}>âš ï¸ No state data found. Check your Customer State column!</div>
          )}
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { minHeight: '100vh', background: 'linear-gradient(to bottom right, #0f172a, #1e293b)' },
  header: { background: 'rgba(15, 23, 42, 0.8)', backdropFilter: 'blur(10px)', borderBottom: '1px solid #475569', position: 'sticky', top: 0, zIndex: 50 },
  headerContent: { maxWidth: '100%', margin: '0 auto', padding: '1.5rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  logo: { fontSize: '1.875rem', fontWeight: 'bold', background: 'linear-gradient(to right, #60a5fa, #22d3ee)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', margin: 0 },
  nav: { display: 'flex', gap: '1rem' },
  navButton: (isActive) => ({ padding: '0.5rem 1rem', borderRadius: '0.5rem', fontWeight: '500', border: 'none', cursor: 'pointer', backgroundColor: isActive ? '#2563eb' : 'transparent', color: isActive ? 'white' : '#cbd5e1' }),
  mainContainer: { display: 'grid', gridTemplateColumns: '280px 1fr' },
  mainContainerCollapsed: { display: 'grid', gridTemplateColumns: '1fr' },
  sidebar: { background: '#1e293b', borderRight: '1px solid #475569', padding: '24px', overflowY: 'auto', position: 'sticky', top: '80px', height: 'calc(100vh - 80px)' },
  sidebarSection: { marginBottom: '20px', paddingBottom: '16px', borderBottom: '1px solid #475569' },
  sidebarLabel: { fontWeight: '600', color: '#e2e8f0', marginBottom: '8px', display: 'block', fontSize: '13px' },
  sidebarSelect: { width: '100%', padding: '8px 12px', border: '1px solid #475569', borderRadius: '8px', fontSize: '13px', fontFamily: 'inherit', backgroundColor: '#0f172a', color: '#e2e8f0', cursor: 'pointer' },
  selectAllButton: { width: '100%', padding: '6px 12px', marginBottom: '8px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '6px', fontSize: '12px', fontWeight: '600', cursor: 'pointer' },
  content: { padding: '32px', overflowY: 'auto', height: 'calc(100vh - 80px)' },
  kpiContainer: { background: '#1e293b', borderRadius: '12px', padding: '28px', boxShadow: '0 4px 12px rgba(0,0,0,0.3)', marginBottom: '32px', border: '1px solid #475569' },
  kpiGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '16px', marginBottom: '16px' },
  kpiCard: (color) => ({ background: color, borderRadius: '12px', padding: '24px', textAlign: 'center', color: 'white', border: '1px solid rgba(255,255,255,0.1)' }),
  kpiLabel: { fontSize: '10px', fontWeight: '700', textTransform: 'uppercase', color: 'rgba(255,255,255,0.75)', marginBottom: '10px' },
  kpiValue: { fontSize: '28px', fontWeight: '700', color: '#FFFFFF', margin: 0 },
  chartsGrid: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '20px' },
  graphCard: { backgroundColor: '#1e293b', borderRadius: '12px', padding: '20px', minHeight: '400px', display: 'flex', flexDirection: 'column', border: '1px solid #475569' },
  graphCardSpan2: { gridColumn: 'span 2' },
  graphCardHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px' },
  graphCardTitle: { fontSize: '14px', fontWeight: '600', color: '#e2e8f0', margin: 0 },
