                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Line type="monotone" dataKey={chart.yKey} stroke={colors[0]} strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            );
            break;

          case 'bar':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
            break;

          case 'area':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <AreaChart data={chart.data}>
                  <defs>
                    <linearGradient id={`colorArea${chart.title}`} x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor={colors[0]} stopOpacity={0.8} />
                      <stop offset="95%" stopColor={colors[0]} stopOpacity={0} />
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Area 
                    type="monotone" 
                    dataKey={chart.yKey} 
                    stroke={colors[0]} 
                    fillOpacity={1} 
                    fill={`url(#colorArea${chart.title})`} 
                  />
                </AreaChart>
              </ResponsiveContainer>
            );
            break;

          case 'pie':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie 
                    data={chart.data} 
                    dataKey={chart.yKey}
                    nameKey={chart.xKey}
                    cx="50%" 
                    cy="50%" 
                    outerRadius={100}
                  >
                    {chart.data.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
                    ))}
                  </Pie>
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            );
            break;

          default:
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
        }

        return (
          <div key={chart.title} style={{...styles.graphCard, marginBottom: '1.5rem'}}>
            <h3 style={styles.graphCardTitle}>{chart.title}</h3>
            {chartComponent}
          </div>
        );
      } catch (e) {
        console.error('Chart render error:', e);
        return (
          <div key={chart.title} style={{...styles.resultCard, borderColor: '#ef4444', marginBottom: '1.5rem'}}>
            <h3 style={{...styles.resultQuestion, color: '#ef4444'}}>âŒ {chart.title}</h3>
            <p style={styles.resultAnswer}>Failed to render chart</p>
          </div>
        );
      }
    };

    return (
      <div style={styles.container}>
        <Navigation />
        <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '2rem 1.5rem' }}>


          {graphLoading && (
            <div style={{ padding: '2rem', textAlign: 'center', color: '#cbd5e1' }}>
