import React, { useState, useRef, useEffect } from 'react';
import { TrendingUp, Users, DollarSign, Activity, ChevronRight, Send, Loader, BarChart3, PieChart as PieChartIcon, LineChart as LineChartIcon, MessageSquare, Home as HomeIcon, Filter, Menu, X, AlertCircle, CheckCircle } from 'lucide-react';
import APIClient from './api';

export default function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [menuOpen, setMenuOpen] = useState(false);

  // Analytics page state
  const [selectedMetric, setSelectedMetric] = useState('revenue');
  const [filterMonth, setFilterMonth] = useState('all');

  // Text analysis state
  const [chatHistory, setChatHistory] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [isTextLoading, setIsTextLoading] = useState(false);
  const textChatEndRef = useRef(null);

  // Infographics state
  const [selectedChart, setSelectedChart] = useState('revenue');
  const [graphInput, setGraphInput] = useState('');
  const [isGraphLoading, setIsGraphLoading] = useState(false);
  const [generatedCharts, setGeneratedCharts] = useState([]);

  // Scroll to bottom for chats
  useEffect(() => {
    textChatEndRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatHistory]);

  // Metrics data
  const metricsData = [
    {
      id: 'revenue',
      title: 'Total Revenue',
      value: '$124,589',
      change: '+12.5%',
      icon: DollarSign,
      color: 'bg-emerald-50',
      accent: 'text-emerald-600',
    },
    {
      id: 'users',
      title: 'Active Users',
      value: '8,234',
      change: '+5.2%',
      icon: Users,
      color: 'bg-blue-50',
      accent: 'text-blue-600',
    },
    {
      id: 'engagement',
      title: 'Engagement Rate',
      value: '64.8%',
      change: '+2.1%',
      icon: Activity,
      color: 'bg-purple-50',
      accent: 'text-purple-600',
    },
    {
      id: 'growth',
      title: 'Month-over-Month',
      value: '23.4%',
      change: '+8.3%',
      icon: TrendingUp,
      color: 'bg-amber-50',
      accent: 'text-amber-600',
    },
  ];

  const chartData = [
    { month: 'Jan', value: 4000 },
    { month: 'Feb', value: 3000 },
    { month: 'Mar', value: 2000 },
    { month: 'Apr', value: 2780 },
    { month: 'May', value: 1890 },
    { month: 'Jun', value: 2390 },
  ];

  const recentActivity = [
    { id: 1, user: 'Sarah Anderson', action: 'Created new project', time: '2 hours ago' },
    { id: 2, user: 'Mike Chen', action: 'Updated dashboard', time: '5 hours ago' },
    { id: 3, user: 'Emma Wilson', action: 'Shared report', time: '1 day ago' },
    { id: 4, user: 'James Rodriguez', action: 'Completed task', time: '2 days ago' },
  ];

  // Learning concepts for the educational section
  const learningConcepts = [
    { concept: 'Components', desc: 'Reusable pieces of UI that encapsulate logic and rendering' },
    { concept: 'JSX', desc: 'HTML-like syntax in JavaScript for creating React elements' },
    { concept: 'State (useState)', desc: 'Data that changes and triggers re-renders when updated' },
    { concept: 'Props', desc: 'Parameters passed to components for configuration (notice "key" prop)' },
    { concept: 'Event Handling', desc: 'onClick, onChange, etc. for responding to user interactions' },
    { concept: '.map()', desc: 'Loop through arrays to dynamically create components for each item' },
    { concept: 'Conditional Rendering', desc: 'Show or hide elements based on conditions' },
    { concept: 'Styling', desc: 'Tailwind CSS utility classes for efficient, responsive styling' },
  ];

  // Text Analysis Handler
  const handleTextAnalysis = async () => {
    if (!textInput.trim()) return;

    const userMessage = textInput;
    setChatHistory(prev => [...prev, { sender: 'user', text: userMessage }]);
    setTextInput('');
    setIsTextLoading(true);

    try {
      const result = await APIClient.analyzeText(userMessage);
      if (result.success) {
        setChatHistory(prev => [...prev, { 
          sender: 'bot', 
          text: result.summary,
          questions: result.questions 
        }]);
      } else {
        setChatHistory(prev => [...prev, { 
          sender: 'bot', 
          text: `Error: ${result.error}`,
          error: true 
        }]);
      }
    } catch (error) {
      setChatHistory(prev => [...prev, { 
        sender: 'bot', 
        text: `Connection error: ${error.message}`,
        error: true 
      }]);
    }

    setIsTextLoading(false);
  };

  // Graph Generation Handler
  const handleGraphGeneration = async () => {
    if (!graphInput.trim()) return;

    const userMessage = graphInput;
    setGraphInput('');
    setIsGraphLoading(true);

    try {
      const result = await APIClient.generateGraphs(userMessage);
      if (result.success) {
        setGeneratedCharts(result.charts || []);
      } else {
        alert(`Error: ${result.error}`);
      }
    } catch (error) {
      alert(`Connection error: ${error.message}`);
    }

    setIsGraphLoading(false);
  };

  // ============================================================================
  // NAVIGATION HEADER
  // ============================================================================
  const NavigationHeader = () => (
    <div className="bg-gradient-to-br from-slate-950 via-slate-900 to-slate-800 border-b border-slate-700 sticky top-0 z-50 shadow-lg">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-400 to-cyan-500 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-lg">E</span>
          </div>
          <h2 className="text-2xl font-bold text-white">ExorVia</h2>
        </div>

        {/* Desktop Navigation */}
        <div className="hidden md:flex gap-2">
          {[
            { page: 'home', label: 'Home', icon: HomeIcon },
            { page: 'analytics', label: 'Analytics', icon: BarChart3 },
            { page: 'text', label: 'Text Analysis', icon: MessageSquare },
            { page: 'graphs', label: 'Infographics', icon: LineChartIcon },
          ].map((nav) => {
            const Icon = nav.icon;
            return (
              <button
                key={nav.page}
                onClick={() => { setCurrentPage(nav.page); setMenuOpen(false); }}
                className={`flex gap-2 items-center px-4 py-2 rounded-lg transition-all duration-300 ${
                  currentPage === nav.page
                    ? 'bg-blue-500 text-white shadow-lg'
                    : 'text-slate-400 hover:text-slate-200 hover:bg-slate-800'
                }`}
              >
                <Icon size={18} />
                <span>{nav.label}</span>
              </button>
            );
          })}
        </div>

        {/* Mobile Menu Button */}
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          className="md:hidden text-slate-400 hover:text-white"
        >
          {menuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Navigation */}
      {menuOpen && (
        <div className="md:hidden bg-slate-900 border-t border-slate-700 px-6 py-4 space-y-2">
          {[
            { page: 'home', label: 'Home', icon: HomeIcon },
            { page: 'analytics', label: 'Analytics', icon: BarChart3 },
            { page: 'text', label: 'Text Analysis', icon: MessageSquare },
            { page: 'graphs', label: 'Infographics', icon: LineChartIcon },
          ].map((nav) => {
            const Icon = nav.icon;
            return (
              <button
                key={nav.page}
                onClick={() => { setCurrentPage(nav.page); setMenuOpen(false); }}
                className={`w-full flex gap-2 items-center px-4 py-2 rounded-lg transition-all duration-300 ${
                  currentPage === nav.page
                    ? 'bg-blue-500 text-white'
                    : 'text-slate-400 hover:text-slate-200 hover:bg-slate-800'
                }`}
              >
                <Icon size={18} />
                <span>{nav.label}</span>
              </button>
            );
          })}
        </div>
      )}
    </div>
  );

  // ============================================================================
  // HOME PAGE
  // ============================================================================
  const HomePage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-800 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-12">
          <h1 className="text-4xl font-bold text-white mb-2">Dashboard</h1>
          <p className="text-slate-400">Welcome back! Here's your performance overview.</p>
        </div>

        {/* Metrics Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {metricsData.map((metric) => {
            const IconComponent = metric.icon;
            const isSelected = selectedMetric === metric.id;

            return (
              <div
                key={metric.id}
                onClick={() => setSelectedMetric(metric.id)}
                className={`p-6 rounded-xl cursor-pointer transition-all duration-300 ${
                  isSelected
                    ? 'bg-white shadow-xl scale-105'
                    : 'bg-slate-800 hover:bg-slate-700 shadow-lg'
                }`}
              >
                <div className="flex items-center justify-between mb-4">
                  <div
                    className={`p-3 rounded-lg ${metric.color} ${isSelected ? metric.accent : 'text-slate-500'}`}
                  >
                    <IconComponent size={24} />
                  </div>
                  <span className="text-emerald-400 text-sm font-semibold">
                    {metric.change}
                  </span>
                </div>
                <h3 className={`text-sm font-medium mb-2 ${isSelected ? 'text-slate-900' : 'text-slate-400'}`}>
                  {metric.title}
                </h3>
                <p className={`text-2xl font-bold ${isSelected ? 'text-slate-900' : 'text-white'}`}>
                  {metric.value}
                </p>
              </div>
            );
          })}
        </div>

        {/* Main Content Area */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
          {/* Chart Section */}
          <div className="lg:col-span-2 bg-slate-800 rounded-xl p-6 shadow-lg">
            <div className="mb-6">
              <h2 className="text-xl font-bold text-white mb-2">Revenue Trend</h2>
              <p className="text-slate-400 text-sm">Last 6 months</p>
            </div>

            <div className="flex items-end gap-3 h-48">
              {chartData.map((item, index) => {
                const maxValue = Math.max(...chartData.map((d) => d.value));
                const percentage = (item.value / maxValue) * 100;

                return (
                  <div key={index} className="flex-1 flex flex-col items-center gap-2">
                    <div
                      className="w-full bg-gradient-to-t from-blue-500 to-blue-400 rounded-t transition-all hover:opacity-80"
                      style={{ height: `${percentage}%` }}
                    />
                    <span className="text-xs text-slate-400">{item.month}</span>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Recent Activity Section */}
          <div className="bg-slate-800 rounded-xl p-6 shadow-lg">
            <h2 className="text-xl font-bold text-white mb-4">Recent Activity</h2>

            <div className="space-y-4">
              {recentActivity.map((activity) => (
                <div
                  key={activity.id}
                  className="flex items-start gap-3 p-3 rounded-lg hover:bg-slate-700 transition-colors cursor-pointer group"
                >
                  <div className="w-10 h-10 rounded-full bg-slate-700 flex items-center justify-center flex-shrink-0">
                    <span className="text-white font-semibold text-sm">
                      {activity.user.charAt(0)}
                    </span>
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-white text-sm font-medium truncate">
                      {activity.user}
                    </p>
                    <p className="text-slate-400 text-xs truncate">
                      {activity.action}
                    </p>
                    <p className="text-slate-500 text-xs mt-1">{activity.time}</p>
                  </div>
                  <ChevronRight
                    size={16}
                    className="text-slate-600 group-hover:text-slate-400 flex-shrink-0 mt-1"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // ============================================================================
  // ANALYTICS PAGE
  // ============================================================================
  const AnalyticsPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-800 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-12">
          <h1 className="text-4xl font-bold text-white mb-2">Analytics Dashboard</h1>
          <p className="text-slate-400">Real-time performance metrics and insights</p>
        </div>

        {/* Metrics Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {metricsData.map((metric) => {
            const IconComponent = metric.icon;
            const isSelected = selectedMetric === metric.id;
            return (
              <div
                key={metric.id}
                onClick={() => setSelectedMetric(metric.id)}
                className={`p-6 rounded-xl cursor-pointer transition-all duration-300 ${
                  isSelected
                    ? 'bg-white shadow-xl scale-105'
                    : 'bg-slate-800 hover:bg-slate-700 shadow-lg'
                }`}
              >
                <div className="flex items-center justify-between mb-4">
                  <div
                    className={`p-3 rounded-lg ${metric.color} ${isSelected ? metric.accent : 'text-slate-500'}`}
                  >
                    <IconComponent size={24} />
                  </div>
                  <span className="text-emerald-400 text-sm font-semibold">{metric.change}</span>
                </div>
                <h3 className={`text-sm font-medium mb-2 ${isSelected ? 'text-slate-900' : 'text-slate-400'}`}>
                  {metric.title}
                </h3>
                <p className={`text-2xl font-bold ${isSelected ? 'text-slate-900' : 'text-white'}`}>
                  {metric.value}
                </p>
              </div>
            );
          })}
        </div>

        {/* Main Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
          {/* Chart */}
          <div className="lg:col-span-2 bg-slate-800 rounded-xl p-6 shadow-lg">
            <div className="mb-6">
              <h2 className="text-xl font-bold text-white mb-2">Revenue Trend</h2>
              <p className="text-slate-400 text-sm">Last 6 months</p>
            </div>
            <div className="flex items-end gap-3 h-48">
              {chartData.map((item, index) => {
                const maxValue = Math.max(...chartData.map((d) => d.value));
                const percentage = (item.value / maxValue) * 100;
                return (
                  <div key={index} className="flex-1 flex flex-col items-center gap-2">
                    <div
                      className="w-full bg-gradient-to-t from-blue-500 to-blue-400 rounded-t transition-all hover:opacity-80"
                      style={{ height: `${percentage}%` }}
                    />
                    <span className="text-xs text-slate-400">{item.month}</span>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Recent Activity */}
          <div className="bg-slate-800 rounded-xl p-6 shadow-lg">
            <h2 className="text-xl font-bold text-white mb-4">Recent Activity</h2>
            <div className="space-y-4">
              {recentActivity.map((activity) => (
                <div
                  key={activity.id}
                  className="flex items-start gap-3 p-3 rounded-lg hover:bg-slate-700 transition-colors cursor-pointer group"
                >
                  <div className="w-10 h-10 rounded-full bg-slate-700 flex items-center justify-center flex-shrink-0">
                    <span className="text-white font-semibold text-sm">{activity.user.charAt(0)}</span>
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-white text-sm font-medium truncate">{activity.user}</p>
                    <p className="text-slate-400 text-xs truncate">{activity.action}</p>
                    <p className="text-slate-500 text-xs mt-1">{activity.time}</p>
                  </div>
                  <ChevronRight size={16} className="text-slate-600 group-hover:text-slate-400 flex-shrink-0 mt-1" />
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Filter Section */}
        <div className="bg-slate-800 rounded-xl p-6 shadow-lg mb-8">
          <h2 className="text-xl font-bold text-white mb-4">Filter Data</h2>
          <div className="flex gap-2 flex-wrap">
            {['all', 'january', 'february', 'march'].map((month) => (
              <button
                key={month}
                onClick={() => setFilterMonth(month)}
                className={`px-4 py-2 rounded-lg font-medium transition-all ${
                  filterMonth === month
                    ? 'bg-blue-500 text-white shadow-lg'
                    : 'bg-slate-700 text-slate-300 hover:bg-slate-600'
                }`}
              >
                {month.charAt(0).toUpperCase() + month.slice(1)}
              </button>
            ))}
          </div>
          <p className="text-slate-400 text-sm mt-4">
            Currently filtering by: <span className="text-white font-semibold">{filterMonth}</span>
          </p>
        </div>

        {/* Learning Section */}
        <LearningSection concepts={learningConcepts} />
      </div>
    </div>
  );

  // ============================================================================
  // TEXT ANALYSIS PAGE
  // ============================================================================
  const TextAnalysisPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-800 p-8">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">Text Analysis</h1>
          <p className="text-slate-400">Ask questions about your data and get AI-powered insights</p>
        </div>

        {/* Chat Container */}
        <div className="bg-slate-800 rounded-xl shadow-lg overflow-hidden flex flex-col h-[600px] mb-8">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-6 space-y-4">
            {chatHistory.length === 0 ? (
              <div className="flex items-center justify-center h-full">
                <div className="text-center">
                  <MessageSquare size={48} className="text-slate-600 mx-auto mb-4" />
                  <p className="text-slate-300 text-lg">Start the conversation...</p>
                  <p className="text-slate-400 text-sm mt-2">Ask about your data and get AI insights</p>
                </div>
              </div>
            ) : (
              <>
                {chatHistory.map((msg, idx) => (
                  <div key={idx} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                    <div
                      className={`max-w-xs px-4 py-3 rounded-lg ${
                        msg.sender === 'user'
                          ? 'bg-blue-600 text-white rounded-br-none shadow-lg'
                          : msg.error
                          ? 'bg-red-900 text-red-100 rounded-bl-none'
                          : 'bg-slate-700 text-slate-100 rounded-bl-none'
                      }`}
                    >
                      <p className="text-sm leading-relaxed">{msg.text}</p>
                      {msg.questions && msg.questions.length > 0 && (
                        <div className="mt-3 pt-3 border-t border-opacity-30 border-white">
                          <p className="text-xs font-semibold mb-2 opacity-90">Generated Questions:</p>
                          <ul className="text-xs space-y-1 opacity-90">
                            {msg.questions.slice(0, 3).map((q, i) => (
                              <li key={i}> {q.substring(0, 50)}...</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
                {isTextLoading && (
                  <div className="flex justify-start">
                    <div className="bg-slate-700 px-4 py-3 rounded-lg rounded-bl-none flex gap-2 items-center">
                      <Loader size={18} className="animate-spin text-blue-400" />
                      <p className="text-sm text-slate-100">Analyzing your data...</p>
                    </div>
                  </div>
                )}
                <div ref={textChatEndRef} />
              </>
            )}
          </div>

          {/* Input */}
          <div className="border-t border-slate-700 p-4 bg-slate-900">
            <div className="flex gap-3">
              <input
                type="text"
                value={textInput}
                onChange={(e) => setTextInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleTextAnalysis()}
                placeholder="Ask a question about your data..."
                className="flex-1 px-4 py-3 bg-slate-700 text-white placeholder-slate-400 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                disabled={isTextLoading}
              />
              <button
                onClick={handleTextAnalysis}
                disabled={isTextLoading || !textInput.trim()}
                className="px-6 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-slate-700 text-white rounded-lg font-medium transition-all flex gap-2 items-center shadow-lg disabled:shadow-none"
              >
                <Send size={18} />
              </button>
            </div>
          </div>
        </div>

        {/* Learning Section */}
        <LearningSection concepts={learningConcepts} />
      </div>
    </div>
  );

  // ============================================================================
  // INFOGRAPHICS PAGE
  // ============================================================================
  const InfographicsPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-800 p-8">
      <div className="max-w-6xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">Infographic Generator</h1>
          <p className="text-slate-400">Create custom visualizations from your data</p>
        </div>

        {/* Input Section */}
        <div className="bg-slate-800 rounded-xl p-6 shadow-lg mb-8">
          <div className="flex gap-3">
            <input
              type="text"
              value={graphInput}
              onChange={(e) => setGraphInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleGraphGeneration()}
              placeholder="Describe the visualization you want to create..."
              className="flex-1 px-4 py-3 bg-slate-700 text-white placeholder-slate-400 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              disabled={isGraphLoading}
            />
            <button
              onClick={handleGraphGeneration}
              disabled={isGraphLoading || !graphInput.trim()}
              className="px-6 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-slate-700 text-white rounded-lg font-medium transition-all flex gap-2 items-center shadow-lg disabled:shadow-none"
            >
              <Send size={18} />
            </button>
          </div>
        </div>

        {/* Loading State */}
        {isGraphLoading && (
          <div className="flex items-center justify-center py-12">
            <div className="bg-slate-800 px-6 py-4 rounded-lg flex gap-3 items-center">
              <Loader size={24} className="animate-spin text-blue-400" />
              <p className="text-white text-lg font-medium">Generating visualizations...</p>
            </div>
          </div>
        )}

        {/* Charts Grid */}
        {generatedCharts.length > 0 && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            {generatedCharts.map((chart, idx) => (
              <div key={idx} className="bg-slate-800 rounded-xl p-6 shadow-lg">
                <h3 className="text-lg font-bold text-white mb-4">{chart.title}</h3>

                {chart.chart_type === 'line' || chart.chart_type === 'bar' ? (
                  <div className="flex items-end gap-3 h-64">
                    {chart.data && chart.data.slice(0, 6).map((item, i) => {
                      const values = chart.data.map(d => d[chart.yKey] || d.value || 0);
                      const maxValue = Math.max(...values);
                      const percentage = (values[i] / maxValue) * 100;
                      return (
                        <div key={i} className="flex-1 flex flex-col items-center gap-2">
                          <div
                            className="w-full bg-gradient-to-t from-blue-500 to-blue-400 rounded-t hover:opacity-80 transition-opacity"
                            style={{ height: `${percentage}%` }}
                          />
                          <span className="text-xs text-slate-400">{item[chart.xKey] || 'N/A'}</span>
                        </div>
                      );
                    })}
                  </div>
                ) : chart.chart_type === 'pie' ? (
                  <div className="flex items-center justify-around h-64 flex-wrap">
                    {chart.data && chart.data.map((item, i) => (
                      <div key={i} className="text-center">
                        <div
                          className="w-24 h-24 rounded-full flex items-center justify-center text-white font-bold mb-3 shadow-lg"
                          style={{
                            background: `linear-gradient(135deg, hsl(${200 + i * 30}, 70%, 50%) 0%, hsl(${200 + i * 30}, 70%, 40%) 100%)`
                          }}
                        >
                          {Math.round((item[chart.yKey] || item.value || 0))}%
                        </div>
                        <p className="text-sm text-slate-300">{item[chart.xKey] || 'N/A'}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <p className="text-slate-400">Chart type: {chart.chart_type}</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {/* Empty State */}
        {!isGraphLoading && generatedCharts.length === 0 && (
          <div className="text-center py-12 mb-8">
            <BarChart3 size={48} className="text-slate-600 mx-auto mb-4" />
            <p className="text-slate-300 text-lg">No visualizations yet</p>
            <p className="text-slate-400 text-sm mt-2">Describe what you want to visualize and click Send</p>
          </div>
        )}

        {/* Learning Section */}
        <LearningSection concepts={learningConcepts} />
      </div>
    </div>
  );

  // ============================================================================
  // LEARNING SECTION COMPONENT
  // ============================================================================
  const LearningSection = ({ concepts }) => (
    <div className="bg-slate-700 border border-slate-600 rounded-xl p-6 shadow-lg">
      <h3 className="text-white font-bold mb-4 text-lg flex items-center gap-2">
        <span className="text-xl"></span> Key Concepts to Learn:
      </h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {concepts.map((item, idx) => (
          <div key={idx} className="flex items-start gap-3 p-3 rounded-lg bg-slate-800 hover:bg-slate-600 transition-colors">
            <CheckCircle size={20} className="text-emerald-400 flex-shrink-0 mt-0.5" />
            <div className="flex-1">
              <p className="text-white font-semibold text-sm">{item.concept}</p>
              <p className="text-slate-300 text-xs mt-1">{item.desc}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  // ============================================================================
  // MAIN RENDER
  // ============================================================================
  return (
    <div className="min-h-screen bg-slate-950">
      <NavigationHeader />
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'analytics' && <AnalyticsPage />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <InfographicsPage />}
    </div>
  );
}
