import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Menu, X } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

class APIClient {
  static async fetchDashboardData() {
    try {
      console.log('ðŸŒ Fetching from:', `${API_BASE_URL}/data`);
      const response = await fetch(`${API_BASE_URL}/data`);
      console.log('ðŸ“¡ Response status:', response.status, response.statusText);

      if (!response.ok) {
        throw new Error(`API error: ${response.status} ${response.statusText}`);
      }

      const rawData = await response.json();
      console.log('ðŸ“¦ Response received');

      if (!Array.isArray(rawData)) {
        console.error('âŒ Response is not an array:', typeof rawData);
        return [];
      }

      console.log(`âœ… Successfully loaded ${rawData.length} records`);
      return rawData;
    } catch (error) {
      console.error('âŒ Dashboard data fetch error:', error.message);
      return [];
    }
  }

  static async analyzeText(userMessage) {
    try {
      const response = await fetch(`${API_BASE_URL}/text/analyze`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_message: userMessage }),
      });
      if (!response.ok) throw new Error(`API error: ${response.status}`);
      const data = await response.json();

      if (data.success) {
        return { success: true, summary: data.summary, questions: data.questions || [], results: data.results || [] };
      } else {
        return { success: false, error: data.error || 'Unknown error' };
      }
    } catch (error) {
      return { success: false, error: error.message || 'Failed to connect to API' };
    }
  }

  static async generateGraphs(userMessage) {
    try {
      const response = await fetch(`${API_BASE_URL}/graphs/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_message: userMessage }),
      });
      if (!response.ok) throw new Error(`API error: ${response.status}`);
      const data = await response.json();

      if (data.success) {
        return { success: true, charts: data.charts || [], questions: data.questions || '' };
      } else {
        return { success: false, error: data.error || 'Unknown error' };
      }
    } catch (error) {
      return { success: false, error: error.message || 'Failed to connect to API' };
    }
  }

  static async healthCheck() {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      return response.ok;
    } catch (error) {
      return false;
    }
  }
}

const COLUMNS = {
  orderId: 'Order ID',
  purchaseTimestamp: 'Purchase Timestamp',
  reviewScore: 'Review Score',
  deliveredOnTime: 'Delivered on Time',
  paymentType: 'Payment Type',
  paymentValue: 'Payment Value',
  shippingCost: 'Shipping Cost',
  productCategory: 'Product Category',
  customerState: 'Customer State',
  customerId: 'Customer ID',
  orderStatus: 'Order Status',
  deliveryDurationDays: 'Delivery Duration (days)'
};

const styles = {
  container: { minHeight: '100vh', background: 'linear-gradient(to bottom right, #0f172a, #1e293b)' },
  header: { background: 'rgba(15, 23, 42, 0.8)', backdropFilter: 'blur(10px)', borderBottom: '1px solid #475569', position: 'sticky', top: 0, zIndex: 50 },
  headerContent: { maxWidth: '100%', margin: '0 auto', padding: '1.5rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  logo: { fontSize: '1.875rem', fontWeight: 'bold', background: 'linear-gradient(to right, #60a5fa, #22d3ee)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', margin: 0 },
  nav: { display: 'flex', gap: '1rem' },
  navButton: (isActive) => ({ padding: '0.5rem 1rem', borderRadius: '0.5rem', fontWeight: '500', border: 'none', cursor: 'pointer', backgroundColor: isActive ? '#2563eb' : 'transparent', color: isActive ? 'white' : '#cbd5e1' }),
  mainContainer: { display: 'grid', gridTemplateColumns: '280px 1fr' },
  mainContainerCollapsed: { display: 'grid', gridTemplateColumns: '1fr' },
  sidebar: { background: '#1e293b', borderRight: '1px solid #475569', padding: '24px', overflowY: 'auto', position: 'sticky', top: '80px', height: 'calc(100vh - 80px)' },
  sidebarSection: { marginBottom: '20px', paddingBottom: '16px', borderBottom: '1px solid #475569' },
  sidebarLabel: { fontWeight: '600', color: '#e2e8f0', marginBottom: '8px', display: 'block', fontSize: '13px' },
  sidebarSelect: { width: '100%', padding: '8px 12px', border: '1px solid #475569', borderRadius: '8px', fontSize: '13px', fontFamily: 'inherit', backgroundColor: '#0f172a', color: '#e2e8f0', cursor: 'pointer' },
  content: { padding: '32px', overflowY: 'auto', height: 'calc(100vh - 80px)' },
  kpiContainer: { background: '#1e293b', borderRadius: '12px', padding: '28px', boxShadow: '0 4px 12px rgba(0,0,0,0.3)', marginBottom: '32px', border: '1px solid #475569' },
  kpiGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '16px', marginBottom: '16px' },
  kpiCard: (color) => ({ background: color, borderRadius: '12px', padding: '24px', textAlign: 'center', color: 'white', border: '1px solid rgba(255,255,255,0.1)' }),
  kpiLabel: { fontSize: '10px', fontWeight: '700', textTransform: 'uppercase', color: 'rgba(255,255,255,0.75)', marginBottom: '10px' },
  kpiValue: { fontSize: '28px', fontWeight: '700', color: '#FFFFFF', margin: 0 },
  chartsGrid: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '20px' },
  graphCard: { backgroundColor: '#1e293b', borderRadius: '12px', padding: '20px', minHeight: '400px', display: 'flex', flexDirection: 'column', border: '1px solid #475569' },
  graphCardSpan2: { gridColumn: 'span 2' },
  tooltipStyle: { backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '8px', color: '#e2e8f0' },
  heroSection: { maxWidth: '80rem', margin: '0 auto', padding: '5rem 1.5rem', textAlign: 'center' },
  heroTitle: { fontSize: '3rem', fontWeight: 'bold', color: 'white', marginBottom: '1rem' },
  heroSubtitle: { fontSize: '1.25rem', color: '#cbd5e1', marginBottom: '3rem' },
  featureGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem' },
  featureCard: { backgroundColor: '#1e293b', borderRadius: '1rem', padding: '2rem', border: '1px solid #475569', cursor: 'pointer' },
  featureIcon: { fontSize: '3.75rem', marginBottom: '1rem', display: 'block' },
  featureTitle: { fontSize: '1.5rem', fontWeight: 'bold', color: 'white', marginBottom: '0.75rem' },
  featureButton: { padding: '0.75rem 1.5rem', background: 'linear-gradient(to right, #2563eb, #1d4ed8)', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600' },
  searchSection: { backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '2rem' },
  searchContainer: { display: 'flex', gap: '0.5rem' },
  searchInput: { flex: 1, padding: '0.75rem', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '0.5rem', color: 'white', fontFamily: 'inherit' },
  searchButton: { padding: '0.75rem 1.5rem', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600' },
  resultCard: { backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '2rem', marginBottom: '1.5rem' },
  resultQuestion: { fontSize: '1.125rem', fontWeight: 'bold', color: 'white', margin: '0 0 1rem 0' },
  resultAnswer: { color: '#cbd5e1', lineHeight: '1.6', marginLeft: '2rem' },
};

export default function EcommerceDashboard() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMetric, setSelectedMetric] = useState('revenue');

  const [selectedStates, setSelectedStates] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedPaymentTypes, setSelectedPaymentTypes] = useState([]);
  const [selectedStatuses, setSelectedStatuses] = useState([]);
  const [deliveryOnTime, setDeliveryOnTime] = useState('all');
  const [reviewRange, setReviewRange] = useState([1, 5]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await APIClient.fetchDashboardData();
        if (!Array.isArray(fetchedData) || fetchedData.length === 0) {
          setError('No data returned from API');
          setData([]);
        } else {
          setData(fetchedData);
          setError(null);
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch data');
        setData([]);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const filteredData = useMemo(() => {
    if (!Array.isArray(data) || data.length === 0) return [];

    let filtered = [...data];
    if (selectedStates.length > 0) filtered = filtered.filter(d => selectedStates.includes(d?.[COLUMNS.customerState]));
    if (selectedCategories.length > 0) filtered = filtered.filter(d => selectedCategories.includes(d?.[COLUMNS.productCategory]));
    if (selectedPaymentTypes.length > 0) filtered = filtered.filter(d => selectedPaymentTypes.includes(d?.[COLUMNS.paymentType]));
    if (selectedStatuses.length > 0) filtered = filtered.filter(d => selectedStatuses.includes(d?.[COLUMNS.orderStatus]));
    if (deliveryOnTime !== 'all') filtered = filtered.filter(d => d?.[COLUMNS.deliveredOnTime] === deliveryOnTime);
    filtered = filtered.filter(d => {
      const score = parseFloat(d?.[COLUMNS.reviewScore]) || 0;
      return score >= reviewRange[0] && score <= reviewRange[1];
    });
    return filtered;
  }, [data, selectedStates, selectedCategories, selectedPaymentTypes, selectedStatuses, deliveryOnTime, reviewRange]);

  const kpis = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) {
      return { totalOrders: 0, avgReview: 0, onTimeRate: 0, totalRevenue: 0, avgDeliveryDays: 0, avgOrderValue: 0 };
    }
    const totalOrders = filteredData.length;
    const avgReview = (filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.reviewScore]) || 0), 0) / totalOrders).toFixed(2);
    const onTimeRate = ((filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'Yes').length / totalOrders) * 100).toFixed(1);
    const totalRevenue = filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.paymentValue]) || 0), 0);
    const avgDeliveryDays = (filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.deliveryDurationDays]) || 0), 0) / totalOrders).toFixed(1);
    const avgOrderValue = (totalRevenue / totalOrders).toFixed(2);
    return { totalOrders, avgReview, onTimeRate, totalRevenue, avgDeliveryDays, avgOrderValue };
  }, [filteredData]);

  const formatCurrency = (value) => {
    const num = typeof value === 'number' ? value : parseFloat(value) || 0;
    if (num >= 1000000) return `$${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `$${(num / 1000).toFixed(1)}K`;
    return `$${num.toFixed(2)}`;
  };

  // Chart data with proper sorting
  const monthlyData = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) return [];

    const months = {};
    filteredData.forEach(d => {
      try {
        const timestamp = d?.[COLUMNS.purchaseTimestamp];
        if (!timestamp) return;
        const date = new Date(timestamp);
        const monthKey = date.toLocaleString('default', { month: 'short', year: '2-digit' });
        const monthNum = date.getMonth();
        if (!months[monthKey]) months[monthKey] = { month: monthKey, revenue: 0, shipping: 0, sortKey: monthNum };
        months[monthKey].revenue += parseFloat(d?.[COLUMNS.paymentValue]) || 0;
        months[monthKey].shipping += parseFloat(d?.[COLUMNS.shippingCost]) || 0;
      } catch (e) {}
    });
    return Object.values(months).sort((a, b) => a.sortKey - b.sortKey).slice(-12);
  }, [filteredData]);

  const categoryData = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) return [];
    const cats = {};
    filteredData.forEach(d => {
      const cat = d?.[COLUMNS.productCategory] || 'Unknown';
      if (!cats[cat]) cats[cat] = 0;
      cats[cat] += parseFloat(d?.[COLUMNS.paymentValue]) || 0;
    });
    return Object.entries(cats).map(([name, value]) => ({ name, value })).sort((a, b) => a.value - b.value);
  }, [filteredData]);

  const onTimeData = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) return [];
    const onTime = filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'Yes').length;
    const late = filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'No').length;
    return [
      { name: 'On Time', value: onTime, fill: '#10b981' },
      { name: 'Late', value: late, fill: '#6b7280' }
    ];
  }, [filteredData]);

  const paymentData = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) return [];
    const payments = {};
    filteredData.forEach(d => {
      const type = d?.[COLUMNS.paymentType] || 'Unknown';
      if (!payments[type]) payments[type] = 0;
      payments[type] += 1;
    });
    const colors = ['#ec4899', '#f472b6', '#f9a8d4', '#fbcfe8'];
    return Object.entries(payments).map(([name, value], idx) => ({ name, value, fill: colors[idx % colors.length] }));
  }, [filteredData]);

  const reviewData = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) return [];
    const scores = {};
    filteredData.forEach(d => {
      const score = parseFloat(d?.[COLUMNS.reviewScore]) || 0;
      if (!scores[score]) scores[score] = 0;
      scores[score] += 1;
    });
    return Object.entries(scores).map(([score, count]) => ({ score: parseFloat(score), count })).sort((a, b) => a.score - b.score);
  }, [filteredData]);

  const getUniqueValues = (columnKey) => {
    if (!Array.isArray(data)) return [];
    return [...new Set(data.map(d => d?.[columnKey]).filter(Boolean))].sort();
  };

  const stateOptions = useMemo(() => getUniqueValues(COLUMNS.customerState), [data]);
  const categoryOptions = useMemo(() => getUniqueValues(COLUMNS.productCategory), [data]);
  const paymentOptions = useMemo(() => getUniqueValues(COLUMNS.paymentType), [data]);
  const statusOptions = useMemo(() => getUniqueValues(COLUMNS.orderStatus), [data]);

  if (loading) {
    return <div style={{...styles.container, display: 'flex', alignItems: 'center', justifyContent: 'center'}}><div style={{color: 'white', fontSize: '18px'}}>â³ Loading data...</div></div>;
  }

  if (error) {
    return <div style={{...styles.container, display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px'}}><div style={{color: '#ef4444', fontSize: '18px'}}>âŒ {error}</div></div>;
  }

  const Navigation = () => (
    <div style={styles.header}>
      <div style={styles.headerContent}>
        <div>
          <h1 style={styles.logo}>E-Commerce Analytics</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {currentPage === 'dashboard' && (
            <button onClick={() => setSidebarOpen(!sidebarOpen)} style={{ background: 'none', border: 'none', color: '#cbd5e1', cursor: 'pointer', fontSize: '20px' }}>
              {sidebarOpen ? <X size={20} /> : <Menu size={20} />}
            </button>
          )}
          <nav style={styles.nav}>
            {['home', 'dashboard', 'text', 'graphs'].map(page => (
              <button key={page} onClick={() => { setCurrentPage(page); setSidebarOpen(false); }} style={styles.navButton(currentPage === page)}>
                {page.charAt(0).toUpperCase() + page.slice(1)}
              </button>
            ))}
          </nav>
        </div>
      </div>
    </div>
  );

  const HomePage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={styles.heroSection}>
        <h2 style={styles.heroTitle}>E-Commerce Analytics</h2>
        <p style={styles.heroSubtitle}>Track your sales, customers, and deliveries in real-time</p>
        <div style={styles.featureGrid}>
          {[
            { page: 'dashboard', icon: 'ðŸ“Š', title: 'Dashboard', desc: 'Monitor KPIs and analytics' },
            { page: 'text', icon: 'âœï¸', title: 'Text Analysis', desc: 'Ask questions about your data' },
            { page: 'graphs', icon: 'ðŸ“ˆ', title: 'Reports', desc: 'Generate custom visualizations' }
          ].map(card => (
            <div key={card.page} style={styles.featureCard} onClick={() => setCurrentPage(card.page)}>
              <span style={styles.featureIcon}>{card.icon}</span>
              <h3 style={styles.featureTitle}>{card.title}</h3>
              <p style={{color: '#cbd5e1', marginBottom: '1.5rem'}}>{card.desc}</p>
              <button style={styles.featureButton}>Explore â†’</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const DashboardPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={sidebarOpen ? styles.mainContainer : styles.mainContainerCollapsed}>
        {sidebarOpen && (
          <div style={styles.sidebar}>
            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Customer State</label>
              <select multiple style={{...styles.sidebarSelect, height: '80px'}} value={selectedStates} onChange={(e) => setSelectedStates(Array.from(e.target.selectedOptions, opt => opt.value))}>
                {stateOptions.map(s => <option key={s} value={s}>{s}</option>)}
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Product Category</label>
              <select multiple style={{...styles.sidebarSelect, height: '80px'}} value={selectedCategories} onChange={(e) => setSelectedCategories(Array.from(e.target.selectedOptions, opt => opt.value))}>
                {categoryOptions.map(c => <option key={c} value={c}>{c}</option>)}
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Payment Type</label>
              <select multiple style={{...styles.sidebarSelect, height: '80px'}} value={selectedPaymentTypes} onChange={(e) => setSelectedPaymentTypes(Array.from(e.target.selectedOptions, opt => opt.value))}>
                {paymentOptions.map(p => <option key={p} value={p}>{p}</option>)}
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Order Status</label>
              <select multiple style={{...styles.sidebarSelect, height: '80px'}} value={selectedStatuses} onChange={(e) => setSelectedStatuses(Array.from(e.target.selectedOptions, opt => opt.value))}>
                {statusOptions.map(s => <option key={s} value={s}>{s}</option>)}
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Delivered on Time</label>
              <select style={styles.sidebarSelect} value={deliveryOnTime} onChange={(e) => setDeliveryOnTime(e.target.value)}>
                <option value="all">All</option>
                <option value="Yes">Yes</option>
                <option value="No">No</option>
              </select>
            </div>

            <div style={styles.sidebarSection}>
              <label style={styles.sidebarLabel}>Review Score Range</label>
              <div style={{display: 'flex', gap: '8px', alignItems: 'center'}}>
                <input type="range" min="1" max="5" step="0.5" value={reviewRange[0]} onChange={(e) => setReviewRange([parseFloat(e.target.value), reviewRange[1]])} style={{flex: 1}} />
                <span style={{fontSize: '12px', color: '#e2e8f0', minWidth: '25px'}}>{reviewRange[0]}</span>
              </div>
              <div style={{display: 'flex', gap: '8px', alignItems: 'center', marginTop: '8px'}}>
                <input type="range" min="1" max="5" step="0.5" value={reviewRange[1]} onChange={(e) => setReviewRange([reviewRange[0], parseFloat(e.target.value)])} style={{flex: 1}} />
                <span style={{fontSize: '12px', color: '#e2e8f0', minWidth: '25px'}}>{reviewRange[1]}</span>
              </div>
            </div>

            <button style={{width: '100%', padding: '10px', background: '#1e293b', color: '#ef4444', border: '1px solid #ef4444', borderRadius: '8px', cursor: 'pointer', fontWeight: '600'}} onClick={() => {
              setSelectedStates([]); setSelectedCategories([]); setSelectedPaymentTypes([]); setSelectedStatuses([]); setDeliveryOnTime('all'); setReviewRange([1, 5]);
            }}>Reset Filters</button>
          </div>
        )}

        <div style={styles.content}>
          <div style={styles.kpiContainer}>
            <div style={styles.kpiGrid}>
              {[
                { label: 'Total Revenue', value: formatCurrency(kpis.totalRevenue), color: 'linear-gradient(135deg, #3b82f6, #06b6d4)' },
                { label: 'Avg Review', value: kpis.avgReview, color: 'linear-gradient(135deg, #a855f7, #ec4899)' },
                { label: 'Total Orders', value: kpis.totalOrders.toLocaleString(), color: 'linear-gradient(135deg, #10b981, #059669)' },
              ].map((kpi, idx) => (
                <div key={idx} style={styles.kpiCard(kpi.color)}>
                  <div style={styles.kpiLabel}>{kpi.label}</div>
                  <h2 style={styles.kpiValue}>{kpi.value}</h2>
                </div>
              ))}
            </div>
            <div style={styles.kpiGrid}>
              {[
                { label: 'On-Time Rate', value: `${kpis.onTimeRate}%` },
                { label: 'Avg Days', value: kpis.avgDeliveryDays },
                { label: 'Avg Order Value', value: formatCurrency(kpis.avgOrderValue) },
              ].map((kpi, idx) => (
                <div key={idx} style={styles.kpiCard('linear-gradient(135deg, #64748b, #475569)')}>
                  <div style={styles.kpiLabel}>{kpi.label}</div>
                  <h2 style={styles.kpiValue}>{kpi.value}</h2>
                </div>
              ))}
            </div>
          </div>

          <div style={styles.chartsGrid}>
            <div style={{...styles.graphCard, ...styles.graphCardSpan2}}>
              <ResponsiveContainer width="100%" height={300}>
                <AreaChart data={monthlyData}>
                  <defs>
                    <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8}/>
                      <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="month" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip contentStyle={styles.tooltipStyle} />
                  <Area type="monotone" dataKey="revenue" stroke="#3b82f6" fillOpacity={1} fill="url(#colorRevenue)" />
                </AreaChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={onTimeData} cx="50%" cy="50%" innerRadius={60} outerRadius={100} dataKey="value">
                    {onTimeData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                  </Pie>
                  <Tooltip contentStyle={styles.tooltipStyle} formatter={(value, name, props) => [value, props.payload.name]} />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div style={{...styles.graphCard, ...styles.graphCardSpan2}}>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={categoryData} layout="vertical">
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis type="number" stroke="#94a3b8" />
                  <YAxis dataKey="name" type="category" stroke="#94a3b8" width={100} />
                  <Tooltip contentStyle={styles.tooltipStyle} />
                  <Bar dataKey="value" fill="#3b82f6" radius={[0, 8, 8, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={paymentData} cx="50%" cy="50%" dataKey="value">
                    {paymentData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                  </Pie>
                  <Tooltip contentStyle={{...styles.tooltipStyle, color: '#ec4899'}} formatter={(value, name, props) => [value, props.payload.name]} />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={reviewData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="score" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip contentStyle={styles.tooltipStyle} />
                  <Bar dataKey="count" fill="#f97316" radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div style={styles.graphCard}>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={monthlyData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey="month" stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" />
                  <Tooltip contentStyle={styles.tooltipStyle} />
                  <Legend />
                  <Line type="monotone" dataKey="revenue" stroke="#3b82f6" strokeWidth={2} />
                  <Line type="monotone" dataKey="shipping" stroke="#f97316" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const TextAnalysisPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '56rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={styles.searchSection}>
          <div style={styles.searchContainer}>
            <input type="text" placeholder="Ask about your data..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} style={styles.searchInput} />
            <button style={styles.searchButton}>Analyze</button>
          </div>
        </div>
        <div>
          {[
            { question: 'What are top product categories?', answer: 'Your top categories are ranked by sales volume and revenue contribution.' },
            { question: 'How is delivery performing?', answer: 'Delivery metrics show on-time rates and average delivery days across regions.' },
            { question: 'Which payment methods dominate?', answer: 'Payment method distribution shows customer preferences and usage trends.' }
          ].map((item, idx) => (
            <div key={idx} style={styles.resultCard}>
              <h3 style={styles.resultQuestion}>{item.question}</h3>
              <p style={styles.resultAnswer}>{item.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const GraphsPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
          {[
            { id: 'revenue', title: 'Revenue Trend', icon: 'ðŸ“ˆ' },
            { id: 'categories', title: 'Category Sales', icon: 'ðŸ“Š' },
            { id: 'delivery', title: 'Delivery Rate', icon: 'ðŸšš' },
            { id: 'payment', title: 'Payment Methods', icon: 'ðŸ’³' }
          ].map(chart => (
            <div key={chart.id} onClick={() => setSelectedMetric(chart.id)} style={{ borderRadius: '0.75rem', padding: '1.5rem', border: selectedMetric === chart.id ? '2px solid #2563eb' : '2px solid #475569', backgroundColor: selectedMetric === chart.id ? 'rgba(37, 99, 235, 0.1)' : '#1e293b', cursor: 'pointer' }}>
              <span style={{fontSize: '2.25rem', marginBottom: '1rem', display: 'block'}}>{chart.icon}</span>
              <h3 style={{fontWeight: 'bold', color: 'white', margin: 0}}>{chart.title}</h3>
            </div>
          ))}
        </div>

        <div style={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '2rem' }}>
          {selectedMetric === 'revenue' && (
            <ResponsiveContainer width="100%" height={400}>
              <AreaChart data={monthlyData}>
                <defs><linearGradient id="colorRev" x1="0" y1="0" x2="0" y2="1"><stop offset="5%" stopColor="#3b82f6" stopOpacity={0.8}/><stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/></linearGradient></defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="month" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={styles.tooltipStyle} />
                <Area type="monotone" dataKey="revenue" stroke="#3b82f6" fillOpacity={1} fill="url(#colorRev)" />
              </AreaChart>
            </ResponsiveContainer>
          )}
          {selectedMetric === 'categories' && (
            <ResponsiveContainer width="100%" height={400}>
              <BarChart data={categoryData} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis type="number" stroke="#94a3b8" />
                <YAxis dataKey="name" type="category" stroke="#94a3b8" width={100} />
                <Tooltip contentStyle={styles.tooltipStyle} />
                <Bar dataKey="value" fill="#3b82f6" />
              </BarChart>
            </ResponsiveContainer>
          )}
          {selectedMetric === 'delivery' && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie data={onTimeData} cx="50%" cy="50%" innerRadius={80} outerRadius={140} dataKey="value">
                  {onTimeData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                </Pie>
                <Tooltip contentStyle={styles.tooltipStyle} />
              </PieChart>
            </ResponsiveContainer>
          )}
          {selectedMetric === 'payment' && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie data={paymentData} cx="50%" cy="50%" dataKey="value">
                  {paymentData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                </Pie>
                <Tooltip contentStyle={styles.tooltipStyle} />
              </PieChart>
            </ResponsiveContainer>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <>
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'dashboard' && <DashboardPage />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <GraphsPage />}
    </>
  );
}
