import React, { useState, useMemo } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Filter, Download, Share2, MessageSquare } from 'lucide-react';

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(to bottom right, #0f172a, #1e293b, #1e293b)',
  },
  header: {
    background: 'rgba(15, 23, 42, 0.8)',
    backdropFilter: 'blur(10px)',
    borderBottom: '1px solid #475569',
    position: 'sticky',
    top: 0,
    zIndex: 50,
  },
  headerContent: {
    maxWidth: '80rem',
    margin: '0 auto',
    padding: '1.5rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  logo: {
    fontSize: '1.875rem',
    fontWeight: 'bold',
    background: 'linear-gradient(to right, #60a5fa, #22d3ee)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    margin: 0,
  },
  subtitle: {
    fontSize: '0.875rem',
    color: '#94a3b8',
    margin: '0.25rem 0 0 0',
  },
  nav: {
    display: 'flex',
    gap: '0.5rem',
  },
  navButton: (isActive) => ({
    padding: '0.5rem 1rem',
    borderRadius: '0.5rem',
    fontWeight: '500',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    backgroundColor: isActive ? '#2563eb' : 'transparent',
    color: isActive ? 'white' : '#cbd5e1',
  }),
  heroSection: {
    maxWidth: '80rem',
    margin: '0 auto',
    padding: '5rem 1.5rem',
    textAlign: 'center',
  },
  heroTitle: {
    fontSize: '3rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '1rem',
  },
  heroSubtitle: {
    fontSize: '1.25rem',
    color: '#cbd5e1',
    marginBottom: '3rem',
  },
  featureGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '2rem',
  },
  featureCard: {
    backgroundColor: '#1e293b',
    borderRadius: '1rem',
    padding: '2rem',
    border: '1px solid #475569',
    boxShadow: '0 10px 15px rgba(0, 0, 0, 0.3)',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
  },
  featureIcon: {
    fontSize: '3.75rem',
    marginBottom: '1rem',
    display: 'block',
  },
  featureTitle: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '0.75rem',
  },
  featureDescription: {
    color: '#cbd5e1',
    marginBottom: '1.5rem',
    lineHeight: '1.6',
  },
  featureButton: {
    padding: '0.5rem 1.5rem',
    background: 'linear-gradient(to right, #2563eb, #1d4ed8)',
    color: 'white',
    fontWeight: '600',
    border: 'none',
    borderRadius: '0.5rem',
    cursor: 'pointer',
    transition: 'box-shadow 0.3s ease',
  },
  statsSection: {
    maxWidth: '80rem',
    margin: '0 auto',
    padding: '4rem 1.5rem',
    backgroundColor: 'rgba(30, 41, 59, 0.5)',
    borderTop: '1px solid #475569',
  },
  statsTitle: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '2rem',
    textAlign: 'center',
  },
  statsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '1.5rem',
  },
  statCard: (color) => ({
    padding: '1.5rem',
    background: color,
    borderRadius: '0.75rem',
    color: 'white',
  }),
  statLabel: {
    fontSize: '0.875rem',
    opacity: 0.9,
    margin: 0,
  },
  statValue: {
    fontSize: '1.875rem',
    fontWeight: 'bold',
    marginTop: '0.5rem',
    margin: '0.5rem 0 0 0',
  },
  statChange: {
    fontSize: '0.875rem',
    marginTop: '0.5rem',
    opacity: 0.75,
    margin: '0.5rem 0 0 0',
  },
  dashboardContent: {
    maxWidth: '80rem',
    margin: '0 auto',
    padding: '2rem 1.5rem',
  },
  kpiGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))',
    gap: '1.5rem',
    marginBottom: '2rem',
  },
  kpiCard: {
    backgroundColor: '#1e293b',
    border: '1px solid #475569',
    borderRadius: '0.75rem',
    padding: '1.5rem',
    transition: 'all 0.3s ease',
    cursor: 'pointer',
  },
  kpiIcon: {
    fontSize: '2.25rem',
    marginBottom: '0.75rem',
    display: 'block',
  },
  kpiTitle: {
    fontSize: '0.875rem',
    color: '#94a3b8',
    margin: 0,
  },
  kpiValue: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: 'white',
    marginTop: '0.5rem',
    margin: '0.5rem 0 0 0',
  },
  kpiChange: {
    fontSize: '0.875rem',
    color: '#4ade80',
    fontWeight: '500',
    marginTop: '0.5rem',
    margin: '0.5rem 0 0 0',
  },
  filterSection: {
    backgroundColor: '#1e293b',
    border: '1px solid #475569',
    borderRadius: '0.75rem',
    padding: '1.5rem',
    marginBottom: '2rem',
  },
  filterContent: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    flexWrap: 'wrap',
  },
  filterLabel: {
    fontWeight: '600',
    color: '#e2e8f0',
  },
  filterButton: (isActive) => ({
    padding: '0.5rem 1rem',
    borderRadius: '0.5rem',
    fontWeight: '500',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    backgroundColor: isActive ? '#2563eb' : '#334155',
    color: isActive ? 'white' : '#cbd5e1',
  }),
  chartsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(500px, 1fr))',
    gap: '2rem',
  },
  chartCard: {
    backgroundColor: '#1e293b',
    border: '1px solid #475569',
    borderRadius: '0.75rem',
    padding: '1.5rem',
    transition: 'all 0.3s ease',
  },
  chartTitle: {
    fontSize: '1.125rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '1rem',
    margin: '0 0 1rem 0',
  },
  searchSection: {
    backgroundColor: '#1e293b',
    border: '1px solid #475569',
    borderRadius: '0.75rem',
    padding: '1.5rem',
    marginBottom: '2rem',
  },
  searchContainer: {
    display: 'flex',
    gap: '0.5rem',
  },
  searchInput: {
    flex: 1,
    padding: '0.75rem 1rem',
    backgroundColor: '#0f172a',
    border: '1px solid #475569',
    borderRadius: '0.5rem',
    color: 'white',
    fontSize: '1rem',
    transition: 'all 0.3s ease',
    fontFamily: 'inherit',
  },
  searchButton: {
    padding: '0.75rem 1.5rem',
    backgroundColor: '#2563eb',
    color: 'white',
    border: 'none',
    borderRadius: '0.5rem',
    fontWeight: '500',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    fontFamily: 'inherit',
  },
  resultsContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1.5rem',
  },
  resultCard: {
    backgroundColor: '#1e293b',
    border: '1px solid #475569',
    borderRadius: '0.75rem',
    padding: '2rem',
    transition: 'all 0.3s ease',
  },
  resultQuestion: {
    fontSize: '1.125rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '1rem',
    margin: '0 0 1rem 0',
  },
  resultAnswer: {
    color: '#cbd5e1',
    lineHeight: '1.6',
    marginLeft: '2rem',
    margin: '0 0 0 2rem',
  },
  chartSelectionGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '1.5rem',
    marginBottom: '2rem',
  },
  chartOption: (isSelected) => ({
    borderRadius: '0.75rem',
    padding: '1.5rem',
    border: isSelected ? '2px solid #2563eb' : '2px solid #475569',
    backgroundColor: isSelected ? 'rgba(37, 99, 235, 0.1)' : '#1e293b',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
  }),
  chartOptionIcon: {
    fontSize: '2.25rem',
    marginBottom: '0.75rem',
    display: 'block',
  },
  chartOptionTitle: {
    fontWeight: 'bold',
    color: 'white',
    margin: 0,
  },
  chartOptionDescription: {
    fontSize: '0.875rem',
    color: '#94a3b8',
    marginTop: '0.25rem',
    margin: '0.25rem 0 0 0',
  },
  vizContainer: {
    backgroundColor: '#1e293b',
    border: '1px solid #475569',
    borderRadius: '0.75rem',
    padding: '2rem',
  },
  vizTitle: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: 'white',
    marginBottom: '1.5rem',
    margin: '0 0 1.5rem 0',
  },
  buttonGroup: {
    marginTop: '2rem',
    display: 'flex',
    gap: '1rem',
  },
  primaryButton: {
    padding: '0.5rem 1.5rem',
    backgroundColor: '#2563eb',
    color: 'white',
    border: 'none',
    borderRadius: '0.5rem',
    fontWeight: '500',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    fontFamily: 'inherit',
  },
  secondaryButton: {
    padding: '0.5rem 1.5rem',
    backgroundColor: 'transparent',
    color: '#cbd5e1',
    border: '1px solid #475569',
    borderRadius: '0.5rem',
    fontWeight: '500',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    fontFamily: 'inherit',
  },
};

export default function ExorViaDashboard() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [dateRange, setDateRange] = useState('1m');
  const [selectedMetric, setSelectedMetric] = useState('revenue');
  const [searchQuery, setSearchQuery] = useState('');

  const analyticsData = [
    { date: 'Jan 1', revenue: 4000, users: 2400, engagement: 65 },
    { date: 'Jan 8', revenue: 3000, users: 1398, engagement: 58 },
    { date: 'Jan 15', revenue: 2000, users: 9800, engagement: 72 },
    { date: 'Jan 22', revenue: 2780, users: 3908, engagement: 68 },
    { date: 'Jan 29', revenue: 1890, users: 4800, engagement: 74 },
    { date: 'Feb 5', revenue: 2390, users: 3800, engagement: 70 },
  ];

  const kpiData = [
    { title: 'Total Revenue', value: '$124,589', change: '+12.5%', icon: 'ðŸ’°', color: 'linear-gradient(135deg, #3b82f6, #06b6d4)' },
    { title: 'Active Users', value: '8,234', change: '+5.2%', icon: 'ðŸ‘¥', color: 'linear-gradient(135deg, #a855f7, #ec4899)' },
    { title: 'Engagement Rate', value: '68.4%', change: '+2.1%', icon: 'ðŸ“Š', color: 'linear-gradient(135deg, #10b981, #059669)' },
    { title: 'Conversion Rate', value: '3.28%', change: '+0.8%', icon: 'ðŸŽ¯', color: 'linear-gradient(135deg, #f97316, #dc2626)' },
  ];

  const textAnalysisResults = [
    { id: 1, question: 'What are the top revenue drivers?', answer: 'Our analysis shows that Premium subscriptions account for 45% of revenue, followed by API usage at 32%. Enterprise clients show 3.2x higher lifetime value.' },
    { id: 2, question: 'How is user engagement trending?', answer: 'User engagement has improved by 14% over the past month. Mobile users show 28% higher daily active rates compared to desktop users.' },
    { id: 3, question: 'What is the churn rate?', answer: 'Current monthly churn is 2.1%, down from 3.4% last quarter. Improved onboarding reduced churn among new users by 45%.' },
  ];

  const chartTypes = [
    { id: 'revenue', title: 'Revenue Trend', icon: 'ðŸ“ˆ', description: 'Track revenue over time' },
    { id: 'users', title: 'User Growth', icon: 'ðŸ‘¥', description: 'Monitor active user counts' },
    { id: 'engagement', title: 'Engagement Metrics', icon: 'âš¡', description: 'Daily engagement rates' },
    { id: 'distribution', title: 'Revenue Distribution', icon: 'ðŸ¥§', description: 'Source breakdown' },
  ];

  const distributionData = [
    { name: 'Premium', value: 45, color: '#3B82F6' },
    { name: 'API Usage', value: 32, color: '#8B5CF6' },
    { name: 'Enterprise', value: 18, color: '#EC4899' },
    { name: 'Other', value: 5, color: '#6B7280' },
  ];

  const filteredData = useMemo(() => {
    const sliceMap = { '1w': 1, '1m': 3, '3m': 6 };
    return analyticsData.slice(-sliceMap[dateRange]);
  }, [dateRange]);

  const tooltipStyle = {
    backgroundColor: '#0f172a',
    border: '1px solid #475569',
    borderRadius: '0.5rem',
    color: '#fff',
  };

  // Navigation Component
  const Navigation = () => (
    <div style={styles.header}>
      <div style={styles.headerContent}>
        <div>
          <h1 style={styles.logo}>ExorVia</h1>
          <p style={styles.subtitle}>Data Analytics Platform</p>
        </div>
        <nav style={styles.nav}>
          {['home', 'dashboard', 'text', 'graphs'].map(page => (
            <button
              key={page}
              onClick={() => setCurrentPage(page)}
              style={styles.navButton(currentPage === page)}
              onMouseEnter={(e) => !currentPage === page && (e.target.style.backgroundColor = '#1e293b')}
              onMouseLeave={(e) => !currentPage === page && (e.target.style.backgroundColor = 'transparent')}
            >
              {page.charAt(0).toUpperCase() + page.slice(1)}
            </button>
          ))}
        </nav>
      </div>
    </div>
  );

  const HomePage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={styles.heroSection}>
        <h2 style={styles.heroTitle}>ExorVia Dashboard</h2>
        <p style={styles.heroSubtitle}>Welcome to your comprehensive data analysis platform</p>
        <div style={styles.featureGrid}>
          {[
            { page: 'dashboard', icon: 'ðŸ“Š', title: 'Analytics Dashboard', desc: 'View comprehensive analytics and KPIs with interactive visualizations and filters.' },
            { page: 'text', icon: 'âœï¸', title: 'Text Analysis', desc: 'Ask questions about your data and get AI-powered insights and summaries.' },
            { page: 'graphs', icon: 'ðŸ“ˆ', title: 'Infographic Generator', desc: 'Generate custom charts and visualizations automatically from your questions.' }
          ].map(card => (
            <div
              key={card.page}
              style={styles.featureCard}
              onClick={() => setCurrentPage(card.page)}
              onMouseEnter={(e) => {
                e.currentTarget.style.boxShadow = '0 20px 25px rgba(0, 0, 0, 0.5)';
                e.currentTarget.style.transform = 'scale(1.05)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.boxShadow = '0 10px 15px rgba(0, 0, 0, 0.3)';
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              <span style={styles.featureIcon}>{card.icon}</span>
              <h3 style={styles.featureTitle}>{card.title}</h3>
              <p style={styles.featureDescription}>{card.desc}</p>
              <button style={styles.featureButton}>Go â†’</button>
            </div>
          ))}
        </div>
      </div>
      <div style={styles.statsSection}>
        <h3 style={styles.statsTitle}>Quick Stats</h3>
        <div style={styles.statsGrid}>
          {kpiData.map((kpi, idx) => (
            <div key={idx} style={styles.statCard(kpi.color)}>
              <p style={styles.statLabel}>{kpi.title}</p>
              <p style={styles.statValue}>{kpi.value}</p>
              <p style={styles.statChange}>{kpi.change}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const AnalyticsDashboard = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={styles.dashboardContent}>
        <div style={styles.kpiGrid}>
          {kpiData.map((kpi, idx) => (
            <div
              key={idx}
              style={styles.kpiCard}
              onMouseEnter={(e) => {
                e.currentTarget.style.borderColor = '#64748b';
                e.currentTarget.style.boxShadow = '0 20px 25px rgba(0, 0, 0, 0.5)';
                e.currentTarget.style.transform = 'scale(1.05)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.borderColor = '#475569';
                e.currentTarget.style.boxShadow = 'none';
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              <span style={styles.kpiIcon}>{kpi.icon}</span>
              <p style={styles.kpiTitle}>{kpi.title}</p>
              <p style={styles.kpiValue}>{kpi.value}</p>
              <p style={styles.kpiChange}>{kpi.change}</p>
            </div>
          ))}
        </div>

        <div style={styles.filterSection}>
          <div style={styles.filterContent}>
            <Filter size={20} color="#94a3b8" />
            <span style={styles.filterLabel}>Date Range:</span>
            {['1w', '1m', '3m'].map(range => (
              <button
                key={range}
                onClick={() => setDateRange(range)}
                style={styles.filterButton(dateRange === range)}
              >
                {range === '1w' ? '1 Week' : range === '1m' ? '1 Month' : '3 Months'}
              </button>
            ))}
          </div>
        </div>

        <div style={styles.chartsGrid}>
          <div style={styles.chartCard} onMouseEnter={(e) => e.currentTarget.style.borderColor = '#64748b'} onMouseLeave={(e) => e.currentTarget.style.borderColor = '#475569'}>
            <h3 style={styles.chartTitle}>Revenue Trend</h3>
            <ResponsiveContainer width="100%" height={300}>
              <AreaChart data={filteredData}>
                <defs><linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1"><stop offset="5%" stopColor="#3B82F6" stopOpacity={0.8}/><stop offset="95%" stopColor="#3B82F6" stopOpacity={0}/></linearGradient></defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="date" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Area type="monotone" dataKey="revenue" stroke="#3B82F6" fillOpacity={1} fill="url(#colorRevenue)" />
              </AreaChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.chartCard} onMouseEnter={(e) => e.currentTarget.style.borderColor = '#64748b'} onMouseLeave={(e) => e.currentTarget.style.borderColor = '#475569'}>
            <h3 style={styles.chartTitle}>Active Users</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={filteredData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="date" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Bar dataKey="users" fill="#8B5CF6" radius={[8, 8, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.chartCard} onMouseEnter={(e) => e.currentTarget.style.borderColor = '#64748b'} onMouseLeave={(e) => e.currentTarget.style.borderColor = '#475569'}>
            <h3 style={styles.chartTitle}>Engagement Rate</h3>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={filteredData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="date" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Line type="monotone" dataKey="engagement" stroke="#10B981" strokeWidth={3} dot={{ fill: '#10B981', r: 5 }} />
              </LineChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.chartCard} onMouseEnter={(e) => e.currentTarget.style.borderColor = '#64748b'} onMouseLeave={(e) => e.currentTarget.style.borderColor = '#475569'}>
            <h3 style={styles.chartTitle}>Revenue Distribution</h3>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie data={distributionData} cx="50%" cy="50%" labelLine={false} label={({ name, value }) => `${name}: ${value}%`} outerRadius={80} fill="#8884d8" dataKey="value">
                  {distributionData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.color} />)}
                </Pie>
                <Tooltip contentStyle={tooltipStyle} />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );

  const TextAnalysisPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={{ maxWidth: '56rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={styles.searchSection}>
          <div style={styles.searchContainer}>
            <input type="text" placeholder="Ask a question about your data..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} style={styles.searchInput} onFocus={(e) => e.target.style.borderColor = '#2563eb'} onBlur={(e) => e.target.style.borderColor = '#475569'} />
            <button style={styles.searchButton} onMouseEnter={(e) => e.target.style.backgroundColor = '#1d4ed8'} onMouseLeave={(e) => e.target.style.backgroundColor = '#2563eb'}>Analyze</button>
          </div>
        </div>
        <div style={styles.resultsContainer}>
          {textAnalysisResults.map(result => (
            <div key={result.id} style={styles.resultCard} onMouseEnter={(e) => { e.currentTarget.style.borderColor = '#64748b'; e.currentTarget.style.boxShadow = '0 10px 15px rgba(0, 0, 0, 0.3)'; }} onMouseLeave={(e) => { e.currentTarget.style.borderColor = '#475569'; e.currentTarget.style.boxShadow = 'none'; }}>
              <div style={{ display: 'flex', gap: '1rem', alignItems: 'flex-start', marginBottom: '1rem' }}>
                <MessageSquare color="#60a5fa" size={20} style={{ marginTop: '0.25rem', flexShrink: 0 }} />
                <h3 style={styles.resultQuestion}>{result.question}</h3>
              </div>
              <p style={styles.resultAnswer}>{result.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const InfographicsPage = () => (
    <div style={styles.container}>
      <Navigation />
      <div style={styles.dashboardContent}>
        <div style={styles.chartSelectionGrid}>
          {chartTypes.map(chart => (
            <div key={chart.id} style={styles.chartOption(selectedMetric === chart.id)} onClick={() => setSelectedMetric(chart.id)} onMouseEnter={(e) => selectedMetric !== chart.id && (e.currentTarget.style.borderColor = '#64748b')} onMouseLeave={(e) => selectedMetric !== chart.id && (e.currentTarget.style.borderColor = '#475569')}>
              <span style={styles.chartOptionIcon}>{chart.icon}</span>
              <h3 style={styles.chartOptionTitle}>{chart.title}</h3>
              <p style={styles.chartOptionDescription}>{chart.description}</p>
            </div>
          ))}
        </div>

        <div style={styles.vizContainer}>
          <h3 style={styles.vizTitle}>{chartTypes.find(c => c.id === selectedMetric)?.title}</h3>

          {selectedMetric === 'revenue' && (
            <ResponsiveContainer width="100%" height={400}>
              <AreaChart data={analyticsData}>
                <defs><linearGradient id="colorRev" x1="0" y1="0" x2="0" y2="1"><stop offset="5%" stopColor="#3B82F6" stopOpacity={0.8}/><stop offset="95%" stopColor="#3B82F6" stopOpacity={0}/></linearGradient></defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="date" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Area type="monotone" dataKey="revenue" stroke="#3B82F6" fillOpacity={1} fill="url(#colorRev)" />
              </AreaChart>
            </ResponsiveContainer>
          )}

          {selectedMetric === 'users' && (
            <ResponsiveContainer width="100%" height={400}>
              <BarChart data={analyticsData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="date" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Bar dataKey="users" fill="#8B5CF6" radius={[8, 8, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          )}

          {selectedMetric === 'engagement' && (
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={analyticsData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="date" stroke="#94a3b8" />
                <YAxis stroke="#94a3b8" />
                <Tooltip contentStyle={tooltipStyle} />
                <Line type="monotone" dataKey="engagement" stroke="#10B981" strokeWidth={3} dot={{ fill: '#10B981', r: 5 }} />
              </LineChart>
            </ResponsiveContainer>
          )}

          {selectedMetric === 'distribution' && (
            <ResponsiveContainer width="100%" height={400}>
              <PieChart>
                <Pie data={distributionData} cx="50%" cy="50%" labelLine={false} label={({ name, value }) => `${name}: ${value}%`} outerRadius={100} fill="#8884d8" dataKey="value">
                  {distributionData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.color} />)}
                </Pie>
                <Tooltip contentStyle={tooltipStyle} />
              </PieChart>
            </ResponsiveContainer>
          )}

          <div style={styles.buttonGroup}>
            <button style={styles.primaryButton} onMouseEnter={(e) => e.target.style.backgroundColor = '#1d4ed8'} onMouseLeave={(e) => e.target.style.backgroundColor = '#2563eb'}>
              <Download size={18} /> Download PNG
            </button>
            <button style={styles.secondaryButton} onMouseEnter={(e) => e.target.style.backgroundColor = '#334155'} onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}>
              <Share2 size={18} /> Share
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <>
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'dashboard' && <AnalyticsDashboard />}
      {currentPage === 'text' && <TextAnalysisPage />}
      {currentPage === 'graphs' && <InfographicsPage />}
    </>
  );
}
