import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Menu, X } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

class APIClient {
  static async fetchDashboardData() {
    try {
      const response = await fetch(`${API_BASE_URL}/data`);
      if (!response.ok) throw new Error(`API error: ${response.status}`);
      const rawData = await response.json();
      if (!Array.isArray(rawData)) return [];
      return rawData;
    } catch (error) {
      console.error('Dashboard data fetch error:', error.message);
      return [];
    }
  }
}

const COLUMNS = {
  purchaseTimestamp: 'Purchase Timestamp',
  reviewScore: 'Review Score',
  deliveredOnTime: 'Delivered on Time',
  paymentType: 'Payment Type',
  paymentValue: 'Payment Value',
  shippingCost: 'Shipping Cost',
  productCategory: 'Product Category',
  customerState: 'Customer State',
  orderStatus: 'Order Status',
  deliveryDurationDays: 'Delivery Duration (days)',
  timeToDeliver: 'Time to Deliver to Customer',
  orderId: 'Order ID'
};

const COLORS = {
  primary_blue: "#0066CC",
  dark_blue: "#0052A3",
  primary_orange: "#FF6B35",
  dark_orange: "#E55A28",
  success: "#10B981",
  danger: "#EF4444"
};

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    return (
      <div style={{ background: '#FFFFFF', border: '1px solid #E5E7EB', borderRadius: '8px', padding: '8px 12px', fontSize: '12px' }}>
        {payload.map((entry, index) => (
          <div key={index} style={{ color: entry.color }}>
            {entry.name}: {typeof entry.value === 'number' ? entry.value.toFixed(2) : entry.value}
          </div>
        ))}
      </div>
    );
  }
  return null;
};

const styles = {
  container: { minHeight: '100vh', background: '#FFFFFF', color: '#374151', fontFamily: 'Inter, sans-serif', display: 'grid', gridTemplateColumns: '280px 1fr' },
  containerCollapsed: { minHeight: '100vh', background: '#FFFFFF', color: '#374151', fontFamily: 'Inter, sans-serif', display: 'grid', gridTemplateColumns: '1fr' },
  sidebar: { background: '#FFFFFF', borderRight: '1px solid #E5E7EB', padding: '24px', overflowY: 'auto', position: 'fixed', top: 0, left: 0, width: '280px', height: '100vh', zIndex: 1000, transition: 'transform 0.3s ease' },
  sidebarCollapsed: { transform: 'translateX(-280px)' },
  header: { background: '#FFFFFF', borderBottom: '1px solid #E5E7EB', padding: '20px 32px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', position: 'sticky', top: 0, zIndex: 50, gridColumn: '1 / -1' },
  headerCollapsed: { gridColumn: '1' },
  toggleBtn: { background: 'none', border: 'none', cursor: 'pointer', fontSize: '20px', color: '#374151', marginRight: '1rem' },
  content: { padding: '32px', overflowY: 'auto', gridColumn: '2' },
  contentCollapsed: { gridColumn: '1', paddingLeft: '32px' },
  sidebarSection: { marginBottom: '20px', paddingBottom: '16px', borderBottom: '1px solid #E5E7EB' },
  sidebarLabel: { fontWeight: '600', color: '#1F2937', marginBottom: '8px', display: 'block', fontSize: '13px' },
  sidebarSelect: { width: '100%', padding: '8px 12px', border: '1px solid #E5E7EB', borderRadius: '8px', fontSize: '13px', fontFamily: 'inherit', backgroundColor: '#F9FAFB', color: '#374151', cursor: 'pointer' },
  selectAllBtn: { width: '100%', marginTop: '8px', padding: '6px', background: '#F9FAFB', border: '1px solid #E5E7EB', borderRadius: '6px', cursor: 'pointer', fontSize: '11px', fontWeight: '600' },
  kpiContainer: { background: '#FFFFFF', borderRadius: '12px', padding: '28px', boxShadow: '0 1px 2px rgba(0,0,0,0.05)', marginBottom: '32px', border: '1px solid #E5E7EB' },
  kpiGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '16px', marginBottom: '16px' },
  kpiCard: (color) => ({ background: color, borderRadius: '12px', padding: '24px', textAlign: 'center', color: 'white', boxShadow: '0 4px 12px rgba(0,102,204,0.12)' }),
  kpiLabel: { fontSize: '10px', fontWeight: '700', textTransform: 'uppercase', color: 'rgba(255,255,255,0.75)', marginBottom: '10px' },
  kpiValue: { fontSize: '28px', fontWeight: '700', color: '#FFFFFF', margin: 0 },
  kpiValueNormal: { fontSize: '20px', fontWeight: '700', color: '#111827', margin: 0 },
  chartsGrid: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '20px' },
  graphCard: { backgroundColor: '#FFFFFF', borderRadius: '12px', padding: '20px', minHeight: '400px', display: 'flex', flexDirection: 'column', border: '1px solid #E5E7EB', boxShadow: '0 1px 2px rgba(0,0,0,0.05)' },
  graphCardSpan2: { gridColumn: 'span 2' },
};

export default function EcommerceDashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [selectedStates, setSelectedStates] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedPaymentTypes, setSelectedPaymentTypes] = useState([]);
  const [selectedStatuses, setSelectedStatuses] = useState([]);
  const [deliveryOnTime, setDeliveryOnTime] = useState('all');
  const [reviewRange, setReviewRange] = useState([1, 5]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await APIClient.fetchDashboardData();
        if (!Array.isArray(fetchedData) || fetchedData.length === 0) {
          setError('No data returned from API');
        } else {
          setData(fetchedData);
          setError(null);
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Parse delivery duration
  const parseDuration = (value) => {
    if (!value || typeof value !== 'string' || value.includes('Yet to be Complete')) return null;
    const match = value.match(/(?:(\d+)\s+days?\s+)?(\d+):(\d+):(\d+)/);
    if (!match) return null;
    const days = parseInt(match[1]) || 0;
    const hours = parseInt(match[2]) || 0;
    const minutes = parseInt(match[3]) || 0;
    const seconds = parseInt(match[4]) || 0;
    return days + hours / 24 + minutes / 1440 + seconds / 86400;
  };

  const enrichedData = useMemo(() => {
    return data.map(d => ({
      ...d,
      deliveryDurationDays: parseDuration(d?.[COLUMNS.timeToDeliver])
    }));
  }, [data]);

  const filteredData = useMemo(() => {
    if (enrichedData.length === 0) return [];
    let filtered = [...enrichedData];

    if (selectedStates.length > 0) filtered = filtered.filter(d => selectedStates.includes(d?.[COLUMNS.customerState]));
    if (selectedCategories.length > 0) filtered = filtered.filter(d => selectedCategories.includes(d?.[COLUMNS.productCategory]));
    if (selectedPaymentTypes.length > 0) filtered = filtered.filter(d => selectedPaymentTypes.includes(d?.[COLUMNS.paymentType]));
    if (selectedStatuses.length > 0) filtered = filtered.filter(d => selectedStatuses.includes(d?.[COLUMNS.orderStatus]));
    if (deliveryOnTime !== 'all') filtered = filtered.filter(d => d?.[COLUMNS.deliveredOnTime] === deliveryOnTime);
    filtered = filtered.filter(d => {
      const score = parseFloat(d?.[COLUMNS.reviewScore]) || 0;
      return score >= reviewRange[0] && score <= reviewRange[1];
    });
    return filtered;
  }, [enrichedData, selectedStates, selectedCategories, selectedPaymentTypes, selectedStatuses, deliveryOnTime, reviewRange]);

  const kpis = useMemo(() => {
    if (filteredData.length === 0) return { totalOrders: 0, avgReview: 0, onTimeRate: 0, totalRevenue: 0, avgDeliveryDays: 0, avgOrderValue: 0 };

    const orderIds = new Set(filteredData.map(d => d?.[COLUMNS.orderId]).filter(Boolean));
    const totalOrders = orderIds.size;
    const avgReview = (filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.reviewScore]) || 0), 0) / filteredData.length).toFixed(2);
    const onTimeRate = ((filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'Yes').length / filteredData.length) * 100).toFixed(1);
    const totalRevenue = filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.paymentValue]) || 0), 0).toFixed(2);

    const validDeliveryDays = filteredData.filter(d => d.deliveryDurationDays != null && !isNaN(d.deliveryDurationDays)).map(d => d.deliveryDurationDays);
    const avgDeliveryDays = validDeliveryDays.length > 0 ? (validDeliveryDays.reduce((a, b) => a + b, 0) / validDeliveryDays.length).toFixed(1) : 0;

    const avgOrderValue = (totalRevenue / totalOrders).toFixed(2);
    return { totalOrders, avgReview, onTimeRate, totalRevenue, avgDeliveryDays, avgOrderValue };
  }, [filteredData]);

  const formatCurrency = (value) => {
    const num = typeof value === 'number' ? value : parseFloat(value) || 0;
    if (num >= 1000000) return `$${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `$${(num / 1000).toFixed(1)}K`;
    return `$${num.toFixed(2)}`;
  };

  const monthlyData = useMemo(() => {
    if (filteredData.length === 0) return [];
    const months = {};
    filteredData.forEach(d => {
      try {
        const timestamp = d?.[COLUMNS.purchaseTimestamp];
        if (!timestamp) return;
        const date = new Date(timestamp);
        const monthKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
        const monthLabel = date.toLocaleString('default', { month: 'short', year: '2-digit' });
        if (!months[monthKey]) months[monthKey] = { month: monthLabel, revenue: 0, shipping: 0, sortKey: monthKey };
        months[monthKey].revenue += parseFloat(d?.[COLUMNS.paymentValue]) || 0;
        months[monthKey].shipping += parseFloat(d?.[COLUMNS.shippingCost]) || 0;
      } catch (e) {}
    });
    return Object.values(months).sort((a, b) => a.sortKey.localeCompare(b.sortKey));
  }, [filteredData]);

  const categoryData = useMemo(() => {
    if (filteredData.length === 0) return [];
    const cats = {};
    filteredData.forEach(d => {
      const cat = d?.[COLUMNS.productCategory] || 'Unknown';
      if (!cats[cat]) cats[cat] = 0;
      cats[cat] += parseFloat(d?.[COLUMNS.paymentValue]) || 0;
    });
    return Object.entries(cats).map(([name, value]) => ({ name, value })).sort((a, b) => a.value - b.value);
  }, [filteredData]);

  const onTimeData = useMemo(() => {
    if (filteredData.length === 0) return [];
    const onTime = filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'Yes').length;
    const late = filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'No').length;
    return [
      { name: 'Yes', value: onTime, fill: COLORS.success },
      { name: 'No', value: late, fill: '#D1D5DB' }
    ];
  }, [filteredData]);

  const paymentData = useMemo(() => {
    if (filteredData.length === 0) return [];
    const payments = {};
    filteredData.forEach(d => {
      const type = d?.[COLUMNS.paymentType] || 'Unknown';
      if (!payments[type]) payments[type] = 0;
      payments[type] += 1;
    });
    const colors = ['#8B5CF6', '#06B6D4', '#EC4899', '#84CC16'];
    return Object.entries(payments).map(([name, value], idx) => ({ name, value, fill: colors[idx % colors.length] }));
  }, [filteredData]);

  const reviewData = useMemo(() => {
    if (filteredData.length === 0) return [];
    const monthData = {};
    filteredData.forEach(d => {
      try {
        const timestamp = d?.[COLUMNS.purchaseTimestamp];
        if (!timestamp) return;
        const date = new Date(timestamp);
        const monthKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
        const monthLabel = date.toLocaleString('default', { month: 'short' });
        if (!monthData[monthKey]) monthData[monthKey] = { month: monthLabel, avgReview: 0, count: 0, sortKey: monthKey };
        monthData[monthKey].avgReview += parseFloat(d?.[COLUMNS.reviewScore]) || 0;
        monthData[monthKey].count += 1;
      } catch (e) {}
    });
    return Object.values(monthData)
      .map(m => ({ month: m.month, avgReview: (m.avgReview / m.count) }))
      .sort((a, b) => a.sortKey - b.sortKey);
  }, [filteredData]);

  const getUniqueValues = (columnKey) => {
    if (!Array.isArray(data)) return [];
    return [...new Set(data.map(d => d?.[columnKey]).filter(Boolean))].sort();
  };

  const stateOptions = useMemo(() => getUniqueValues(COLUMNS.customerState), [data]);
  const categoryOptions = useMemo(() => getUniqueValues(COLUMNS.productCategory), [data]);
  const paymentOptions = useMemo(() => getUniqueValues(COLUMNS.paymentType), [data]);
  const statusOptions = useMemo(() => getUniqueValues(COLUMNS.orderStatus), [data]);

  if (loading) return <div style={{...styles.container, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>Loading...</div>;
  if (error) return <div style={{...styles.container, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>Error: {error}</div>;

  return (
    <div style={sidebarOpen ? styles.container : styles.containerCollapsed}>
      {/* Sidebar */}
      <div style={{...styles.sidebar, ...(sidebarOpen ? {} : styles.sidebarCollapsed)}}>
        <div style={{marginBottom: '24px'}}>
          <button onClick={() => setSidebarOpen(false)} style={{...styles.toggleBtn, marginBottom: '16px'}}>
            <X size={20} />
          </button>
          <h2 style={{margin: '0 0 24px 0', fontSize: '18px', fontWeight: 'bold', color: '#111827'}}>Filters</h2>
        </div>

        <div style={styles.sidebarSection}>
          <label style={styles.sidebarLabel}>Customer State</label>
          <select multiple style={{...styles.sidebarSelect, height: '100px'}} value={selectedStates} onChange={(e) => setSelectedStates(Array.from(e.target.selectedOptions, opt => opt.value))}>
            {stateOptions.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
          <button onClick={() => setSelectedStates(selectedStates.length === stateOptions.length ? [] : stateOptions)} style={styles.selectAllBtn}>
            {selectedStates.length === stateOptions.length ? 'Deselect All' : 'Select All'}
          </button>
        </div>

        <div style={styles.sidebarSection}>
          <label style={styles.sidebarLabel}>Product Category</label>
          <select multiple style={{...styles.sidebarSelect, height: '100px'}} value={selectedCategories} onChange={(e) => setSelectedCategories(Array.from(e.target.selectedOptions, opt => opt.value))}>
            {categoryOptions.map(c => <option key={c} value={c}>{c}</option>)}
          </select>
          <button onClick={() => setSelectedCategories(selectedCategories.length === categoryOptions.length ? [] : categoryOptions)} style={styles.selectAllBtn}>
            {selectedCategories.length === categoryOptions.length ? 'Deselect All' : 'Select All'}
          </button>
        </div>

        <div style={styles.sidebarSection}>
          <label style={styles.sidebarLabel}>Payment Type</label>
          <select multiple style={{...styles.sidebarSelect, height: '100px'}} value={selectedPaymentTypes} onChange={(e) => setSelectedPaymentTypes(Array.from(e.target.selectedOptions, opt => opt.value))}>
            {paymentOptions.map(p => <option key={p} value={p}>{p}</option>)}
          </select>
          <button onClick={() => setSelectedPaymentTypes(selectedPaymentTypes.length === paymentOptions.length ? [] : paymentOptions)} style={styles.selectAllBtn}>
            {selectedPaymentTypes.length === paymentOptions.length ? 'Deselect All' : 'Select All'}
          </button>
        </div>

        <div style={styles.sidebarSection}>
          <label style={styles.sidebarLabel}>Order Status</label>
          <select multiple style={{...styles.sidebarSelect, height: '100px'}} value={selectedStatuses} onChange={(e) => setSelectedStatuses(Array.from(e.target.selectedOptions, opt => opt.value))}>
            {statusOptions.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
          <button onClick={() => setSelectedStatuses(selectedStatuses.length === statusOptions.length ? [] : statusOptions)} style={styles.selectAllBtn}>
            {selectedStatuses.length === statusOptions.length ? 'Deselect All' : 'Select All'}
          </button>
        </div>

        <div style={styles.sidebarSection}>
          <label style={styles.sidebarLabel}>Delivered on Time</label>
          <select style={styles.sidebarSelect} value={deliveryOnTime} onChange={(e) => setDeliveryOnTime(e.target.value)}>
            <option value="all">All</option>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>

        <div style={styles.sidebarSection}>
          <label style={styles.sidebarLabel}>Review Score: {reviewRange[0]} - {reviewRange[1]}</label>
          <input type="range" min="1" max="5" step="0.5" value={reviewRange[0]} onChange={(e) => setReviewRange([parseFloat(e.target.value), reviewRange[1]])} style={{width: '100%'}} />
          <input type="range" min="1" max="5" step="0.5" value={reviewRange[1]} onChange={(e) => setReviewRange([reviewRange[0], parseFloat(e.target.value)])} style={{width: '100%', marginTop: '8px'}} />
        </div>

        <button onClick={() => {
          setSelectedStates([]); setSelectedCategories([]); setSelectedPaymentTypes([]); setSelectedStatuses([]); 
          setDeliveryOnTime('all'); setReviewRange([1, 5]);
        }} style={{width: '100%', padding: '10px', background: '#FFFFFF', color: COLORS.danger, border: `1px solid ${COLORS.danger}`, borderRadius: '8px', cursor: 'pointer', fontWeight: '600', fontSize: '13px'}}>
          Reset Filters
        </button>
      </div>

      {/* Header */}
      <div style={{...styles.header, ...(sidebarOpen ? {} : styles.headerCollapsed)}}>
        <button onClick={() => setSidebarOpen(true)} style={styles.toggleBtn}>
          <Menu size={20} />
        </button>
        <h1 style={{margin: 0, fontSize: '24px', fontWeight: 'bold', color: COLORS.primary_blue}}>ExorVia Dashboard</h1>
        <div />
      </div>

      {/* Content */}
      <div style={{...styles.content, ...(sidebarOpen ? {} : styles.contentCollapsed)}}>
        <div style={styles.kpiContainer}>
          <div style={styles.kpiGrid}>
            {[
              { label: 'Total Revenue', value: formatCurrency(kpis.totalRevenue), color: `linear-gradient(135deg, ${COLORS.primary_blue} 0%, ${COLORS.dark_blue} 100%)` },
              { label: 'Avg Review', value: parseFloat(kpis.avgReview).toFixed(2), color: `linear-gradient(135deg, ${COLORS.primary_orange} 0%, ${COLORS.dark_orange} 100%)` },
              { label: 'Total Orders', value: parseInt(kpis.totalOrders).toLocaleString(), color: `linear-gradient(135deg, ${COLORS.success} 0%, #059669 100%)` },
            ].map((kpi, idx) => (
              <div key={idx} style={styles.kpiCard(kpi.color)}>
                <div style={styles.kpiLabel}>{kpi.label}</div>
                <h2 style={styles.kpiValue}>{kpi.value}</h2>
              </div>
            ))}
          </div>
          <div style={styles.kpiGrid}>
            {[
              { label: 'On-Time Rate', value: `${kpis.onTimeRate}%` },
              { label: 'Avg Days', value: parseFloat(kpis.avgDeliveryDays).toFixed(1) },
              { label: 'Avg Order Value', value: formatCurrency(kpis.avgOrderValue) },
            ].map((kpi, idx) => (
              <div key={idx} style={styles.kpiCard('linear-gradient(135deg, #9CA3AF 0%, #6B7280 100%)')}>
                <div style={styles.kpiLabel}>{kpi.label}</div>
                <h2 style={styles.kpiValue}>{kpi.value}</h2>
              </div>
            ))}
          </div>
        </div>

        <div style={styles.chartsGrid}>
          <div style={{...styles.graphCard, ...styles.graphCardSpan2}}>
            <ResponsiveContainer width="100%" height={300}>
              <AreaChart data={monthlyData}>
                <defs>
                  <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor={COLORS.primary_blue} stopOpacity={0.8}/>
                    <stop offset="95%" stopColor={COLORS.primary_blue} stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                <XAxis dataKey="month" stroke="#6B7280" />
                <YAxis stroke="#6B7280" />
                <Tooltip content={<CustomTooltip />} />
                <Area type="monotone" dataKey="revenue" stroke={COLORS.primary_blue} fillOpacity={1} fill="url(#colorRevenue)" />
              </AreaChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.graphCard}>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie data={onTimeData} cx="50%" cy="50%" innerRadius={60} outerRadius={100} dataKey="value">
                  {onTimeData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                </Pie>
                <Tooltip content={<CustomTooltip />} />
              </PieChart>
            </ResponsiveContainer>
          </div>

          <div style={{...styles.graphCard, ...styles.graphCardSpan2}}>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={categoryData} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                <XAxis type="number" stroke="#6B7280" />
                <YAxis dataKey="name" type="category" stroke="#6B7280" width={100} />
                <Tooltip content={<CustomTooltip />} />
                <Bar dataKey="value" fill={COLORS.primary_blue} radius={[0, 8, 8, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.graphCard}>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie data={paymentData} cx="50%" cy="50%" dataKey="value">
                  {paymentData.map((entry, index) => <Cell key={`cell-${index}`} fill={entry.fill} />)}
                </Pie>
                <Tooltip content={<CustomTooltip />} />
              </PieChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.graphCard}>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={reviewData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                <XAxis dataKey="month" stroke="#6B7280" />
                <YAxis stroke="#6B7280" />
                <Tooltip content={<CustomTooltip />} />
                <Bar dataKey="avgReview" fill={COLORS.primary_orange} radius={[8, 8, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>

          <div style={styles.graphCard}>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={monthlyData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#E5E7EB" />
                <XAxis dataKey="month" stroke="#6B7280" />
                <YAxis stroke="#6B7280" />
                <Tooltip content={<CustomTooltip />} />
                <Legend />
                <Line type="monotone" dataKey="revenue" stroke={COLORS.primary_blue} strokeWidth={2} />
                <Line type="monotone" dataKey="shipping" stroke={COLORS.primary_orange} strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
}
