import React, { useState, useMemo } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Filter, Download, Share2, MessageSquare, Menu, X } from 'lucide-react';

// Mock data generation
const generateMockData = () => {
  const states = ['S칚o Paulo', 'Rio de Janeiro', 'Minas Gerais', 'Bahia', 'Santa Catarina', 'Paran치', 'Goi치s'];
  const categories = ['Electronics', 'Fashion', 'Home', 'Sports', 'Books', 'Beauty'];
  const paymentTypes = ['Credit Card', 'Boleto', 'Debit Card', 'Voucher'];
  const statuses = ['Delivered', 'Shipped', 'Canceled', 'Processing'];

  const data = [];
  for (let i = 0; i < 500; i++) {
    data.push({
      id: i,
      month: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'][i % 6],
      revenue: Math.floor(Math.random() * 5000) + 100,
      shippingCost: Math.floor(Math.random() * 500) + 10,
      state: states[Math.floor(Math.random() * states.length)],
      category: categories[Math.floor(Math.random() * categories.length)],
      paymentType: paymentTypes[Math.floor(Math.random() * paymentTypes.length)],
      status: statuses[Math.floor(Math.random() * statuses.length)],
      reviewScore: parseFloat((Math.random() * 5).toFixed(1)),
      deliveryDays: Math.floor(Math.random() * 30) + 1,
      onTime: Math.random() > 0.3 ? 'Yes' : 'No',
    });
  }
  return data;
};

const mockData = generateMockData();

// Brazil state coordinates (lat, lng)
const stateCoordinates = {
  'S칚o Paulo': { lat: -23.5505, lng: -46.6333, customers: 145 },
  'Rio de Janeiro': { lat: -22.9068, lng: -43.1729, customers: 98 },
  'Minas Gerais': { lat: -19.9191, lng: -43.9386, customers: 76 },
  'Bahia': { lat: -12.9822, lng: -38.5104, customers: 54 },
  'Santa Catarina': { lat: -27.2423, lng: -49.6439, customers: 62 },
  'Paran치': { lat: -25.2637, lng: -51.1925, customers: 58 },
  'Goi치s': { lat: -15.7942, lng: -48.0362, customers: 41 }
};

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(to bottom right, #0f172a, #1e293b, #1e293b)',
  },
  header: {
    background: 'rgba(15, 23, 42, 0.8)',
    backdropFilter: 'blur(10px)',
    borderBottom: '1px solid #475569',
    position: 'sticky',
    top: 0,
    zIndex: 50,
  },
  headerContent: {
    maxWidth: '100%',
    margin: '0 auto',
    padding: '1.5rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  logo: {
    fontSize: '1.875rem',
    fontWeight: 'bold',
    background: 'linear-gradient(to right, #60a5fa, #22d3ee)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    margin: 0,
  },
  subtitle: {
    fontSize: '0.875rem',
    color: '#94a3b8',
    margin: '0.25rem 0 0 0',
  },
  nav: {
    display: 'flex',
    gap: '0.5rem',
  },
  navButton: (isActive) => ({
    padding: '0.5rem 1rem',
    borderRadius: '0.5rem',
    fontWeight: '500',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    backgroundColor: isActive ? '#2563eb' : 'transparent',
    color: isActive ? 'white' : '#cbd5e1',
  }),
  toggleButton: {
    background: 'transparent',
    border: 'none',
    color: '#cbd5e1',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    fontSize: '14px',
    fontWeight: '600',
    transition: 'all 0.3s ease',
  },
  mainContainer: {
    display: 'grid',
    gridTemplateColumns: '280px 1fr',
    gap: 0,
    transition: 'grid-template-columns 0.3s ease',
  },
  mainContainerCollapsed: {
    display: 'grid',
    gridTemplateColumns: '1fr',
    gap: 0,
    transition: 'grid-template-columns 0.3s ease',
  },
  sidebar: {
    background: '#1e293b',
    borderRight: '1px solid #475569',
    padding: '24px',
    overflowY: 'auto',
    position: 'sticky',
    top: '80px',
    height: 'calc(100vh - 80px)',
    transition: 'transform 0.3s ease, opacity 0.3s ease',
  },
  sidebarSection: {
    marginBottom: '20px',
    paddingBottom: '16px',
    borderBottom: '1px solid #475569',
