import React, { useState, useMemo, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Menu, X } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

class APIClient {
  static async fetchDashboardData() {
    try {
      console.log('ðŸŒ Fetching from:', `${API_BASE_URL}/data`);
      const response = await fetch(`${API_BASE_URL}/data`);
      console.log('ðŸ“¡ Response status:', response.status, response.statusText);

      if (!response.ok) {
        throw new Error(`API error: ${response.status} ${response.statusText}`);
      }

      const rawData = await response.json();
      console.log('ðŸ“¦ Response received');

      if (!Array.isArray(rawData)) {
        console.error('âŒ Response is not an array:', typeof rawData);
        return [];
      }

      console.log(`âœ… Successfully loaded ${rawData.length} records`);
      return rawData;
    } catch (error) {
      console.error('âŒ Dashboard data fetch error:', error.message);
      return [];
    }
  }
}

const COLUMNS = {
  orderId: 'Order ID',
  purchaseTimestamp: 'Purchase Timestamp',
  reviewScore: 'Review Score',
  deliveredOnTime: 'Delivered On Time',
  paymentType: 'Payment Type',
  paymentValue: 'Payment Value',
  shippingCost: 'Shipping Cost',
  productCategory: 'Product Category',
  customerState: 'Customer State',
  customerId: 'Customer ID',
  orderStatus: 'Order Status',
  deliveryDurationDays: 'Time to Deliver to Customer Days'
};

// Color scale for heatmap (white to dark blue)
const getColorForValue = (value, maxValue) => {
  if (!value) return '#f3f4f6';
  const intensity = Math.min(value / maxValue, 1);

  if (intensity < 0.2) return '#dbeafe';
  if (intensity < 0.4) return '#93c5fd';
  if (intensity < 0.6) return '#3b82f6';
  if (intensity < 0.8) return '#1d4ed8';
  return '#1e40af';
};

const BrazilStateMap = ({ stateData, maxOrders }) => {
  const [hoveredState, setHoveredState] = useState(null);

  // Brazilian states with approximate SVG coordinates
  const brazilStates = [
    { code: 'AC', name: 'Acre', x: 120, y: 320 },
    { code: 'AL', name: 'Alagoas', x: 670, y: 380 },
    { code: 'AP', name: 'AmapÃ¡', x: 380, y: 80 },
    { code: 'AM', name: 'Amazonas', x: 220, y: 180 },
    { code: 'BA', name: 'Bahia', x: 680, y: 420 },
    { code: 'CE', name: 'CearÃ¡', x: 650, y: 300 },
    { code: 'DF', name: 'Distrito Federal', x: 550, y: 420 },
    { code: 'ES', name: 'EspÃ­rito Santo', x: 700, y: 480 },
    { code: 'GO', name: 'GoiÃ¡s', x: 520, y: 400 },
    { code: 'MA', name: 'MaranhÃ£o', x: 620, y: 280 },
    { code: 'MT', name: 'Mato Grosso', x: 420, y: 350 },
    { code: 'MS', name: 'Mato Grosso do Sul', x: 480, y: 420 },
    { code: 'MG', name: 'Minas Gerais', x: 630, y: 450 },
    { code: 'PA', name: 'ParÃ¡', x: 380, y: 220 },
    { code: 'PB', name: 'ParaÃ­ba', x: 710, y: 340 },
    { code: 'PR', name: 'ParanÃ¡', x: 580, y: 480 },
    { code: 'PE', name: 'Pernambuco', x: 700, y: 360 },
    { code: 'PI', name: 'PiauÃ­', x: 620, y: 330 },
    { code: 'RJ', name: 'Rio de Janeiro', x: 720, y: 510 },
    { code: 'RN', name: 'Rio Grande do Norte', x: 710, y: 320 },
    { code: 'RS', name: 'Rio Grande do Sul', x: 520, y: 540 },
    { code: 'RO', name: 'RondÃ´nia', x: 200, y: 280 },
    { code: 'RR', name: 'Roraima', x: 280, y: 100 },
    { code: 'SC', name: 'Santa Catarina', x: 560, y: 530 },
    { code: 'SP', name: 'SÃ£o Paulo', x: 610, y: 500 },
    { code: 'SE', name: 'Sergipe', x: 690, y: 390 },
    { code: 'TO', name: 'Tocantins', x: 480, y: 280 },
  ];

  return (
    <div style={{ position: 'relative', width: '100%', height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <svg viewBox="0 0 800 600" style={{ width: '100%', height: '100%', maxWidth: '100%' }}>
        <defs>
          <style>{`
            .state-circle { cursor: pointer; transition: opacity 0.2s; }
            .state-circle:hover { opacity: 0.8; filter: drop-shadow(0 0 3px rgba(255,255,255,0.5)); }
            .state-label { font-size: 10px; font-weight: bold; text-anchor: middle; pointer-events: none; fill: #1e293b; }
          `}</style>
        </defs>

        {/* Brazil map border */}
        <rect x="80" y="50" width="650" height="500" fill="none" stroke="#475569" strokeWidth="2" rx="8" />

        {/* States as circles */}
        {brazilStates.map(state => {
          const orders = stateData[state.code] || 0;
          const color = getColorForValue(orders, maxOrders);
          const radius = Math.max(8, Math.min(22, 8 + (orders / maxOrders) * 14));

          return (
            <g key={state.code} onMouseEnter={() => setHoveredState(state.code)} onMouseLeave={() => setHoveredState(null)}>
              <circle cx={state.x} cy={state.y} r={radius} fill={color} className="state-circle" stroke="#1e293b" strokeWidth="1.5" />
              <text x={state.x} y={state.y + 4} className="state-label" style={{ opacity: hoveredState === state.code ? 1 : 0.8, fontSize: '9px' }}>
                {state.code}
              </text>
              {hoveredState === state.code && (
                <g>
                  <rect x={state.x - 40} y={state.y - 55} width="80" height="40" fill="#1e293b" rx="4" stroke="#cbd5e1" strokeWidth="1" />
                  <text x={state.x} y={state.y - 32} style={{ fontSize: '13px', fontWeight: 'bold', textAnchor: 'middle', fill: '#10b981' }}>
                    {orders}
                  </text>
                  <text x={state.x} y={state.y - 15} style={{ fontSize: '10px', textAnchor: 'middle', fill: '#cbd5e1' }}>
                    {state.name}
                  </text>
                </g>
              )}
            </g>
          );
        })}
      </svg>

      {/* Legend */}
      <div style={{ marginTop: '12px', display: 'flex', alignItems: 'center', gap: '8px', fontSize: '11px', color: '#cbd5e1' }}>
        <div style={{ display: 'flex', gap: '4px', alignItems: 'center' }}>
          <div style={{ width: '12px', height: '12px', backgroundColor: '#f3f4f6', border: '1px solid #9ca3af', borderRadius: '2px' }} />
          <div style={{ width: '12px', height: '12px', backgroundColor: '#dbeafe', border: '1px solid #9ca3af', borderRadius: '2px' }} />
          <div style={{ width: '12px', height: '12px', backgroundColor: '#93c5fd', border: '1px solid #9ca3af', borderRadius: '2px' }} />
          <div style={{ width: '12px', height: '12px', backgroundColor: '#3b82f6', border: '1px solid #9ca3af', borderRadius: '2px' }} />
          <div style={{ width: '12px', height: '12px', backgroundColor: '#1d4ed8', border: '1px solid #9ca3af', borderRadius: '2px' }} />
          <div style={{ width: '12px', height: '12px', backgroundColor: '#1e40af', border: '1px solid #9ca3af', borderRadius: '2px' }} />
        </div>
        <span style={{ marginLeft: '8px' }}>Low â†’ High Orders (Size = Volume)</span>
      </div>
    </div>
  );
};

const styles = {
  container: { minHeight: '100vh', background: 'linear-gradient(to bottom right, #0f172a, #1e293b)' },
  header: { background: 'rgba(15, 23, 42, 0.8)', backdropFilter: 'blur(10px)', borderBottom: '1px solid #475569', position: 'sticky', top: 0, zIndex: 50 },
  headerContent: { maxWidth: '100%', margin: '0 auto', padding: '1.5rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  logo: { fontSize: '1.875rem', fontWeight: 'bold', background: 'linear-gradient(to right, #60a5fa, #22d3ee)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent', margin: 0 },
  nav: { display: 'flex', gap: '1rem' },
  navButton: (isActive) => ({ padding: '0.5rem 1rem', borderRadius: '0.5rem', fontWeight: '500', border: 'none', cursor: 'pointer', backgroundColor: isActive ? '#2563eb' : 'transparent', color: isActive ? 'white' : '#cbd5e1' }),
  mainContainer: { display: 'grid', gridTemplateColumns: '280px 1fr' },
  mainContainerCollapsed: { display: 'grid', gridTemplateColumns: '1fr' },
  sidebar: { background: '#1e293b', borderRight: '1px solid #475569', padding: '24px', overflowY: 'auto', position: 'sticky', top: '80px', height: 'calc(100vh - 80px)' },
  sidebarSection: { marginBottom: '20px', paddingBottom: '16px', borderBottom: '1px solid #475569' },
  sidebarLabel: { fontWeight: '600', color: '#e2e8f0', marginBottom: '8px', display: 'block', fontSize: '13px' },
  sidebarSelect: { width: '100%', padding: '8px 12px', border: '1px solid #475569', borderRadius: '8px', fontSize: '13px', fontFamily: 'inherit', backgroundColor: '#0f172a', color: '#e2e8f0', cursor: 'pointer' },
  selectAllButton: { width: '100%', padding: '6px 12px', marginBottom: '8px', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '6px', fontSize: '12px', fontWeight: '600', cursor: 'pointer' },
  content: { padding: '32px', overflowY: 'auto', height: 'calc(100vh - 80px)' },
  kpiContainer: { background: '#1e293b', borderRadius: '12px', padding: '28px', boxShadow: '0 4px 12px rgba(0,0,0,0.3)', marginBottom: '32px', border: '1px solid #475569' },
  kpiGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '16px', marginBottom: '16px' },
  kpiCard: (color) => ({ background: color, borderRadius: '12px', padding: '24px', textAlign: 'center', color: 'white', border: '1px solid rgba(255,255,255,0.1)' }),
  kpiLabel: { fontSize: '10px', fontWeight: '700', textTransform: 'uppercase', color: 'rgba(255,255,255,0.75)', marginBottom: '10px' },
  kpiValue: { fontSize: '28px', fontWeight: '700', color: '#FFFFFF', margin: 0 },
  chartsGrid: { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '20px' },
  graphCard: { backgroundColor: '#1e293b', borderRadius: '12px', padding: '20px', minHeight: '400px', display: 'flex', flexDirection: 'column', border: '1px solid #475569' },
  graphCardSpan2: { gridColumn: 'span 2' },
  graphCardHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px' },
  graphCardTitle: { fontSize: '14px', fontWeight: '600', color: '#e2e8f0', margin: 0 },
  filterButton: { background: '#2563eb', color: 'white', border: 'none', borderRadius: '6px', padding: '6px 10px', cursor: 'pointer', fontSize: '11px', fontWeight: '600' },
  filterContainer: { display: 'none', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '8px', padding: '12px', marginBottom: '12px' },
  filterContainerVisible: { display: 'block', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '8px', padding: '12px', marginBottom: '12px' },
  filterLabel: { color: '#e2e8f0', fontSize: '11px', fontWeight: '600', marginBottom: '6px', display: 'block' },
  filterSelect: { width: '100%', padding: '6px 8px', border: '1px solid #475569', borderRadius: '6px', fontSize: '11px', backgroundColor: '#1e293b', color: '#e2e8f0', cursor: 'pointer', marginBottom: '8px' },
  filterInput: { width: '100%', padding: '6px 8px', border: '1px solid #475569', borderRadius: '6px', fontSize: '11px', backgroundColor: '#1e293b', color: '#e2e8f0', marginBottom: '8px' },
  tooltipStyle: { backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '8px', color: '#e2e8f0', padding: '8px' },
  heroSection: { maxWidth: '80rem', margin: '0 auto', padding: '5rem 1.5rem', textAlign: 'center' },
  heroTitle: { fontSize: '3rem', fontWeight: 'bold', color: 'white', marginBottom: '1rem' },
  heroSubtitle: { fontSize: '1.25rem', color: '#cbd5e1', marginBottom: '3rem' },
  featureGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem' },
  featureCard: { backgroundColor: '#1e293b', borderRadius: '1rem', padding: '2rem', border: '1px solid #475569', cursor: 'pointer' },
  featureIcon: { fontSize: '3.75rem', marginBottom: '1rem', display: 'block' },
  featureTitle: { fontSize: '1.5rem', fontWeight: 'bold', color: 'white', marginBottom: '0.75rem' },
  featureButton: { padding: '0.75rem 1.5rem', background: 'linear-gradient(to right, #2563eb, #1d4ed8)', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600' },
  searchSection: { backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '2rem' },
  searchContainer: { display: 'flex', gap: '0.5rem' },
  searchInput: { flex: 1, padding: '0.75rem', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '0.5rem', color: 'white', fontFamily: 'inherit' },
  searchButton: { padding: '0.75rem 1.5rem', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600' },
  resultCard: { backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '2rem', marginBottom: '1.5rem' },
  resultQuestion: { fontSize: '1.125rem', fontWeight: 'bold', color: 'white', margin: '0 0 1rem 0' },
  resultAnswer: { color: '#cbd5e1', lineHeight: '1.6', marginLeft: '2rem' },
};

const formatCurrency = (value) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  if (num >= 1000000) return `$${(num / 1000000).toFixed(1)}M`;
  if (num >= 1000) return `$${(num / 1000).toFixed(1)}K`;
  return `$${num.toFixed(2)}`;
};

const formatCurrencyWithCommas = (value) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  return `$${num.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
};

const formatDecimal = (value, decimals = 2) => {
  const num = typeof value === 'number' ? value : parseFloat(value) || 0;
  return num.toFixed(decimals);
};

const CustomTooltip = ({ active, payload, isCurrency = false }) => {
  if (active && payload && payload.length) {
    const value = payload[0].value;
    const formattedValue = isCurrency ? formatCurrencyWithCommas(value) : formatDecimal(value, 2);
    return (
      <div style={styles.tooltipStyle}>
        <p style={{ margin: 0 }}>{`${payload[0].name}: ${formattedValue}`}</p>
      </div>
    );
  }
  return null;
};

export default function EcommerceDashboard() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMetric, setSelectedMetric] = useState('revenue');

  const [selectedStates, setSelectedStates] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedPaymentTypes, setSelectedPaymentTypes] = useState([]);
  const [selectedStatuses, setSelectedStatuses] = useState([]);
  const [deliveryOnTime, setDeliveryOnTime] = useState('all');
  const [reviewRange, setReviewRange] = useState([1, 5]);

  const [showGraph1Filters, setShowGraph1Filters] = useState(false);
  const [showGraph2Filters, setShowGraph2Filters] = useState(false);
  const [showGraph3Filters, setShowGraph3Filters] = useState(false);
  const [showGraph4Filters, setShowGraph4Filters] = useState(false);
  const [showGraph5Filters, setShowGraph5Filters] = useState(false);

  const [graph1DeliveryDays, setGraph1DeliveryDays] = useState([0, 30]);
  const [graph1DeliveryStatus, setGraph1DeliveryStatus] = useState('all');

  const [graph2CustomerStates, setGraph2CustomerStates] = useState([]);
  const [graph2ReviewRange, setGraph2ReviewRange] = useState([1, 5]);

  const [graph3SpendMin, setGraph3SpendMin] = useState(0);
  const [graph3SpendMax, setGraph3SpendMax] = useState(10000);

  const [graph4Categories, setGraph4Categories] = useState([]);

  const [graph5CustomerStates, setGraph5CustomerStates] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await APIClient.fetchDashboardData();
        if (!Array.isArray(fetchedData) || fetchedData.length === 0) {
          setError('No data returned from API');
          setData([]);
        } else {
          setData(fetchedData);
          const maxSpend = Math.max(...fetchedData.map(d => parseFloat(d?.[COLUMNS.paymentValue]) || 0));
          setGraph3SpendMax(maxSpend);
          setError(null);
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch data');
        setData([]);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const filteredData = useMemo(() => {
    if (!Array.isArray(data) || data.length === 0) return [];

    let filtered = [...data];
    if (selectedStates.length > 0) filtered = filtered.filter(d => selectedStates.includes(d?.[COLUMNS.customerState]));
    if (selectedCategories.length > 0) filtered = filtered.filter(d => selectedCategories.includes(d?.[COLUMNS.productCategory]));
    if (selectedPaymentTypes.length > 0) filtered = filtered.filter(d => selectedPaymentTypes.includes(d?.[COLUMNS.paymentType]));
    if (selectedStatuses.length > 0) filtered = filtered.filter(d => selectedStatuses.includes(d?.[COLUMNS.orderStatus]));
    if (deliveryOnTime !== 'all') filtered = filtered.filter(d => d?.[COLUMNS.deliveredOnTime] === deliveryOnTime);
    filtered = filtered.filter(d => {
      const score = parseFloat(d?.[COLUMNS.reviewScore]) || 0;
      return score >= reviewRange[0] && score <= reviewRange[1];
    });
    return filtered;
  }, [data, selectedStates, selectedCategories, selectedPaymentTypes, selectedStatuses, deliveryOnTime, reviewRange]);

  const kpis = useMemo(() => {
    if (!Array.isArray(filteredData) || filteredData.length === 0) {
      return { totalOrders: 0, avgReview: 0, onTimeRate: 0, totalRevenue: 0, avgDeliveryDays: 0, avgOrderValue: 0 };
    }
    const totalOrders = filteredData.length;
    const completedOrders = filteredData.filter(d => d?.[COLUMNS.orderStatus] === 'delivered').length;
    const avgReview = (filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.reviewScore]) || 0), 0) / totalOrders).toFixed(2);
    const onTimeRate = completedOrders > 0 ? ((filteredData.filter(d => d?.[COLUMNS.deliveredOnTime] === 'Yes').length / completedOrders) * 100).toFixed(1) : 0;
    const totalRevenue = filteredData.reduce((sum, d) => sum + (parseFloat(d?.[COLUMNS.paymentValue]) || 0), 0);

    const validDeliveryDays = filteredData
      .map(d => parseFloat(d?.[COLUMNS.deliveryDurationDays]) || null)
      .filter(d => d !== null);
    const avgDeliveryDays = validDeliveryDays.length > 0 
