                    <button key={i} onClick={() => { setChatInput(s); inputRef.current?.focus(); }}
                      style={{ padding: '12px 14px', backgroundColor: '#1e293b', border: '1px solid #334155', borderRadius: '12px', color: '#94a3b8', cursor: 'pointer', fontSize: '13px', textAlign: 'left', lineHeight: '1.4', transition: 'all 0.15s' }}
                      onMouseEnter={e => { e.currentTarget.style.backgroundColor = '#273549'; e.currentTarget.style.color = '#e2e8f0'; e.currentTarget.style.borderColor = '#3b82f6'; }}
                      onMouseLeave={e => { e.currentTarget.style.backgroundColor = '#1e293b'; e.currentTarget.style.color = '#94a3b8'; e.currentTarget.style.borderColor = '#334155'; }}>
                      â†’ {s}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Message list */}
            {chatGroupedMessages.map(item =>
              item.type === 'divider'
                ? <DateDivider key={item.key} label={item.label} />
                : <div key={item.key} className="chat-message-animate"><ChatMessage message={item.message} /></div>
            )}

            {/* Typing indicator */}
            {chatLoading && <TypingIndicator />}

            <div ref={messagesEndRef} />
          </div>

          {/* Input area */}
          <div style={{ flexShrink: 0, paddingBottom: '20px', paddingTop: '12px', borderTop: '1px solid #1e293b' }}>
            <div style={{
              display: 'flex', gap: '10px', alignItems: 'flex-end',
              backgroundColor: '#1e293b', border: '1px solid #334155',
              borderRadius: '16px', padding: '10px 12px',
              boxShadow: '0 0 0 0px #3b82f6', transition: 'box-shadow 0.2s',
            }}
              onFocusCapture={e => e.currentTarget.style.boxShadow = '0 0 0 2px rgba(59,130,246,0.4)'}
              onBlurCapture={e => e.currentTarget.style.boxShadow = '0 0 0 0px #3b82f6'}
            >
              <textarea
                ref={inputRef}
                rows={1}
                placeholder="Ask a question about your data..."
                value={chatInput}
                onChange={e => { setChatInput(e.target.value); e.target.style.height = 'auto'; e.target.style.height = Math.min(e.target.scrollHeight, 120) + 'px'; }}
                onKeyDown={handleKeyDown}
                disabled={chatLoading}
                style={{
                  flex: 1, background: 'transparent', border: 'none',
                  color: '#e2e8f0', fontSize: '14px', lineHeight: '1.5',
                  fontFamily: 'inherit', padding: '4px 0',
                  maxHeight: '120px', overflowY: 'auto',
                  opacity: chatLoading ? 0.6 : 1,
                }}
              />
              <button
                onClick={handleSendMessage}
                disabled={!chatInput.trim() || chatLoading}
                style={{
                  width: '36px', height: '36px', borderRadius: '10px', flexShrink: 0,
                  background: chatInput.trim() && !chatLoading ? 'linear-gradient(135deg, #3b82f6, #06b6d4)' : '#334155',
                  border: 'none', cursor: chatInput.trim() && !chatLoading ? 'pointer' : 'not-allowed',
                  display: 'flex', alignItems: 'center', justifyContent: 'center',
                  transition: 'all 0.15s',
                  boxShadow: chatInput.trim() && !chatLoading ? '0 2px 8px rgba(59,130,246,0.3)' : 'none',
                }}
              >
                <Send size={16} color={chatInput.trim() && !chatLoading ? 'white' : '#64748b'} />
              </button>
            </div>
            <p style={{ color: '#475569', fontSize: '11px', margin: '8px 0 0', textAlign: 'center' }}>
              Press Enter to send Â· Shift+Enter for new line Â· Conversation is saved to your database
            </p>
          </div>
        </div>
      </div>
  );

  const GraphsPage = () => {
    const renderChart = (chart) => {
      if (chart.chart_type === 'error') {
        return (
          <div key={chart.title} style={{...styles.graphCard, borderColor: '#ef4444', marginBottom: '1.5rem'}}>
            <h3 style={{fontSize: '16px', fontWeight: '600', color: '#ef4444', margin: '0 0 1rem 0'}}>âŒ {chart.title}</h3>
            <p style={{color: '#cbd5e1'}}>{chart.error}</p>
          </div>
        );
      }
      if (!chart.data || chart.data.length === 0) return null;
      const colors = ['#3b82f6', '#f97316', '#10b981', '#ec4899', '#f59e0b'];
      try {
        let chartComponent = null;
        switch (chart.chart_type) {
          case 'line':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" label={{ value: chart.xKey, position: 'insideBottom', offset: -5, textAnchor: 'middle' }} />
                  <YAxis stroke="#94a3b8" width={70} label={{ value: chart.yKey, angle: -90, position: 'center', dx: -40, fill: '#94a3b8', style: { textAnchor: 'middle' } }} />
                  <Tooltip content={<EnhancedTooltip />} />
                  <Legend />
                  <Line type="monotone" dataKey={chart.yKey} stroke={colors[0]} strokeWidth={2} dot={{ r: 4 }} />
                </LineChart>
              </ResponsiveContainer>
            );
            break;
          case 'bar':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data} margin={{left: 0, right: 0, top: 0, bottom: 0}}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" label={{ value: chart.xKey, position: 'insideBottom', offset: -5, textAnchor: 'middle' }} />
                  <YAxis stroke="#94a3b8" width={70} label={{ value: chart.yKey, angle: -90, position: 'center', dx: -40, fill: '#94a3b8', style: { textAnchor: 'middle' } }} />
                  <Tooltip content={<EnhancedTooltip />} />
                  <Legend />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
            break;
          case 'pie':
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie data={chart.data} dataKey={chart.yKey} nameKey={chart.xKey} cx="50%" cy="50%" outerRadius={100}>
                    {chart.data.map((_, i) => <Cell key={i} fill={colors[i % 5]} />)}
                  </Pie>
                  <Tooltip content={<EnhancedTooltip />} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            );
            break;
          default:
            chartComponent = (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={chart.data}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                  <XAxis dataKey={chart.xKey} stroke="#94a3b8" />
                  <YAxis stroke="#94a3b8" width={70} />
                  <Tooltip content={<EnhancedTooltip />} />
                  <Bar dataKey={chart.yKey} fill={colors[0]} radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            );
        }
        return (
          <div key={chart.title} style={{...styles.graphCard, marginBottom: '1.5rem'}}>
            <h3 style={styles.graphCardTitle}>{chart.title}</h3>
            {chartComponent}
          </div>
        );
      } catch (e) {
        return null;
      }
    };

    return (
      <div style={styles.container}>
        <Navigation />
        <div style={{ maxWidth: '80rem', margin: '0 auto', padding: '2rem 1.5rem' }}>
          <div style={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '2rem' }}>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <input autoFocus type="text" placeholder="Ask for visualizations (e.g., 'Show revenue trends by month')..."
                value={graphQuery} onChange={(e) => setGraphQuery(e.target.value)}
                onKeyDown={(e) => { if (e.key === 'Enter') handleGenerateGraphs(e); }}
                style={{ flex: 1, padding: '0.75rem', backgroundColor: '#0f172a', border: '1px solid #475569', borderRadius: '0.5rem', color: 'white', fontFamily: 'inherit' }} />
              <button onClick={handleGenerateGraphs} disabled={graphLoading}
                style={{padding: '0.75rem 1.5rem', backgroundColor: '#2563eb', color: 'white', border: 'none', borderRadius: '0.5rem', cursor: 'pointer', fontWeight: '600', opacity: graphLoading ? 0.6 : 1}}>
                {graphLoading ? 'ðŸ“Š Generating...' : 'Generate'}
              </button>
            </div>
          </div>
          {graphLoading && <div style={{ padding: '2rem', textAlign: 'center', color: '#cbd5e1' }}>Generating...</div>}
          {generatedCharts.length > 0 && !graphLoading && <div>{generatedCharts.map(chart => renderChart(chart))}</div>}
          {generatedCharts.length === 0 && !graphLoading && (
            <div>
              <h3 style={{color: '#e2e8f0', marginBottom: '1rem', fontSize: '1.125rem', fontWeight: 'bold', textAlign: 'center'}}>ðŸ“Š Suggested Visualizations:</h3>
              {['Show total revenue by month (line chart)', 'What are the top product categories by volume (bar chart)', 'Breakdown of regions (pie chart)', 'Revenue trends over time (line chart)', 'Average AOV by product category (bar chart)'].map((item, idx) => (
                <div key={idx} style={{backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '0.75rem', padding: '1.5rem', marginBottom: '1rem', cursor: 'pointer'}}
                  onClick={() => setGraphQuery(item)}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#334155'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#1e293b'}>
                  <p style={{color: '#cbd5e1', margin: 0}}>â†’ {item}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <>
      <style>{`@keyframes spin { to { transform: rotate(360deg); } }`}</style>
      {currentPage === 'home' && <HomePage />}
      {currentPage === 'dashboard' && <DashboardPage />}
      {currentPage === 'text' && chatPageJSX}
      {currentPage === 'graphs' && <GraphsPage />}
    </>
  );
}
